/**
 * Oliver-OS - Main Entry Point
 * For the honor, not the glory‚Äîby the people, for the people.
 * 
 * A rebellious operating system designed to disrupt bureaucracy
 * through clean, efficient microservices architecture.
 */

import { createHttpServerWithWebSocket } from './core/server';
import { Logger } from './core/logger';
import { Config } from './core/config';
import { ServiceManager } from './services/service-manager';
import { ProcessManager } from './core/process-manager';
import { BureaucracyDisruptorService } from './services/bureaucracy-disruptor';
import { MonitoringService } from './services/monitoring-service';
import { PrismaClient } from '@prisma/client';

const logger = new Logger('Oliver-OS');
const config = new Config();

/**
 * Initialize Oliver-OS
 */
async function initialize(): Promise<void> {
  try {
    logger.info('üöÄ Initializing Oliver-OS...');
    
    // Load configuration
    await config.load();
    logger.info('‚úÖ Configuration loaded');
    
    // Initialize database
    const prisma = new PrismaClient();
    await prisma.$connect();
    logger.info('‚úÖ Database connected');
    
    // Initialize service manager
    const serviceManager = new ServiceManager(config);
    await serviceManager.initialize();
    logger.info('‚úÖ Services initialized');
    
    // Initialize process manager
    const processManager = new ProcessManager(config);
    await processManager.initialize();
    logger.info('‚úÖ Process manager initialized');
    
    // Initialize bureaucracy disruptor service
    const disruptorService = new BureaucracyDisruptorService(config);
    await disruptorService.initialize();
    logger.info('‚úÖ Bureaucracy disruptor service initialized');
    
    // Initialize monitoring service
    const monitoringService = new MonitoringService();
    logger.info('‚úÖ Monitoring service initialized');
    
    // Create and start server with WebSocket support
    const { httpServer, wsManager } = createHttpServerWithWebSocket(config, serviceManager, prisma);
    
    // Connect monitoring service to WebSocket manager
    wsManager.setMonitoringService(monitoringService);
    
    const port = config.get('port', 3000);
    
    httpServer.listen(port, () => {
      logger.info(`üéØ Oliver-OS running on port ${port}`);
      logger.info('üî• Ready to disrupt bureaucracy with clean code!');
      logger.info('üåê WebSocket server enabled for real-time communication');
      
      // Open browser automatically
      import('child_process').then(({ exec }) => {
        const url = `http://localhost:${port}`;
        exec(`start firefox ${url}`, (error: any) => {
          if (error) {
            // Fallback to default browser if Firefox is not available
            exec(`start ${url}`, (fallbackError: any) => {
              if (fallbackError) {
                logger.info(`üåê Server running at ${url}`);
              }
            });
          }
        });
      });
      
      // Log available endpoints
      logger.info('üì° Available endpoints:');
      logger.info(`   GET  /api/health - System health check`);
      logger.info(`   GET  /api/services - List active services`);
      logger.info(`   GET  /api/processes - List running processes`);
      logger.info(`   POST /api/processes - Start new process`);
      logger.info(`   GET  /api/status - System status`);
      logger.info(`   GET  /api/disruptor - Bureaucracy disruption status`);
      logger.info(`   GET  /api/agents - List available agent types`);
      logger.info(`   POST /api/agents/spawn - Spawn a new agent`);
      logger.info(`   POST /api/agents/spawn-multiple - Spawn multiple agents`);
      logger.info(`   POST /api/auth/register - Register new user`);
      logger.info(`   POST /api/auth/login - Login user`);
      logger.info(`   POST /api/auth/refresh - Refresh access token`);
      logger.info(`   POST /api/auth/logout - Logout user`);
      logger.info(`   GET  /api/auth/me - Get current user info`);
      logger.info('üîå WebSocket endpoints:');
      logger.info(`   WS   /ws/{client_id} - Real-time communication`);
      logger.info(`   Events: thought:create, thought:analyze, collaboration:event, agent:spawn, voice:data`);
    });
    
    // Graceful shutdown
    process.on('SIGINT', async () => {
      logger.info('üõë Shutting down Oliver-OS...');
      
      await serviceManager.shutdown();
      await processManager.shutdown();
      await wsManager.shutdown();
      await prisma.$disconnect();
      
      httpServer.close(() => {
        logger.info('‚úÖ Oliver-OS shutdown complete');
        process.exit(0);
      });
    });
    
  } catch (error) {
    logger.error('‚ùå Failed to initialize Oliver-OS:', error);
    process.exit(1);
  }
}

// Start the system
initialize().catch((error) => {
  logger.error('üí• Critical error during initialization:', error);
  process.exit(1);
});

/**
 * MCP Server Entry Point for Oliver-OS
 * Main module for running MCP servers with different transport options
 */

import { Config } from '../core/config';
import { Logger } from '../core/logger';
import { OliverOSMCPServerImpl } from './server';
import { StdioTransport, WebSocketTransport, HTTPTransport } from './transport';
import type { MCPTransport } from './transport';

export class MCPManager {
  private _logger: Logger;
  private _config: Config;
  private mcpServer: OliverOSMCPServerImpl;
  private transport?: MCPTransport;

  constructor() {
    this._config = new Config();
    this._logger = new Logger('MCP-Manager');
    this.mcpServer = new OliverOSMCPServerImpl(this._config);
  }

  async initialize(): Promise<void> {
    try {
      await this._config.load();
      this._logger.info('‚úÖ MCP Manager initialized');
    } catch (error) {
      this._logger.error('‚ùå Failed to initialize MCP Manager', error);
      throw error;
    }
  }

  async startWithStdio(): Promise<void> {
    this.transport = new StdioTransport();
    await this.startMCP();
  }

  async startWithWebSocket(port: number = 3001): Promise<void> {
    this.transport = new WebSocketTransport(port);
    await this.startMCP();
  }

  async startWithHTTP(port: number = 3002): Promise<void> {
    this.transport = new HTTPTransport(port);
    await this.startMCP();
  }

  private async startMCP(): Promise<void> {
    if (!this.transport) {
      throw new Error('No transport configured');
    }

    try {
      // Set up request handling
      this.transport.onRequest(async (request) => {
        return await this.mcpServer.handleRequest(request);
      });

      // Start transport
      await this.transport.start();

      // Start MCP server
      await this.mcpServer.start();

      this._logger.info('üöÄ MCP Server started successfully');
    } catch (error) {
      this._logger.error('‚ùå Failed to start MCP Server', error);
      throw error;
    }
  }

  async stop(): Promise<void> {
    try {
      if (this.transport) {
        await this.transport.stop();
      }
      await this.mcpServer.stop();
      this._logger.info('üõë MCP Server stopped');
    } catch (error) {
      this._logger.error('‚ùå Error stopping MCP Server', error);
      throw error;
    }
  }
}

// CLI interface
async function main() {
  const manager = new MCPManager();
  
  try {
    await manager.initialize();
    
    const transportType = process.argv[2]! || 'stdio';
    
    switch (transportType) {
      case 'stdio':
        await manager.startWithStdio();
        break;
      case 'websocket': {
        const wsPort = parseInt(process.argv[3]! || '3001');
        await manager.startWithWebSocket(wsPort);
        break;
      }
      case 'http': {
        const httpPort = parseInt(process.argv[3]! || '3002');
        await manager.startWithHTTP(httpPort);
        break;
      }
      default:
        console.error('‚ùå Unknown transport type. Use: stdio, websocket, or http');
        process.exit(1);
    }
    
    // Keep the process running
    process.on('SIGINT', async () => {
      console.log('\nüõë Shutting down MCP Server...');
      await manager.stop();
      process.exit(0);
    });
    
  } catch (error) {
    console.error('‚ùå Failed to start MCP Server:', error);
    process.exit(1);
  }
}

// Export for use as module
export { OliverOSMCPServerImpl, StdioTransport, WebSocketTransport, HTTPTransport };
export * from './types';

// Run if this is the main module
if (typeof process !== 'undefined' && import.meta.url === `file://${process.argv[1]!}`) {
  main().catch(console.error);
}

/**
 * MCP Orchestrator for Oliver-OS
 * Manages and coordinates all MCP servers
 */

import { EventEmitter } from 'node:events';
import { Logger } from '../core/logger';
import { OliverOSMCPServerImpl } from './server';
import { GitHubMCPServer } from './servers/github';
import { FilesystemMCPServer } from './servers/filesystem';
import type { MCPRequest } from './types';
import { DatabaseMCPServer } from './servers/database';
import { WebSearchMCPServer } from './servers/websearch';
import { TerminalMCPServer } from './servers/terminal';
import { MemoryMCPServer } from './servers/memory';
import type { OliverOSMCPServer } from './types';

export interface MCPServerInfo {
  name: string;
  server: OliverOSMCPServer;
  port: number;
  status: 'stopped' | 'starting' | 'running' | 'error';
  lastError?: string;
}

export class MCPOrchestrator extends EventEmitter {
  private _logger: Logger;
  private servers: Map<string, MCPServerInfo> = new Map();
  private isRunning: boolean = false;

  constructor() {
    super();
    this._logger = new Logger('MCP-Orchestrator');
    this.initializeServers();
  }

  private initializeServers(): void {
    // Core Oliver-OS MCP Server
    this.servers.set('oliver-os', {
      name: 'oliver-os',
      server: new OliverOSMCPServerImpl(new (require('../core/config').Config)()),
      port: 4000,
      status: 'stopped'
    });

    // GitHub MCP Server
    this.servers.set('github', {
      name: 'github',
      server: new GitHubMCPServer(),
      port: 4001,
      status: 'stopped'
    });

    // Filesystem MCP Server
    this.servers.set('filesystem', {
      name: 'filesystem',
      server: new FilesystemMCPServer(),
      port: 4002,
      status: 'stopped'
    });

    // Database MCP Server
    this.servers.set('database', {
      name: 'database',
      server: new DatabaseMCPServer(),
      port: 4003,
      status: 'stopped'
    });

    // Web Search MCP Server
    this.servers.set('websearch', {
      name: 'websearch',
      server: new WebSearchMCPServer(),
      port: 4004,
      status: 'stopped'
    });

    // Terminal MCP Server
    this.servers.set('terminal', {
      name: 'terminal',
      server: new TerminalMCPServer(),
      port: 4005,
      status: 'stopped'
    });

    // Memory MCP Server
    this.servers.set('memory', {
      name: 'memory',
      server: new MemoryMCPServer(),
      port: 4006,
      status: 'stopped'
    });

    this._logger.info(`üîß Initialized ${this.servers.size} MCP servers`);
  }

  async startAll(): Promise<void> {
    if (this.isRunning) {
      this._logger.warn('MCP Orchestrator is already running');
      return;
    }

    this._logger.info('üöÄ Starting all MCP servers...');
    this.isRunning = true;

    const startPromises = Array.from(this.servers.values()).map(serverInfo => 
      this._startServerInternal(serverInfo)
    );

    try {
      await Promise.allSettled(startPromises);
      
      const runningServers = Array.from(this.servers.values()).filter(s => s.status === 'running');
      const failedServers = Array.from(this.servers.values()).filter(s => s.status === 'error');
      
      this._logger.info(`‚úÖ Started ${runningServers.length} MCP servers successfully`);
      if (failedServers.length > 0) {
        this._logger.warn(`‚ö†Ô∏è ${failedServers.length} servers failed to start`);
        failedServers.forEach(server => {
          this._logger.error(`‚ùå ${server.name}: ${server.lastError}`);
        });
      }
      
      this.emit('allStarted', { running: runningServers.length, failed: failedServers.length });
    } catch (error) {
      this._logger.error('‚ùå Failed to start MCP servers', error);
      this.emit('error', error);
      throw error;
    }
  }

  async stopAll(): Promise<void> {
    if (!this.isRunning) {
      this._logger.warn('MCP Orchestrator is not running');
      return;
    }

    this._logger.info('üõë Stopping all MCP servers...');

    const stopPromises = Array.from(this.servers.values()).map(serverInfo => 
      this._stopServerInternal(serverInfo)
    );

    try {
      await Promise.allSettled(stopPromises);
      this.isRunning = false;
      
      const stoppedServers = Array.from(this.servers.values()).filter(s => s.status === 'stopped');
      this._logger.info(`‚úÖ Stopped ${stoppedServers.length} MCP servers`);
      
      this.emit('allStopped');
    } catch (error) {
      this._logger.error('‚ùå Error stopping MCP servers', error);
      this.emit('error', error);
      throw error;
    }
  }

  /**
   * Graceful shutdown with proper cleanup
   */
  async shutdown(): Promise<void> {
    this._logger.info('üîÑ Shutting down MCP Orchestrator...');
    
    try {
      // Stop all servers
      await this.stopAll();
      
      // Clean up event listeners
      this.removeAllListeners();
      
      // Clear server map
      this.servers.clear();
      
      this._logger.info('‚úÖ MCP Orchestrator shutdown complete');
    } catch (error) {
      this._logger.error('‚ùå Error during shutdown', error);
      throw error;
    }
  }

  async startServer(serverName: string): Promise<void> {
    const serverInfo = this.servers.get(serverName);
    if (!serverInfo) {
      throw new Error(`Server not found: ${serverName}`);
    }

    await this._startServerInternal(serverInfo);
  }

  private async _startServerInternal(serverInfo: MCPServerInfo): Promise<void> {
    try {
      this._logger.info(`üöÄ Starting ${serverInfo.name} MCP server on port ${serverInfo.port}`);
      serverInfo.status = 'starting';
      
      await serverInfo.server.start();
      serverInfo.status = 'running';
      delete serverInfo.lastError;
      
      this._logger.info(`‚úÖ ${serverInfo.name} MCP server started successfully`);
      this.emit('serverStarted', serverInfo);
    } catch (error) {
      serverInfo.status = 'error';
      serverInfo.lastError = error instanceof Error ? error.message : 'Unknown error';
      
      this._logger.error(`‚ùå Failed to start ${serverInfo.name} MCP server`, error);
      this.emit('serverError', { server: serverInfo, error });
    }
  }

  async stopServer(serverName: string): Promise<void> {
    const serverInfo = this.servers.get(serverName);
    if (!serverInfo) {
      throw new Error(`Server not found: ${serverName}`);
    }

    await this._stopServerInternal(serverInfo);
  }

  private async _stopServerInternal(serverInfo: MCPServerInfo): Promise<void> {
    try {
      this._logger.info(`üõë Stopping ${serverInfo.name} MCP server`);
      
      await serverInfo.server.stop();
      serverInfo.status = 'stopped';
      delete serverInfo.lastError;
      
      this._logger.info(`‚úÖ ${serverInfo.name} MCP server stopped`);
      this.emit('serverStopped', serverInfo);
    } catch (error) {
      this._logger.error(`‚ùå Error stopping ${serverInfo.name} MCP server`, error);
      this.emit('serverError', { server: serverInfo, error });
    }
  }

  async restartServer(serverName: string): Promise<void> {
    this._logger.info(`üîÑ Restarting ${serverName} MCP server`);
    await this.stopServer(serverName);
    await this.startServer(serverName);
  }

  getServer(serverName: string): MCPServerInfo | undefined {
    return this.servers.get(serverName);
  }

  getAllServers(): MCPServerInfo[] {
    return Array.from(this.servers.values());
  }

  getRunningServers(): MCPServerInfo[] {
    return Array.from(this.servers.values()).filter(s => s.status === 'running');
  }

  getServerStatus(): Record<string, any> {
    const status: Record<string, any> = {};
    
    for (const [name, serverInfo] of this.servers.entries()) {
      status[name] = {
        name: serverInfo.name,
        port: serverInfo.port,
        status: serverInfo.status,
        lastError: serverInfo.lastError,
        uptime: serverInfo.status === 'running' ? 'running' : 'stopped'
      };
    }
    
    return status;
  }

  async getServerHealth(): Promise<Record<string, any>> {
    const health: Record<string, any> = {};
    
    for (const [name, serverInfo] of this.servers.entries()) {
      try {
        if (serverInfo.status === 'running') {
          // Test server health by sending a simple request
          const testRequest: MCPRequest = {
            jsonrpc: '2.0' as const,
            id: 'health-check',
            method: 'initialize',
            params: {
              protocolVersion: '2024-11-05',
              capabilities: {},
              clientInfo: { name: 'health-check', version: '1.0.0' }
            }
          };
          
          await serverInfo.server.handleRequest(testRequest);
          health[name] = {
            status: 'healthy',
            response_time: '0ms', // Would measure actual response time
            last_check: new Date().toISOString()
          };
        } else {
          health[name] = {
            status: 'unhealthy',
            reason: serverInfo.lastError || 'Server not running',
            last_check: new Date().toISOString()
          };
        }
      } catch (error) {
        health[name] = {
          status: 'unhealthy',
          reason: error instanceof Error ? error.message : 'Unknown error',
          last_check: new Date().toISOString()
        };
      }
    }
    
    return health;
  }

  async executeCommand(serverName: string, command: string, args: Record<string, unknown> = {}): Promise<any> {
    const serverInfo = this.servers.get(serverName);
    if (!serverInfo) {
      throw new Error(`Server not found: ${serverName}`);
    }

    if (serverInfo.status !== 'running') {
      throw new Error(`Server ${serverName} is not running`);
    }

    const request: MCPRequest = {
      jsonrpc: '2.0' as const,
      id: `cmd-${Date.now()}`,
      method: 'tools/call',
      params: {
        name: command,
        arguments: args
      }
    };

    return await serverInfo.server.handleRequest(request);
  }

  async getServerTools(serverName: string): Promise<any> {
    const serverInfo = this.servers.get(serverName);
    if (!serverInfo) {
      throw new Error(`Server not found: ${serverName}`);
    }

    const request: MCPRequest = {
      jsonrpc: '2.0' as const,
      id: `tools-${Date.now()}`,
      method: 'tools/list'
    };

    return await serverInfo.server.handleRequest(request);
  }

  async getServerResources(serverName: string): Promise<any> {
    const serverInfo = this.servers.get(serverName);
    if (!serverInfo) {
      throw new Error(`Server not found: ${serverName}`);
    }

    const request: MCPRequest = {
      jsonrpc: '2.0' as const,
      id: `resources-${Date.now()}`,
      method: 'resources/list'
    };

    return await serverInfo.server.handleRequest(request);
  }

  // Convenience methods for common operations
  async searchAllServers(query: string): Promise<Record<string, any>> {
    const results: Record<string, any> = {};
    
    for (const [name, serverInfo] of this.servers.entries()) {
      if (serverInfo.status === 'running') {
        try {
          // Try to search using available tools
          const toolsResponse = await this.getServerTools(name);
          if (toolsResponse.result?.tools) {
            const searchTools = toolsResponse.result.tools.filter((tool: any) => 
              tool.name.includes('search') || tool.name.includes('find')
            );
            
            if (searchTools.length > 0) {
              const searchTool = searchTools[0]!;
              const searchResult = await this.executeCommand(name, searchTool.name, { query });
              results[name] = searchResult;
            }
          }
        } catch (error) {
          this._logger.warn(`Search failed for ${name}:`, { error: String(error) });
        }
      }
    }
    
    return results;
  }

  async getSystemOverview(): Promise<any> {
    const overview = {
      orchestrator: {
        status: this.isRunning ? 'running' : 'stopped',
        total_servers: this.servers.size,
        running_servers: this.getRunningServers().length
      },
      servers: this.getServerStatus(),
      health: await this.getServerHealth(),
      timestamp: new Date().toISOString()
    };
    
    return overview;
  }

  // Event handlers - inherited from EventEmitter
}

// CLI interface for the orchestrator
export async function main() {
  const orchestrator = new MCPOrchestrator();
  
  try {
    const command = process.argv[2]! || 'start';
    
    switch (command) {
      case 'start':
        await orchestrator.startAll();
        console.log('üéâ All MCP servers started successfully!');
        break;
      case 'stop':
        await orchestrator.stopAll();
        console.log('üõë All MCP servers stopped');
        break;
      case 'status': {
        const status = orchestrator.getServerStatus();
        console.log('üìä MCP Server Status:');
        console.log(JSON.stringify(status, null, 2));
        break;
      }
      case 'health': {
        const health = await orchestrator.getServerHealth();
        console.log('üè• MCP Server Health:');
        console.log(JSON.stringify(health, null, 2));
        break;
      }
      case 'restart': {
        const serverName = process.argv[3]!;
        if (serverName) {
          await orchestrator.restartServer(serverName);
          console.log(`üîÑ ${serverName} server restarted`);
        } else {
          console.log('‚ùå Please specify server name to restart');
        }
        break;
      }
      default:
        console.log('Available commands: start, stop, status, health, restart <server>');
    }
    
    if (command === 'start') {
      // Keep the process running
      process.on('SIGINT', async () => {
        console.log('\nüõë Shutting down MCP servers...');
        await orchestrator.stopAll();
        process.exit(0);
      });
      
      // Keep alive
      setInterval(() => {
        // Heartbeat
      }, 30000);
    }
    
  } catch (error) {
    console.error('‚ùå MCP Orchestrator error:', error);
    process.exit(1);
  }
}

// Run if this is the main module
if (typeof process !== 'undefined' && import.meta.url === `file://${process.argv[1]!}`) {
  main().catch(console.error);
}

/**
 * MCP Server Implementation for Oliver-OS
 * Provides AI models with access to Oliver-OS services and resources
 */

import { EventEmitter } from 'node:events';
import { Logger } from '../core/logger';
import { Config } from '../core/config';
import { CodebuffService } from '../services/codebuff/codebuff-service';
import { CodebuffMCPTools } from './tools/codebuff';
import type { 
  MCPServerConfig, 
  MCPTool, 
  MCPResource, 
  MCPRequest, 
  MCPResponse,
  OliverOSMCPServer 
} from './types';

export class OliverOSMCPServerImpl extends EventEmitter implements OliverOSMCPServer {
  private _logger: Logger;
  private oliverConfig: Config;
  public config!: MCPServerConfig;
  private serverConfig: MCPServerConfig;
  private isRunning: boolean = false;
  private codebuffService: CodebuffService;
  private codebuffTools: CodebuffMCPTools;

  constructor(config: Config) {
    super();
    this.oliverConfig = config;
    this._logger = new Logger('MCP-Server');
    this.codebuffService = new CodebuffService(config);
    this.codebuffTools = new CodebuffMCPTools(this.codebuffService);
    this.serverConfig = this.createServerConfig();
    this.config = this.serverConfig;
  }

  private createServerConfig(): MCPServerConfig {
    return {
      name: 'oliver-os-mcp-server',
      version: '1.0.0',
      description: 'MCP Server for Oliver-OS AI-brain interface system',
      port: (this.oliverConfig.get('port') as number) + 1000, // MCP server on different port
      host: 'localhost',
      tools: this.createTools(),
      resources: this.createResources()
    };
  }

  private createTools(): MCPTool[] {
    const baseTools: MCPTool[] = [
      {
        name: 'get_system_status',
        description: 'Get current Oliver-OS system status and health metrics',
        inputSchema: {
          type: 'object',
          properties: {
            includeDetails: { type: 'boolean', default: false }
          }
        },
        handler: this.handleGetSystemStatus.bind(this)
      },
      {
        name: 'process_thought',
        description: 'Process a thought through the AI-brain interface',
        inputSchema: {
          type: 'object',
          properties: {
            thought: { type: 'string', description: 'The thought to process' },
            userId: { type: 'string', description: 'User ID processing the thought' },
            context: { type: 'object', description: 'Additional context for processing' }
          },
          required: ['thought', 'userId']
        },
        handler: this.handleProcessThought.bind(this)
      },
      {
        name: 'get_agent_status',
        description: 'Get status of AI agents and their current tasks',
        inputSchema: {
          type: 'object',
          properties: {
            agentId: { type: 'string', description: 'Specific agent ID to check' }
          }
        },
        handler: this.handleGetAgentStatus.bind(this)
      },
      {
        name: 'spawn_agent',
        description: 'Spawn a new AI agent with specific capabilities',
        inputSchema: {
          type: 'object',
          properties: {
            agentType: { type: 'string', description: 'Type of agent to spawn' },
            capabilities: { type: 'array', items: { type: 'string' } },
            config: { type: 'object', description: 'Agent configuration' }
          },
          required: ['agentType']
        },
        handler: this.handleSpawnAgent.bind(this)
      },
      {
        name: 'get_collaboration_data',
        description: 'Get real-time collaboration data and user presence',
        inputSchema: {
          type: 'object',
          properties: {
            workspaceId: { type: 'string', description: 'Workspace ID to get data for' }
          }
        },
        handler: this.handleGetCollaborationData.bind(this)
      },
      {
        name: 'execute_bmad_command',
        description: 'Execute BMAD (Break, Map, Automate, Document) commands',
        inputSchema: {
          type: 'object',
          properties: {
            command: { type: 'string', enum: ['break', 'map', 'automate', 'document'] },
            target: { type: 'string', description: 'Target for the BMAD command' },
            options: { type: 'object', description: 'Additional options' }
          },
          required: ['command', 'target']
        },
        handler: this.handleExecuteBmadCommand.bind(this)
      }
    ];

    // Add Codebuff tools
    const codebuffTools = this.codebuffTools.getTools();
    
    this._logger.info(`üîß Integrated ${codebuffTools.length} Codebuff MCP tools`);
    
    return [...baseTools, ...codebuffTools];
  }

  private createResources(): MCPResource[] {
    return [
      {
        uri: 'oliver-os://system/architecture',
        name: 'System Architecture',
        description: 'Current Oliver-OS system architecture and component relationships',
        mimeType: 'application/json',
        handler: this.handleGetArchitecture.bind(this)
      },
      {
        uri: 'oliver-os://logs/system',
        name: 'System Logs',
        description: 'Recent system logs and error reports',
        mimeType: 'text/plain',
        handler: this.handleGetSystemLogs.bind(this)
      },
      {
        uri: 'oliver-os://config/current',
        name: 'Current Configuration',
        description: 'Current system configuration and environment settings',
        mimeType: 'application/json',
        handler: this.handleGetCurrentConfig.bind(this)
      }
    ];
  }

  async start(): Promise<void> {
    if (this.isRunning) {
      this._logger.warn('MCP Server is already running');
      return;
    }

    try {
      this._logger.info(`üöÄ Starting MCP Server on ${this.serverConfig.host}:${this.serverConfig.port}`);
      this._logger.info(`üìã Available tools: ${this.serverConfig.tools.length}`);
      this._logger.info(`üìö Available resources: ${this.serverConfig.resources.length}`);
      
      this.isRunning = true;
      this.emit('started');
      
      this._logger.info('‚úÖ MCP Server started successfully');
    } catch (error) {
      this._logger.error('‚ùå Failed to start MCP Server', error);
      throw error;
    }
  }

  async stop(): Promise<void> {
    if (!this.isRunning) {
      this._logger.warn('MCP Server is not running');
      return;
    }

    try {
      this._logger.info('üõë Stopping MCP Server...');
      
      // Shutdown Codebuff service
      await this.codebuffService.shutdown();
      
      this.isRunning = false;
      this.emit('stopped');
      this._logger.info('‚úÖ MCP Server stopped successfully');
    } catch (error) {
      this._logger.error('‚ùå Failed to stop MCP Server', error);
      throw error;
    }
  }

  async handleRequest(request: MCPRequest): Promise<MCPResponse> {
    try {
      this._logger.debug(`üì® Handling MCP request: ${request.method}`);

      switch (request.method) {
        case 'tools/list':
          return this.handleToolsList(request);
        case 'tools/call':
          return this.handleToolsCall(request);
        case 'resources/list':
          return this.handleResourcesList(request);
        case 'resources/read':
          return this.handleResourcesRead(request);
        case 'initialize':
          return this.handleInitialize(request);
        default:
          return this.createErrorResponse(request.id, -32601, `Method not found: ${request.method}`);
      }
    } catch (error) {
      this._logger.error('‚ùå Error handling MCP request', error);
      return this.createErrorResponse(request.id, -32603, 'Internal error', error);
    }
  }

  private async handleToolsList(request: MCPRequest): Promise<MCPResponse> {
    const tools = this.serverConfig.tools.map(tool => ({
      name: tool.name,
      description: tool.description,
      inputSchema: tool.inputSchema
    }));

    return {
      jsonrpc: '2.0',
      id: request.id,
      result: { tools }
    };
  }

  private async handleToolsCall(request: MCPRequest): Promise<MCPResponse> {
    const { name, arguments: args } = request.params as { name: string; arguments: Record<string, unknown> };
    
    const tool = this.serverConfig.tools.find(t => t.name === name);
    if (!tool) {
      return this.createErrorResponse(request.id, -32601, `Tool not found: ${name}`);
    }

    try {
      const result = await tool.handler(args || {});
      return {
        jsonrpc: '2.0',
        id: request.id,
        result
      };
    } catch (error) {
      this._logger.error(`‚ùå Error executing tool ${name}`, error);
      return this.createErrorResponse(request.id, -32603, `Tool execution failed: ${error}`);
    }
  }

  private async handleResourcesList(request: MCPRequest): Promise<MCPResponse> {
    const resources = this.serverConfig.resources.map(resource => ({
      uri: resource.uri,
      name: resource.name,
      description: resource.description,
      mimeType: resource.mimeType
    }));

    return {
      jsonrpc: '2.0',
      id: request.id,
      result: { resources }
    };
  }

  private async handleResourcesRead(request: MCPRequest): Promise<MCPResponse> {
    const { uri } = request.params as { uri: string };
    
    const resource = this.serverConfig.resources.find(r => r.uri === uri);
    if (!resource) {
      return this.createErrorResponse(request.id, -32601, `Resource not found: ${uri}`);
    }

    try {
      const result = await resource.handler();
      return {
        jsonrpc: '2.0',
        id: request.id,
        result
      };
    } catch (error) {
      this._logger.error(`‚ùå Error reading resource ${uri}`, error);
      return this.createErrorResponse(request.id, -32603, `Resource read failed: ${error}`);
    }
  }

  private async handleInitialize(request: MCPRequest): Promise<MCPResponse> {
    return {
      jsonrpc: '2.0',
      id: request.id,
      result: {
        protocolVersion: '2024-11-05',
        capabilities: {
          tools: {},
          resources: {}
        },
        serverInfo: {
          name: this.serverConfig.name,
          version: this.serverConfig.version
        }
      }
    };
  }

  // Tool Handlers
  private async handleGetSystemStatus(args: Record<string, unknown>): Promise<any> {
    const includeDetails = args['includeDetails'] as boolean || false;
    
    return {
      content: [{
        type: 'text',
        text: JSON.stringify({
          status: 'healthy',
          uptime: globalThis.process?.uptime() || 0,
          memory: globalThis.process?.memoryUsage() || {},
          version: this.oliverConfig.get('version'),
          environment: this.oliverConfig.get('nodeEnv'),
          details: includeDetails ? {
            tools: this.serverConfig.tools.length,
            resources: this.serverConfig.resources.length,
            port: this.serverConfig.port
          } : undefined
        }, null, 2)
      }]
    };
  }

  private async handleProcessThought(args: Record<string, unknown>): Promise<any> {
    const { thought, userId, context } = args;
    
    // This would integrate with your actual thought processing service
    this._logger.info(`üß† Processing thought for user ${userId}: ${thought}`);
    
    return {
      content: [{
        type: 'text',
        text: JSON.stringify({
          processedThought: `Processed: ${thought}`,
          userId,
          timestamp: new Date().toISOString(),
          context
        }, null, 2)
      }]
    };
  }

  private async handleGetAgentStatus(args: Record<string, unknown>): Promise<any> {
    const agentId = args['agentId'] as string;
    
    return {
      content: [{
        type: 'text',
        text: JSON.stringify({
          agents: agentId ? [{ id: agentId, status: 'active' }] : [
            { id: 'agent-1', status: 'active', type: 'thought-processor' },
            { id: 'agent-2', status: 'idle', type: 'collaboration-manager' }
          ]
        }, null, 2)
      }]
    };
  }

  private async handleSpawnAgent(args: Record<string, unknown>): Promise<any> {
    const { agentType, capabilities, config } = args;
    
    this._logger.info(`ü§ñ Spawning agent of type: ${agentType}`);
    
    return {
      content: [{
        type: 'text',
        text: JSON.stringify({
          agentId: `agent-${Date.now()}`,
          type: agentType,
          capabilities: capabilities || [],
          config: config || {},
          status: 'spawning',
          timestamp: new Date().toISOString()
        }, null, 2)
      }]
    };
  }

  private async handleGetCollaborationData(args: Record<string, unknown>): Promise<any> {
    const workspaceId = args['workspaceId'] as string;
    
    return {
      content: [{
        type: 'text',
        text: JSON.stringify({
          workspaceId: workspaceId || 'default',
          activeUsers: 2,
          sharedThoughts: 15,
          lastActivity: new Date().toISOString()
        }, null, 2)
      }]
    };
  }

  private async handleExecuteBmadCommand(args: Record<string, unknown>): Promise<any> {
    const { command, target, options } = args;
    
    this._logger.info(`üîß Executing BMAD command: ${command} on ${target}`);
    
    return {
      content: [{
        type: 'text',
        text: JSON.stringify({
          command,
          target,
          options: options || {},
          status: 'completed',
          timestamp: new Date().toISOString()
        }, null, 2)
      }]
    };
  }

  // Resource Handlers
  private async handleGetArchitecture(): Promise<any> {
    return {
      contents: [{
        uri: 'oliver-os://system/architecture',
        mimeType: 'application/json',
        text: JSON.stringify({
          layers: ['frontend', 'backend', 'ai-services', 'database'],
          components: ['react-app', 'express-server', 'python-services', 'supabase'],
          connections: ['websocket', 'rest-api', 'database-queries']
        }, null, 2)
      }]
    };
  }

  private async handleGetSystemLogs(): Promise<any> {
    return {
      contents: [{
        uri: 'oliver-os://logs/system',
        mimeType: 'text/plain',
        text: `[${new Date().toISOString()}] MCP Server started\n[${new Date().toISOString()}] System healthy\n`
      }]
    };
  }

  private async handleGetCurrentConfig(): Promise<any> {
    return {
      contents: [{
        uri: 'oliver-os://config/current',
        mimeType: 'application/json',
        text: JSON.stringify(this.oliverConfig.getAll(), null, 2)
      }]
    };
  }

  private createErrorResponse(id: string | number, code: number, message: string, data?: unknown): MCPResponse {
    return {
      jsonrpc: '2.0',
      id,
      error: { code, message, data }
    };
  }
}

/**
 * Database MCP Server for Oliver-OS
