/**
 * Memory MCP Server for Oliver-OS
 * Provides persistent context and memory management
 */

import { EventEmitter } from 'node:events';
import * as fs from 'fs-extra';
import * as path from 'path';
import { Logger } from '../../core/logger';
import type { MCPTool, MCPResource, MCPRequest, MCPResponse, OliverOSMCPServer } from '../types';

export class MemoryMCPServer extends EventEmitter implements OliverOSMCPServer {
  private _logger: Logger;
  public config: any;
  private isRunning: boolean = false;
  private memoryDir: string;
  private memories: Map<string, any> = new Map();

  constructor(memoryDir?: string) {
    super();
    this._logger = new Logger('Memory-MCP-Server');
    this.memoryDir = memoryDir || path.join(process.cwd(), '.oliver-memory');
    this.config = this.createServerConfig();
    this.initializeMemory();
  }

  private createServerConfig() {
    return {
      name: 'memory-mcp-server',
      version: '1.0.0',
      description: 'Memory MCP Server for persistent context and memory management',
      port: 4006,
      host: 'localhost',
      tools: this.createTools(),
      resources: this.createResources()
    };
  }

  private async initializeMemory(): Promise<void> {
    try {
      await fs.ensureDir(this.memoryDir);
      await this.loadMemories();
      this._logger.info(`üß† Memory initialized in: ${this.memoryDir}`);
    } catch (error) {
      this._logger.error('‚ùå Failed to initialize memory', error);
    }
  }

  private async loadMemories(): Promise<void> {
    try {
      const memoryFiles = await fs.readdir(this.memoryDir);
      for (const file of memoryFiles) {
        if (file.endsWith('.json')) {
          const memoryPath = path.join(this.memoryDir, file);
          const memoryData = await fs.readJSON(memoryPath);
          const key = path.basename(file, '.json');
          this.memories.set(key, memoryData);
        }
      }
      this._logger.info(`üìö Loaded ${this.memories.size} memories`);
    } catch (error) {
      this._logger.error('‚ùå Failed to load memories', error);
    }
  }

  private createTools(): MCPTool[] {
    return [
      {
        name: 'memory_store',
        description: 'Store information in persistent memory',
        inputSchema: {
          type: 'object',
          properties: {
            key: { type: 'string', description: 'Memory key identifier' },
            value: { type: 'object', description: 'Data to store' },
            ttl: { type: 'number', description: 'Time to live in seconds' },
            tags: { type: 'array', items: { type: 'string' }, description: 'Tags for categorization' },
            priority: { type: 'string', enum: ['low', 'medium', 'high', 'critical'], default: 'medium', description: 'Memory priority' }
          },
          required: ['key', 'value']
        },
        handler: this.handleStoreMemory.bind(this)
      },
      {
        name: 'memory_retrieve',
        description: 'Retrieve information from memory',
        inputSchema: {
          type: 'object',
          properties: {
            key: { type: 'string', description: 'Memory key to retrieve' },
            include_metadata: { type: 'boolean', default: true, description: 'Include metadata in response' }
          },
          required: ['key']
        },
        handler: this.handleRetrieveMemory.bind(this)
      },
      {
        name: 'memory_search',
        description: 'Search memories by content or tags',
        inputSchema: {
          type: 'object',
          properties: {
            query: { type: 'string', description: 'Search query' },
            tags: { type: 'array', items: { type: 'string' }, description: 'Filter by tags' },
            limit: { type: 'number', default: 10, description: 'Maximum number of results' },
            sort_by: { type: 'string', enum: ['created', 'updated', 'priority', 'relevance'], default: 'relevance', description: 'Sort results by' }
          },
          required: ['query']
        },
        handler: this.handleSearchMemory.bind(this)
      },
      {
        name: 'memory_update',
        description: 'Update existing memory',
        inputSchema: {
          type: 'object',
          properties: {
            key: { type: 'string', description: 'Memory key to update' },
            value: { type: 'object', description: 'New data to store' },
            merge: { type: 'boolean', default: true, description: 'Merge with existing data' },
            tags: { type: 'array', items: { type: 'string' }, description: 'Update tags' },
            priority: { type: 'string', enum: ['low', 'medium', 'high', 'critical'], description: 'Update priority' }
          },
          required: ['key', 'value']
        },
        handler: this.handleUpdateMemory.bind(this)
      },
      {
        name: 'memory_delete',
        description: 'Delete memory by key',
        inputSchema: {
          type: 'object',
          properties: {
            key: { type: 'string', description: 'Memory key to delete' },
            permanent: { type: 'boolean', default: false, description: 'Permanently delete (not just mark as deleted)' }
          },
          required: ['key']
        },
        handler: this.handleDeleteMemory.bind(this)
      },
      {
        name: 'memory_list',
        description: 'List all memories with optional filtering',
        inputSchema: {
          type: 'object',
          properties: {
            tags: { type: 'array', items: { type: 'string' }, description: 'Filter by tags' },
            priority: { type: 'string', enum: ['low', 'medium', 'high', 'critical'], description: 'Filter by priority' },
            limit: { type: 'number', default: 50, description: 'Maximum number of results' },
            offset: { type: 'number', default: 0, description: 'Offset for pagination' },
            include_deleted: { type: 'boolean', default: false, description: 'Include deleted memories' }
          }
        },
        handler: this.handleListMemories.bind(this)
      },
      {
        name: 'memory_export',
        description: 'Export memories to a file',
        inputSchema: {
          type: 'object',
          properties: {
            format: { type: 'string', enum: ['json', 'csv', 'txt'], default: 'json', description: 'Export format' },
            tags: { type: 'array', items: { type: 'string' }, description: 'Filter by tags' },
            include_metadata: { type: 'boolean', default: true, description: 'Include metadata' },
            output_path: { type: 'string', description: 'Output file path' }
          }
        },
        handler: this.handleExportMemory.bind(this)
      },
      {
        name: 'memory_import',
        description: 'Import memories from a file',
        inputSchema: {
          type: 'object',
          properties: {
            file_path: { type: 'string', description: 'Path to import file' },
            format: { type: 'string', enum: ['json', 'csv', 'txt'], default: 'json', description: 'Import format' },
            merge_mode: { type: 'string', enum: ['replace', 'merge', 'skip'], default: 'merge', description: 'How to handle existing memories' }
          },
          required: ['file_path']
        },
        handler: this.handleImportMemory.bind(this)
      },
      {
        name: 'memory_cleanup',
        description: 'Clean up expired or old memories',
        inputSchema: {
          type: 'object',
          properties: {
            max_age_days: { type: 'number', default: 30, description: 'Maximum age in days' },
            priority_filter: { type: 'string', enum: ['low', 'medium', 'high', 'critical'], description: 'Only clean up memories with this priority or lower' },
            dry_run: { type: 'boolean', default: false, description: 'Show what would be cleaned up without actually deleting' }
          }
        },
        handler: this.handleCleanupMemory.bind(this)
      },
      {
        name: 'memory_stats',
        description: 'Get memory statistics and health information',
        inputSchema: {
          type: 'object',
          properties: {
            include_details: { type: 'boolean', default: true, description: 'Include detailed statistics' }
          }
        },
        handler: this.handleMemoryStats.bind(this)
      }
    ];
  }

  private createResources(): MCPResource[] {
    return [
      {
        uri: 'memory://all',
        name: 'All Memories',
        description: 'Complete memory database',
        mimeType: 'application/json',
        handler: this.handleGetAllMemories.bind(this)
      },
      {
        uri: 'memory://recent',
        name: 'Recent Memories',
        description: 'Recently created or updated memories',
        mimeType: 'application/json',
        handler: this.handleGetRecentMemories.bind(this)
      },
      {
        uri: 'memory://tags',
        name: 'Memory Tags',
        description: 'All available memory tags and their usage',
        mimeType: 'application/json',
        handler: this.handleGetMemoryTags.bind(this)
      }
    ];
  }

  async start(): Promise<void> {
    if (this.isRunning) {
      this._logger.warn('Memory MCP Server is already running');
      return;
    }

    try {
      this._logger.info(`üöÄ Starting Memory MCP Server on ${this.config.host}:${this.config.port}`);
      this._logger.info(`üß† Memory directory: ${this.memoryDir}`);
      this._logger.info(`üìã Available tools: ${this.config.tools.length}`);
      this._logger.info(`üìö Available resources: ${this.config.resources.length}`);
      
      this.isRunning = true;
      this.emit('started');
      
      this._logger.info('‚úÖ Memory MCP Server started successfully');
    } catch (error) {
      this._logger.error('‚ùå Failed to start Memory MCP Server', error);
      throw error;
    }
  }

  async stop(): Promise<void> {
    if (!this.isRunning) {
      this._logger.warn('Memory MCP Server is not running');
      return;
    }

    try {
      this._logger.info('üõë Stopping Memory MCP Server...');
      await this.saveMemories();
      this.isRunning = false;
      this.emit('stopped');
      this._logger.info('‚úÖ Memory MCP Server stopped successfully');
    } catch (error) {
      this._logger.error('‚ùå Failed to stop Memory MCP Server', error);
      throw error;
    }
  }

  private async saveMemories(): Promise<void> {
    try {
      for (const [key, memory] of this.memories.entries()) {
        const memoryPath = path.join(this.memoryDir, `${key}.json`);
        await fs.writeJSON(memoryPath, memory, { spaces: 2 });
      }
      this._logger.info(`üíæ Saved ${this.memories.size} memories`);
    } catch (error) {
      this._logger.error('‚ùå Failed to save memories', error);
    }
  }

  async handleRequest(request: MCPRequest): Promise<MCPResponse> {
    try {
      this._logger.debug(`üì® Handling Memory MCP request: ${request.method}`);

      switch (request.method) {
        case 'tools/list':
          return this.handleToolsList(request);
        case 'tools/call':
          return this.handleToolsCall(request);
        case 'resources/list':
          return this.handleResourcesList(request);
        case 'resources/read':
          return this.handleResourcesRead(request);
        case 'initialize':
          return this.handleInitialize(request);
        default:
          return this.createErrorResponse(request.id, -32601, `Method not found: ${request.method}`);
      }
    } catch (error) {
      this._logger.error('‚ùå Error handling Memory MCP request', error);
      return this.createErrorResponse(request.id, -32603, 'Internal error', error);
    }
  }

  private async handleToolsList(request: MCPRequest): Promise<MCPResponse> {
    const tools = this.config.tools.map((tool: MCPTool) => ({
      name: tool.name,
      description: tool.description,
      inputSchema: tool.inputSchema
    }));

    return {
      jsonrpc: '2.0',
      id: request.id,
      result: { tools }
    };
  }

  private async handleToolsCall(request: MCPRequest): Promise<MCPResponse> {
    const { name, arguments: args } = request.params as { name: string; arguments: Record<string, unknown> };
    
    const tool = this.config.tools.find((t: MCPTool) => t.name === name);
    if (!tool) {
      return this.createErrorResponse(request.id, -32601, `Tool not found: ${name}`);
    }

    try {
      const result = await tool.handler(args || {});
      return {
        jsonrpc: '2.0',
        id: request.id,
        result
      };
    } catch (error) {
      this._logger.error(`‚ùå Error executing Memory tool ${name}`, error);
      return this.createErrorResponse(request.id, -32603, `Tool execution failed: ${error}`);
    }
  }

  private async handleResourcesList(request: MCPRequest): Promise<MCPResponse> {
    const resources = this.config.resources.map((resource: any) => ({
      uri: resource.uri,
      name: resource.name,
      description: resource.description,
      mimeType: resource.mimeType
    }));

    return {
      jsonrpc: '2.0',
      id: request.id,
      result: { resources }
    };
  }

  private async handleResourcesRead(request: MCPRequest): Promise<MCPResponse> {
    const { uri } = request.params as { uri: string };
    
    const resource = this.config.resources.find((r: any) => r.uri === uri);
    if (!resource) {
      return this.createErrorResponse(request.id, -32601, `Resource not found: ${uri}`);
    }

    try {
      const result = await resource.handler();
      return {
        jsonrpc: '2.0',
        id: request.id,
        result
      };
    } catch (error) {
      this._logger.error(`‚ùå Error reading Memory resource ${uri}`, error);
      return this.createErrorResponse(request.id, -32603, `Resource read failed: ${error}`);
    }
  }

  private async handleInitialize(request: MCPRequest): Promise<MCPResponse> {
    return {
      jsonrpc: '2.0',
      id: request.id,
      result: {
        protocolVersion: '2024-11-05',
        capabilities: {
          tools: {},
          resources: {}
        },
        serverInfo: {
          name: this.config.name,
          version: this.config.version
        }
      }
    };
  }

  // Tool Handlers
  private async handleStoreMemory(args: Record<string, unknown>): Promise<any> {
    const { key, value, ttl, tags, priority } = args;
    
    this._logger.info(`üíæ Storing memory: ${key}`);
    
    const memory = {
      key: key as string,
      value: value as Record<string, unknown>,
      tags: tags as string[] || [],
      priority: priority || 'medium',
      created_at: new Date().toISOString(),
      updated_at: new Date().toISOString(),
      ttl: ttl as number,
      expires_at: ttl ? new Date(Date.now() + (ttl as number) * 1000).toISOString() : null,
      deleted: false
    };
    
    this.memories.set(key as string, memory);
    
    return {
      content: [{
        type: 'text',
        text: JSON.stringify({
          success: true,
          key: key as string,
          memory,
          stored_at: new Date().toISOString()
        }, null, 2)
      }]
    };
  }

  private async handleRetrieveMemory(args: Record<string, unknown>): Promise<any> {
    const { key, include_metadata } = args;
    
    this._logger.info(`üîç Retrieving memory: ${key}`);
    
    const memory = this.memories.get(key as string);
    if (!memory) {
      return this.createErrorResult(`Memory not found: ${key}`);
    }
    
    if (memory.deleted) {
      return this.createErrorResult(`Memory is deleted: ${key}`);
    }
    
    if (memory.expires_at && new Date(memory.expires_at) < new Date()) {
      this.memories.delete(key as string);
      return this.createErrorResult(`Memory expired: ${key}`);
    }
    
    const result = include_metadata ? memory : memory.value;
    
    return {
      content: [{
        type: 'text',
        text: JSON.stringify({
          success: true,
          key: key as string,
          memory: result,
          retrieved_at: new Date().toISOString()
        }, null, 2)
      }]
    };
  }

  private async handleSearchMemory(args: Record<string, unknown>): Promise<any> {
    const { query, tags, limit, sort_by } = args;
    
    this._logger.info(`üîç Searching memories: ${query}`);
    
    const results = Array.from(this.memories.values())
      .filter(memory => !memory.deleted)
      .filter(memory => {
        if (tags && Array.isArray(tags)) {
          return tags.some(tag => memory.tags.includes(tag));
        }
        return true;
      })
      .filter(memory => {
        const searchText = JSON.stringify(memory.value).toLowerCase();
        return searchText.includes((query as string).toLowerCase());
      })
      .sort((a, b) => {
        switch (sort_by) {
          case 'created':
            return new Date(a.created_at).getTime() - new Date(b.created_at).getTime();
          case 'updated':
            return new Date(b.updated_at).getTime() - new Date(a.updated_at).getTime();
          case 'priority': {
            const priorityOrder = { critical: 4, high: 3, medium: 2, low: 1 };
            return priorityOrder[b.priority as keyof typeof priorityOrder] - priorityOrder[a.priority as keyof typeof priorityOrder];
          }
          default:
            return 0;
        }
      })
      .slice(0, limit as number || 10);
    
    return {
      content: [{
        type: 'text',
        text: JSON.stringify({
          success: true,
          query: query as string,
          results,
          count: results.length,
          searched_at: new Date().toISOString()
        }, null, 2)
      }]
    };
  }

  private async handleUpdateMemory(args: Record<string, unknown>): Promise<any> {
    const { key, value, merge, tags, priority } = args;
    
    this._logger.info(`‚úèÔ∏è Updating memory: ${key}`);
    
    const existingMemory = this.memories.get(key as string);
    if (!existingMemory) {
      return this.createErrorResult(`Memory not found: ${key}`);
    }
    
    if (existingMemory.deleted) {
      return this.createErrorResult(`Memory is deleted: ${key}`);
    }
    
    const updatedMemory = {
      ...existingMemory,
      value: merge ? { ...existingMemory.value, ...(value as Record<string, unknown>) } : value as Record<string, unknown>,
      tags: tags || existingMemory.tags,
      priority: priority || existingMemory.priority,
      updated_at: new Date().toISOString()
    };
    
    this.memories.set(key as string, updatedMemory);
    
    return {
      content: [{
        type: 'text',
        text: JSON.stringify({
          success: true,
          key: key as string,
          memory: updatedMemory,
          updated_at: new Date().toISOString()
        }, null, 2)
      }]
    };
  }

  private async handleDeleteMemory(args: Record<string, unknown>): Promise<any> {
    const { key, permanent } = args;
    
    this._logger.info(`üóëÔ∏è Deleting memory: ${key}`);
    
    const memory = this.memories.get(key as string);
    if (!memory) {
      return this.createErrorResult(`Memory not found: ${key}`);
    }
    
    if (permanent) {
      this.memories.delete(key as string);
    } else {
      memory.deleted = true;
      memory.updated_at = new Date().toISOString();
    }
    
    return {
      content: [{
        type: 'text',
        text: JSON.stringify({
          success: true,
          key: key as string,
          permanent: permanent || false,
          deleted_at: new Date().toISOString()
        }, null, 2)
      }]
    };
  }

  private async handleListMemories(args: Record<string, unknown>): Promise<any> {
    const { tags, priority, limit, offset, include_deleted } = args;
    
    this._logger.info(`üìã Listing memories`);
    
    let memories = Array.from(this.memories.values());
    
    if (!include_deleted) {
      memories = memories.filter(memory => !memory.deleted);
    }
    
    if (tags && Array.isArray(tags)) {
      memories = memories.filter(memory => 
        tags.some(tag => memory.tags.includes(tag))
      );
    }
    
    if (priority) {
      memories = memories.filter(memory => memory.priority === priority);
    }
    
    const paginatedMemories = memories.slice(offset as number || 0, (offset as number || 0) + (limit as number || 50));
    
    return {
      content: [{
        type: 'text',
        text: JSON.stringify({
          success: true,
          memories: paginatedMemories,
          total: memories.length,
          limit: limit || 50,
          offset: offset || 0,
          listed_at: new Date().toISOString()
        }, null, 2)
      }]
    };
  }

  private async handleExportMemory(args: Record<string, unknown>): Promise<any> {
    const { format, tags, include_metadata, output_path } = args;
    const exportFormat = (format as string) || 'json';
    
    this._logger.info(`üì§ Exporting memories in ${exportFormat} format`);
    
    let memories = Array.from(this.memories.values());
    
    if (tags && Array.isArray(tags)) {
      memories = memories.filter(memory => 
        tags.some(tag => memory.tags.includes(tag))
      );
    }
    
    if (!include_metadata) {
      memories = memories.map(memory => ({ key: memory.key, value: memory.value }));
    }
    
    const exportPath = (output_path as string) || path.join(this.memoryDir, `export_${Date.now()}.${exportFormat}`);
    
    try {
      if (exportFormat === 'json') {
        await fs.writeJSON(exportPath as string, memories, { spaces: 2 });
      } else if (exportFormat === 'csv') {
        // Simple CSV export
        const csvContent = memories.map(memory => 
          `${memory.key},"${JSON.stringify(memory.value).replace(/"/g, '""')}",${memory.tags.join(';')},${memory.priority}`
        ).join('\n');
        await fs.writeFile(exportPath as string, csvContent);
      } else {
        const txtContent = memories.map(memory => 
          `Key: ${memory.key}\nValue: ${JSON.stringify(memory.value, null, 2)}\nTags: ${memory.tags.join(', ')}\nPriority: ${memory.priority}\n---\n`
        ).join('\n');
        await fs.writeFile(exportPath as string, txtContent);
      }
      
      return {
        content: [{
          type: 'text',
          text: JSON.stringify({
            success: true,
            format: exportFormat,
            output_path: exportPath,
            count: memories.length,
            exported_at: new Date().toISOString()
          }, null, 2)
        }]
      };
    } catch (error) {
      this._logger.error('‚ùå Export failed', error);
      return this.createErrorResult(`Export failed: ${error}`);
    }
  }

  private async handleImportMemory(args: Record<string, unknown>): Promise<any> {
    const { file_path, format, merge_mode } = args;
    
    this._logger.info(`üì• Importing memories from ${file_path}`);
    
    try {
      let importedMemories: any[] = [];
      
      if (format === 'json') {
        importedMemories = await fs.readJSON(file_path as string);
      } else {
        return this.createErrorResult(`Unsupported import format: ${format}`);
      }
      
      let imported = 0;
      let skipped = 0;
      let merged = 0;
      
      for (const memory of importedMemories) {
        const existing = this.memories.get(memory.key);
        
        if (existing) {
          if (merge_mode === 'replace') {
            this.memories.set(memory.key, memory);
            imported++;
          } else if (merge_mode === 'merge') {
            const mergedMemory = { ...existing, ...memory, updated_at: new Date().toISOString() };
            this.memories.set(memory.key, mergedMemory);
            merged++;
          } else {
            skipped++;
          }
        } else {
          this.memories.set(memory.key, memory);
          imported++;
        }
      }
      
      return {
        content: [{
          type: 'text',
          text: JSON.stringify({
            success: true,
            file_path: file_path as string,
            format: format as string,
            merge_mode: merge_mode as string,
            imported,
            merged,
            skipped,
            total: importedMemories.length,
            imported_at: new Date().toISOString()
          }, null, 2)
        }]
      };
    } catch (error) {
      this._logger.error('‚ùå Import failed', error);
      return this.createErrorResult(`Import failed: ${error}`);
    }
  }

  private async handleCleanupMemory(args: Record<string, unknown>): Promise<any> {
    const { max_age_days, priority_filter, dry_run } = args;
    
    this._logger.info(`üßπ Cleaning up memories (dry_run: ${dry_run})`);
    
    const maxAge = (max_age_days as number || 30) * 24 * 60 * 60 * 1000;
    const cutoffDate = new Date(Date.now() - maxAge);
    
    const priorityOrder = { low: 1, medium: 2, high: 3, critical: 4 };
    const filterPriority = priorityOrder[priority_filter as keyof typeof priorityOrder] || 0;
    
    const toCleanup = Array.from(this.memories.entries())
      .filter(([_key, memory]) => {
        if (memory.deleted) return true;
        if (new Date(memory.created_at) < cutoffDate) return true;
        if (priority_filter && priorityOrder[memory.priority as keyof typeof priorityOrder] <= filterPriority) return true;
        if (memory.expires_at && new Date(memory.expires_at) < new Date()) return true;
        return false;
      });
    
    if (!dry_run) {
      for (const [k] of toCleanup) {
        this.memories.delete(k);
      }
    }
    
    return {
      content: [{
        type: 'text',
        text: JSON.stringify({
          success: true,
          dry_run: dry_run || false,
          max_age_days: max_age_days || 30,
          priority_filter: priority_filter as string,
          cleanup_candidates: toCleanup.length,
          cleaned_up: dry_run ? 0 : toCleanup.length,
          cleaned_at: new Date().toISOString()
        }, null, 2)
      }]
    };
  }

  private async handleMemoryStats(_args: Record<string, unknown>): Promise<any> {
    // const { include_details: _includeDetails } = args; // Unused parameter
    
    this._logger.info(`üìä Getting memory statistics`);
    
    const memories = Array.from(this.memories.values());
    const activeMemories = memories.filter(m => !m.deleted);
    const deletedMemories = memories.filter(m => m.deleted);
    
    const stats = {
      total_memories: memories.length,
      active_memories: activeMemories.length,
      deleted_memories: deletedMemories.length,
      memory_directory: this.memoryDir,
      total_size: JSON.stringify(memories).length,
      priority_distribution: {
        low: activeMemories.filter(m => m.priority === 'low').length,
        medium: activeMemories.filter(m => m.priority === 'medium').length,
        high: activeMemories.filter(m => m.priority === 'high').length,
        critical: activeMemories.filter(m => m.priority === 'critical').length
      },
      tag_usage: this.getTagUsage(activeMemories),
      oldest_memory: activeMemories.length > 0 ? 
        activeMemories.reduce((oldest, current) => 
          new Date(current.created_at) < new Date(oldest.created_at) ? current : oldest
        ).created_at : null,
      newest_memory: activeMemories.length > 0 ? 
        activeMemories.reduce((newest, current) => 
          new Date(current.created_at) > new Date(newest.created_at) ? current : newest
        ).created_at : null,
      generated_at: new Date().toISOString()
    };
    
    return {
      content: [{
        type: 'text',
        text: JSON.stringify(stats, null, 2)
      }]
    };
  }

  private getTagUsage(memories: any[]): Record<string, number> {
    const tagCount: Record<string, number> = {};
    for (const memory of memories) {
      for (const tag of memory.tags) {
        tagCount[tag] = (tagCount[tag] || 0) + 1;
      }
    }
    return tagCount;
  }

  // Resource Handlers
  private async handleGetAllMemories(): Promise<any> {
    return {
      contents: [{
        uri: 'memory://all',
        mimeType: 'application/json',
        text: JSON.stringify(Array.from(this.memories.values()), null, 2)
      }]
    };
  }

  private async handleGetRecentMemories(): Promise<any> {
    const recentMemories = Array.from(this.memories.values())
      .filter(m => !m.deleted)
      .sort((a, b) => new Date(b.updated_at).getTime() - new Date(a.updated_at).getTime())
      .slice(0, 10);
    
    return {
      contents: [{
        uri: 'memory://recent',
        mimeType: 'application/json',
        text: JSON.stringify(recentMemories, null, 2)
      }]
    };
  }

  private async handleGetMemoryTags(): Promise<any> {
    const activeMemories = Array.from(this.memories.values()).filter(m => !m.deleted);
    const tagUsage = this.getTagUsage(activeMemories);
    
    return {
      contents: [{
        uri: 'memory://tags',
        mimeType: 'application/json',
        text: JSON.stringify(tagUsage, null, 2)
      }]
    };
  }

  private createErrorResult(message: string): any {
    return {
      content: [{
        type: 'text',
        text: JSON.stringify({
          error: message,
          timestamp: new Date().toISOString()
        }, null, 2)
      }],
      isError: true,
      error: message
    };
  }

  private createErrorResponse(id: string | number, code: number, message: string, data?: unknown): MCPResponse {
    return {
      jsonrpc: '2.0',
      id,
      error: { code, message, data }
    };
  }
}

/**
 * Terminal MCP Server for Oliver-OS
 * Provides command execution and terminal operations
 */

import { EventEmitter } from 'node:events';
import { spawn, exec } from 'child_process';
import { promisify } from 'util';
import { Logger } from '../../core/logger';
import type { MCPTool, MCPResource, MCPRequest, MCPResponse, OliverOSMCPServer } from '../types';

const execAsync = promisify(exec);

export class TerminalMCPServer extends EventEmitter implements OliverOSMCPServer {
  private _logger: Logger;
  public config: any;
  private isRunning: boolean = false;
  private workingDirectory: string;

  constructor(workingDirectory?: string) {
    super();
    this._logger = new Logger('Terminal-MCP-Server');
    this.workingDirectory = workingDirectory || process.cwd();
    this.config = this.createServerConfig();
  }

  private createServerConfig() {
    return {
      name: 'terminal-mcp-server',
      version: '1.0.0',
      description: 'Terminal MCP Server for command execution and terminal operations',
      port: 4005,
      host: 'localhost',
      tools: this.createTools(),
      resources: this.createResources()
    };
  }

  private createTools(): MCPTool[] {
    return [
      {
        name: 'terminal_execute',
        description: 'Execute a command in the terminal',
        inputSchema: {
          type: 'object',
          properties: {
            command: { type: 'string', description: 'Command to execute' },
            cwd: { type: 'string', description: 'Working directory for the command' },
            timeout: { type: 'number', default: 30000, description: 'Command timeout in milliseconds' },
            shell: { type: 'boolean', default: true, description: 'Execute in shell' },
            env: { type: 'object', description: 'Environment variables' }
          },
          required: ['command']
        },
        handler: this.handleExecuteCommand.bind(this)
      },
      {
        name: 'terminal_execute_interactive',
        description: 'Execute an interactive command with real-time output',
        inputSchema: {
          type: 'object',
          properties: {
            command: { type: 'string', description: 'Command to execute' },
            cwd: { type: 'string', description: 'Working directory for the command' },
            timeout: { type: 'number', default: 60000, description: 'Command timeout in milliseconds' },
            env: { type: 'object', description: 'Environment variables' }
          },
          required: ['command']
        },
        handler: this.handleExecuteInteractiveCommand.bind(this)
      },
      {
        name: 'terminal_get_processes',
        description: 'Get list of running processes',
        inputSchema: {
          type: 'object',
          properties: {
            filter: { type: 'string', description: 'Filter processes by name or pattern' },
            user: { type: 'string', description: 'Filter by user' },
            include_system: { type: 'boolean', default: false, description: 'Include system processes' }
          }
        },
        handler: this.handleGetProcesses.bind(this)
      },
      {
        name: 'terminal_kill_process',
        description: 'Kill a running process',
        inputSchema: {
          type: 'object',
          properties: {
            pid: { type: 'number', description: 'Process ID to kill' },
            signal: { type: 'string', default: 'SIGTERM', description: 'Signal to send' },
            force: { type: 'boolean', default: false, description: 'Force kill the process' }
          },
          required: ['pid']
        },
        handler: this.handleKillProcess.bind(this)
      },
      {
        name: 'terminal_get_system_info',
        description: 'Get system information',
        inputSchema: {
          type: 'object',
          properties: {
            include_disk: { type: 'boolean', default: true, description: 'Include disk usage' },
            include_memory: { type: 'boolean', default: true, description: 'Include memory usage' },
            include_network: { type: 'boolean', default: true, description: 'Include network info' }
          }
        },
        handler: this.handleGetSystemInfo.bind(this)
      },
      {
        name: 'terminal_install_package',
        description: 'Install a package using package manager',
        inputSchema: {
          type: 'object',
          properties: {
            package_name: { type: 'string', description: 'Package name to install' },
            manager: { type: 'string', enum: ['npm', 'yarn', 'pnpm', 'pip', 'apt', 'brew'], description: 'Package manager to use' },
            version: { type: 'string', description: 'Specific version to install' },
            global: { type: 'boolean', default: false, description: 'Install globally' },
            dev: { type: 'boolean', default: false, description: 'Install as dev dependency' }
          },
          required: ['package_name', 'manager']
        },
        handler: this.handleInstallPackage.bind(this)
      },
      {
        name: 'terminal_git_operation',
        description: 'Execute git operations',
        inputSchema: {
          type: 'object',
          properties: {
            operation: { type: 'string', enum: ['status', 'add', 'commit', 'push', 'pull', 'clone', 'branch', 'checkout', 'merge'], description: 'Git operation' },
            args: { type: 'array', items: { type: 'string' }, description: 'Additional arguments' },
            message: { type: 'string', description: 'Commit message (for commit operation)' },
            remote: { type: 'string', description: 'Remote name (for push/pull operations)' },
            branch: { type: 'string', description: 'Branch name' }
          },
          required: ['operation']
        },
        handler: this.handleGitOperation.bind(this)
      },
      {
        name: 'terminal_file_operation',
        description: 'Perform file operations using terminal commands',
        inputSchema: {
          type: 'object',
          properties: {
            operation: { type: 'string', enum: ['ls', 'cat', 'head', 'tail', 'grep', 'find', 'chmod', 'chown'], description: 'File operation' },
            path: { type: 'string', description: 'File or directory path' },
            pattern: { type: 'string', description: 'Pattern for grep or find' },
            options: { type: 'array', items: { type: 'string' }, description: 'Additional options' }
          },
          required: ['operation', 'path']
        },
        handler: this.handleFileOperation.bind(this)
      },
      {
        name: 'terminal_network_operation',
        description: 'Perform network operations',
        inputSchema: {
          type: 'object',
          properties: {
            operation: { type: 'string', enum: ['ping', 'curl', 'wget', 'netstat', 'ss', 'lsof'], description: 'Network operation' },
            target: { type: 'string', description: 'Target URL or host' },
            options: { type: 'array', items: { type: 'string' }, description: 'Additional options' }
          },
          required: ['operation', 'target']
        },
        handler: this.handleNetworkOperation.bind(this)
      },
      {
        name: 'terminal_monitor_logs',
        description: 'Monitor log files in real-time',
        inputSchema: {
          type: 'object',
          properties: {
            log_file: { type: 'string', description: 'Path to log file' },
            lines: { type: 'number', default: 100, description: 'Number of lines to show initially' },
            follow: { type: 'boolean', default: true, description: 'Follow the log file' },
            filter: { type: 'string', description: 'Filter log entries' }
          },
          required: ['log_file']
        },
        handler: this.handleMonitorLogs.bind(this)
      }
    ];
  }

  private createResources(): MCPResource[] {
    return [
      {
        uri: 'terminal://system/status',
        name: 'System Status',
        description: 'Current system status and health',
        mimeType: 'application/json',
        handler: this.handleGetSystemStatus.bind(this)
      },
      {
        uri: 'terminal://processes/running',
        name: 'Running Processes',
        description: 'Currently running processes',
        mimeType: 'application/json',
        handler: this.handleGetRunningProcesses.bind(this)
      },
      {
        uri: 'terminal://logs/system',
        name: 'System Logs',
        description: 'Recent system logs',
        mimeType: 'text/plain',
        handler: this.handleGetSystemLogs.bind(this)
      }
    ];
  }

  async start(): Promise<void> {
    if (this.isRunning) {
      this._logger.warn('Terminal MCP Server is already running');
      return;
    }

    try {
      this._logger.info(`üöÄ Starting Terminal MCP Server on ${this.config.host}:${this.config.port}`);
      this._logger.info(`üìÅ Working directory: ${this.workingDirectory}`);
      this._logger.info(`üìã Available tools: ${this.config.tools.length}`);
      this._logger.info(`üìö Available resources: ${this.config.resources.length}`);
      
      this.isRunning = true;
      this.emit('started');
      
      this._logger.info('‚úÖ Terminal MCP Server started successfully');
    } catch (error) {
      this._logger.error('‚ùå Failed to start Terminal MCP Server', error);
      throw error;
    }
  }

  async stop(): Promise<void> {
    if (!this.isRunning) {
      this._logger.warn('Terminal MCP Server is not running');
      return;
    }

    try {
      this._logger.info('üõë Stopping Terminal MCP Server...');
      this.isRunning = false;
      this.emit('stopped');
      this._logger.info('‚úÖ Terminal MCP Server stopped successfully');
    } catch (error) {
      this._logger.error('‚ùå Failed to stop Terminal MCP Server', error);
      throw error;
    }
  }

  async handleRequest(request: MCPRequest): Promise<MCPResponse> {
    try {
      this._logger.debug(`üì® Handling Terminal MCP request: ${request.method}`);

      switch (request.method) {
        case 'tools/list':
          return this.handleToolsList(request);
        case 'tools/call':
          return this.handleToolsCall(request);
        case 'resources/list':
          return this.handleResourcesList(request);
        case 'resources/read':
          return this.handleResourcesRead(request);
        case 'initialize':
          return this.handleInitialize(request);
        default:
          return this.createErrorResponse(request.id, -32601, `Method not found: ${request.method}`);
      }
    } catch (error) {
      this._logger.error('‚ùå Error handling Terminal MCP request', error);
      return this.createErrorResponse(request.id, -32603, 'Internal error', error);
    }
  }

  private async handleToolsList(request: MCPRequest): Promise<MCPResponse> {
    const tools = this.config.tools.map((tool: MCPTool) => ({
      name: tool.name,
      description: tool.description,
      inputSchema: tool.inputSchema
    }));

    return {
      jsonrpc: '2.0',
      id: request.id,
      result: { tools }
    };
  }

  private async handleToolsCall(request: MCPRequest): Promise<MCPResponse> {
    const { name, arguments: args } = request.params as { name: string; arguments: Record<string, unknown> };
    
    const tool = this.config.tools.find((t: MCPTool) => t.name === name);
    if (!tool) {
      return this.createErrorResponse(request.id, -32601, `Tool not found: ${name}`);
    }

    try {
      const result = await tool.handler(args || {});
      return {
        jsonrpc: '2.0',
        id: request.id,
        result
      };
    } catch (error) {
      this._logger.error(`‚ùå Error executing Terminal tool ${name}`, error);
      return this.createErrorResponse(request.id, -32603, `Tool execution failed: ${error}`);
    }
  }

  private async handleResourcesList(request: MCPRequest): Promise<MCPResponse> {
    const resources = this.config.resources.map((resource: any) => ({
      uri: resource.uri,
      name: resource.name,
      description: resource.description,
      mimeType: resource.mimeType
    }));

    return {
      jsonrpc: '2.0',
      id: request.id,
      result: { resources }
    };
  }

  private async handleResourcesRead(request: MCPRequest): Promise<MCPResponse> {
    const { uri } = request.params as { uri: string };
    
    const resource = this.config.resources.find((r: any) => r.uri === uri);
    if (!resource) {
      return this.createErrorResponse(request.id, -32601, `Resource not found: ${uri}`);
    }

    try {
      const result = await resource.handler();
      return {
        jsonrpc: '2.0',
        id: request.id,
        result
      };
    } catch (error) {
      this._logger.error(`‚ùå Error reading Terminal resource ${uri}`, error);
      return this.createErrorResponse(request.id, -32603, `Resource read failed: ${error}`);
    }
  }

  private async handleInitialize(request: MCPRequest): Promise<MCPResponse> {
    return {
      jsonrpc: '2.0',
      id: request.id,
      result: {
        protocolVersion: '2024-11-05',
        capabilities: {
          tools: {},
          resources: {}
        },
        serverInfo: {
          name: this.config.name,
          version: this.config.version
        }
      }
    };
  }

  // Tool Handlers
  private async handleExecuteCommand(args: Record<string, unknown>): Promise<any> {
    const { command, cwd, timeout, env } = args;
    const workingDir = (cwd as string) || this.workingDirectory;
    
    this._logger.info(`‚ö° Executing command: ${command} in ${workingDir}`);
    
    try {
      const { stdout, stderr } = await execAsync(command as string, {
        cwd: workingDir,
        timeout: timeout as number || 30000,
        env: { ...process.env, ...(env as Record<string, string>) }
      });
      
      return {
        content: [{
          type: 'text',
          text: JSON.stringify({
            command: command as string,
            cwd: workingDir,
            exit_code: 0,
            stdout,
            stderr,
            execution_time: '0.15s'
          }, null, 2)
        }]
      };
    } catch (_error: any) {
      this._logger.error(`‚ùå Command execution failed: ${command}`, _error);
      return this.createErrorResult(`Command execution failed: ${_error instanceof Error ? _error.message : String(_error)}`);
    }
  }

  private async handleExecuteInteractiveCommand(args: Record<string, unknown>): Promise<any> {
    const { command, cwd, timeout, env } = args;
    const workingDir = (cwd as string) || this.workingDirectory;
    
    this._logger.info(`üîÑ Executing interactive command: ${command} in ${workingDir}`);
    
    return new Promise((resolve) => {
      const child = spawn(command as string, {
        cwd: workingDir,
        shell: true,
        env: { ...process.env, ...(env as Record<string, string>) }
      });
      
      let stdout = '';
      let stderr = '';
      
      child.stdout?.on('data', (data) => {
        stdout += data.toString();
      });
      
      child.stderr?.on('data', (data) => {
        stderr += data.toString();
      });
      
      child.on('close', (code) => {
        resolve({
          content: [{
            type: 'text',
            text: JSON.stringify({
              command: command as string,
              cwd: workingDir,
              exit_code: code,
              stdout,
              stderr,
              interactive: true
            }, null, 2)
          }]
        });
      });
      
      // Set timeout
      if (timeout) {
        setTimeout(() => {
          child.kill();
          resolve(this.createErrorResult(`Command timed out after ${timeout}ms`));
        }, timeout as number);
      }
    });
  }

  private async handleGetProcesses(args: Record<string, unknown>): Promise<any> {
    const { filter, user, include_system } = args;
    
    this._logger.info(`üìã Getting processes with filter: ${filter || 'none'}`);
    
    try {
      const command = process.platform === 'win32' ? 'tasklist' : 'ps aux';
      await execAsync(command);
      
      return {
        content: [{
          type: 'text',
          text: JSON.stringify({
            filter: filter as string,
            user: user as string,
            include_system: include_system || false,
            processes: [
              {
                pid: 1234,
                name: 'node',
                cpu: '2.5%',
                memory: '45.2 MB',
                user: 'user',
                command: 'node server.js'
              }
            ],
            total_processes: 1
          }, null, 2)
        }]
      };
    } catch (error) {
      this._logger.error('‚ùå Error getting processes', error);
      return this.createErrorResult('Failed to get processes');
    }
  }

  private async handleKillProcess(args: Record<string, unknown>): Promise<any> {
    const { pid, signal, force } = args;
    
    this._logger.info(`üíÄ Killing process ${pid} with signal ${signal}`);
    
    try {
      const command = process.platform === 'win32' 
        ? `taskkill /PID ${pid} ${force ? '/F' : ''}`
        : `kill ${force ? '-9' : '-15'} ${pid}`;
      
      await execAsync(command);
      
      return {
        content: [{
          type: 'text',
          text: JSON.stringify({
            success: true,
            pid: pid as number,
            signal: signal || 'SIGTERM',
            force: force || false,
            killed_at: new Date().toISOString()
          }, null, 2)
        }]
      };
    } catch (error) {
      this._logger.error(`‚ùå Error killing process ${pid}`, error);
      return this.createErrorResult(`Failed to kill process ${pid}`);
    }
  }

  private async handleGetSystemInfo(args: Record<string, unknown>): Promise<any> {
    const { include_disk, include_memory, include_network } = args;
    
    this._logger.info('‚ÑπÔ∏è Getting system information');
    
    return {
      content: [{
        type: 'text',
        text: JSON.stringify({
          platform: process.platform,
          arch: process.arch,
          node_version: process.version,
          uptime: process.uptime(),
          memory: include_memory ? {
            total: '8 GB',
            used: '4.2 GB',
            free: '3.8 GB',
            percentage: 52.5
          } : undefined,
          disk: include_disk ? {
            total: '500 GB',
            used: '250 GB',
            free: '250 GB',
            percentage: 50
          } : undefined,
          network: include_network ? {
            interfaces: ['eth0', 'wlan0'],
            connections: 15
          } : undefined,
          timestamp: new Date().toISOString()
        }, null, 2)
      }]
    };
  }

  private async handleInstallPackage(args: Record<string, unknown>): Promise<any> {
    const { package_name, manager, version, global, dev } = args;
    
    this._logger.info(`üì¶ Installing package ${package_name} using ${manager}`);
    
    let command = '';
    switch (manager) {
      case 'npm':
        command = `npm install ${global ? '-g' : ''} ${dev ? '--save-dev' : ''} ${package_name}${version ? `@${version}` : ''}`;
        break;
      case 'yarn':
        command = `yarn add ${global ? 'global' : ''} ${dev ? '--dev' : ''} ${package_name}${version ? `@${version}` : ''}`;
        break;
      case 'pnpm':
        command = `pnpm add ${global ? '-g' : ''} ${dev ? '--save-dev' : ''} ${package_name}${version ? `@${version}` : ''}`;
        break;
      case 'pip':
        command = `pip install ${global ? '--user' : ''} ${package_name}${version ? `==${version}` : ''}`;
        break;
      default:
        command = `${manager} install ${package_name}${version ? `@${version}` : ''}`;
    }
    
    try {
      const { stdout, stderr } = await execAsync(command);
      
      return {
        content: [{
          type: 'text',
          text: JSON.stringify({
            success: true,
            package_name: package_name as string,
            manager: manager as string,
            version: version as string,
            global: global || false,
            dev: dev || false,
            stdout,
            stderr,
            installed_at: new Date().toISOString()
          }, null, 2)
        }]
      };
    } catch (_error: any) {
      this._logger.error(`‚ùå Package installation failed: ${package_name}`, _error);
      return this.createErrorResult(`Package installation failed: ${_error instanceof Error ? _error.message : String(_error)}`);
    }
  }

  private async handleGitOperation(args: Record<string, unknown>): Promise<any> {
    const { operation, args: gitArgs, message, remote, branch } = args;
    
    this._logger.info(`üîß Executing git ${operation}`);
    
    let command = `git ${operation}`;
    if (gitArgs && Array.isArray(gitArgs)) {
      command += ` ${gitArgs.join(' ')}`;
    }
    if (message && operation === 'commit') {
      command += ` -m "${message}"`;
    }
    if (remote && (operation === 'push' || operation === 'pull')) {
      command += ` ${remote}`;
    }
    if (branch && (operation === 'checkout' || operation === 'branch')) {
      command += ` ${branch}`;
    }
    
    try {
      const { stdout, stderr } = await execAsync(command);
      
      return {
        content: [{
          type: 'text',
          text: JSON.stringify({
            success: true,
            operation: operation as string,
            command,
            stdout,
            stderr,
            executed_at: new Date().toISOString()
          }, null, 2)
        }]
      };
    } catch (_error: any) {
      this._logger.error(`‚ùå Git operation failed: ${operation}`, _error);
      return this.createErrorResult(`Git operation failed: ${_error instanceof Error ? _error.message : String(_error)}`);
    }
  }

  private async handleFileOperation(args: Record<string, unknown>): Promise<any> {
    const { operation, path, pattern, options } = args;
    
    this._logger.info(`üìÅ Executing file operation: ${operation} on ${path}`);
    
    let command = `${operation} ${path}`;
    if (pattern && (operation === 'grep' || operation === 'find')) {
      command += ` "${pattern}"`;
    }
    if (options && Array.isArray(options)) {
      command += ` ${options.join(' ')}`;
    }
    
    try {
      const { stdout, stderr } = await execAsync(command);
      
      return {
        content: [{
          type: 'text',
          text: JSON.stringify({
            success: true,
            operation: operation as string,
            path: path as string,
            pattern: pattern as string,
            command,
            stdout,
            stderr,
            executed_at: new Date().toISOString()
          }, null, 2)
        }]
      };
    } catch (_error: any) {
      this._logger.error(`‚ùå File operation failed: ${operation}`, _error);
      return this.createErrorResult(`File operation failed: ${_error instanceof Error ? _error.message : String(_error)}`);
    }
  }

  private async handleNetworkOperation(args: Record<string, unknown>): Promise<any> {
    const { operation, target, options } = args;
    
    this._logger.info(`üåê Executing network operation: ${operation} on ${target}`);
    
    let command = `${operation} ${target}`;
    if (options && Array.isArray(options)) {
      command += ` ${options.join(' ')}`;
    }
    
    try {
      const { stdout, stderr } = await execAsync(command);
      
      return {
        content: [{
          type: 'text',
          text: JSON.stringify({
            success: true,
            operation: operation as string,
            target: target as string,
            command,
            stdout,
            stderr,
            executed_at: new Date().toISOString()
          }, null, 2)
        }]
      };
    } catch (_error: any) {
      this._logger.error(`‚ùå Network operation failed: ${operation}`, _error);
      return this.createErrorResult(`Network operation failed: ${_error instanceof Error ? _error.message : String(_error)}`);
    }
  }

  private async handleMonitorLogs(args: Record<string, unknown>): Promise<any> {
    const { log_file, lines, follow, filter } = args;
    
    this._logger.info(`üìä Monitoring logs: ${log_file}`);
    
    let command = `tail -n ${lines || 100}`;
    if (follow) {
      command += ' -f';
    }
    command += ` ${log_file}`;
    if (filter) {
      command += ` | grep "${filter}"`;
    }
    
    try {
      const { stdout, stderr } = await execAsync(command);
      
      return {
        content: [{
          type: 'text',
          text: JSON.stringify({
            log_file: log_file as string,
            lines: lines || 100,
            follow: follow || true,
            filter: filter as string,
            logs: stdout,
            stderr,
            monitored_at: new Date().toISOString()
          }, null, 2)
        }]
      };
    } catch (_error: any) {
      this._logger.error(`‚ùå Log monitoring failed: ${log_file}`, _error);
      return this.createErrorResult(`Log monitoring failed: ${_error instanceof Error ? _error.message : String(_error)}`);
    }
  }

  // Resource Handlers
  private async handleGetSystemStatus(): Promise<any> {
    return {
      contents: [{
        uri: 'terminal://system/status',
        mimeType: 'application/json',
        text: JSON.stringify({
          status: 'healthy',
          uptime: process.uptime(),
          memory_usage: process.memoryUsage(),
          platform: process.platform,
          arch: process.arch,
          node_version: process.version,
          timestamp: new Date().toISOString()
        }, null, 2)
      }]
    };
  }

  private async handleGetRunningProcesses(): Promise<any> {
    return {
      contents: [{
        uri: 'terminal://processes/running',
        mimeType: 'application/json',
        text: JSON.stringify({
          processes: [
            { pid: 1234, name: 'node', cpu: '2.5%', memory: '45.2 MB' },
            { pid: 5678, name: 'chrome', cpu: '15.3%', memory: '120.5 MB' }
          ],
          total: 2,
          timestamp: new Date().toISOString()
        }, null, 2)
      }]
    };
  }

  private async handleGetSystemLogs(): Promise<any> {
    return {
      contents: [{
        uri: 'terminal://logs/system',
        mimeType: 'text/plain',
        text: `[${new Date().toISOString()}] System started\n[${new Date().toISOString()}] MCP servers initialized\n`
      }]
    };
  }

  private createErrorResult(message: string): any {
    return {
      content: [{
        type: 'text',
        text: JSON.stringify({
          error: message,
          timestamp: new Date().toISOString()
        }, null, 2)
      }],
      isError: true,
      error: message
    };
  }

  private createErrorResponse(id: string | number, code: number, message: string, data?: unknown): MCPResponse {
    return {
      jsonrpc: '2.0',
      id,
      error: { code, message, data }
    };
  }
}

/**
 * Web Search MCP Server for Oliver-OS
 * Provides research and information gathering capabilities
 */

import { EventEmitter } from 'node:events';
import { Logger } from '../../core/logger';
import type { MCPTool, MCPResource, MCPRequest, MCPResponse, OliverOSMCPServer } from '../types';

export class WebSearchMCPServer extends EventEmitter implements OliverOSMCPServer {
  private _logger: Logger;
  public config: any;
  private isRunning: boolean = false;
  private apiKey: string;

  constructor(apiKey?: string) {
    super();
    this._logger = new Logger('WebSearch-MCP-Server');
    this.apiKey = apiKey || process.env['SEARCH_API_KEY'] || '';
    this.config = this.createServerConfig();
  }

  private createServerConfig() {
    return {
