  // Helper methods that would integrate with your actual BMAD services
  private async performBreakdown(task: string, _context: Record<string, unknown>, _maxDepth: number, includeDependencies: boolean): Promise<Record<string, unknown>> {
    return {
      originalTask: task,
      breakdown: [
        {
          id: 'subtask-1',
          title: 'Analyze requirements',
          description: 'Break down the task requirements into clear specifications',
          dependencies: [],
          estimatedTime: '2h',
          priority: 'high'
        },
        {
          id: 'subtask-2',
          title: 'Design solution',
          description: 'Create a high-level design for the solution',
          dependencies: ['subtask-1'],
          estimatedTime: '3h',
          priority: 'high'
        },
        {
          id: 'subtask-3',
          title: 'Implement core functionality',
          description: 'Implement the main functionality',
          dependencies: ['subtask-2'],
          estimatedTime: '8h',
          priority: 'medium'
        }
      ],
      dependencies: includeDependencies ? {
        external: ['react', 'typescript', 'express'],
        internal: ['config-service', 'logger-service']
      } : undefined,
      totalEstimatedTime: '13h',
      complexity: 'medium'
    };
  }

  private async performMapping(target: string, mapType: string, includeExternal: boolean, _format: string): Promise<Record<string, unknown>> {
    const baseMapping = {
      target,
      mapType,
      components: [
        { name: 'frontend', type: 'react-app', dependencies: ['backend-api'] },
        { name: 'backend-api', type: 'express-server', dependencies: ['database', 'ai-services'] },
        { name: 'database', type: 'postgresql', dependencies: [] },
        { name: 'ai-services', type: 'python-services', dependencies: ['database'] }
      ],
      connections: [
        { from: 'frontend', to: 'backend-api', type: 'http' },
        { from: 'backend-api', to: 'database', type: 'sql' },
        { from: 'backend-api', to: 'ai-services', type: 'http' }
      ]
    };

    if (includeExternal) {
      (baseMapping as any).externalDependencies = [
        { name: 'react', type: 'library', version: '18.2.0' },
        { name: 'express', type: 'framework', version: '4.18.2' },
        { name: 'postgresql', type: 'database', version: '14.0' }
      ];
    }

    return baseMapping;
  }

  private async performAutomation(process: string, template: string, outputFormat: string, language: string, options: Record<string, unknown>): Promise<Record<string, unknown>> {
    return {
      process,
      template,
      generatedCode: `// Generated ${outputFormat} for ${process}
// Template: ${template}
// Language: ${language}

export class ${process.charAt(0).toUpperCase() + process.slice(1)}Service {
  constructor() {
    // Auto-generated constructor
  }
  
  async execute() {
    // Auto-generated implementation
  }
}`,
      outputFormat,
      language,
      options,
      filesGenerated: [`${process}-service.${language === 'typescript' ? 'ts' : 'js'}`]
    };
  }

  private async generateDocumentation(target: string, docType: string, includeExamples: boolean, format: string, outputPath?: string): Promise<Record<string, unknown>> {
    return {
      target,
      docType,
      content: `# ${target} Documentation

## Overview
This document provides comprehensive documentation for ${target}.

## API Reference
${includeExamples ? `
### Example Usage
\`\`\`typescript
const service = new ${target}Service();
await service.initialize();
\`\`\`
` : ''}

## Configuration
- Environment variables
- Configuration options
- Default values

## Troubleshooting
Common issues and solutions`,
      format,
      outputPath: outputPath || `docs/${target}.md`,
      sections: ['overview', 'api-reference', 'configuration', 'troubleshooting']
    };
  }

  private async performAnalysis(target: string, analysisType: string, includeRecommendations: boolean, depth: string): Promise<Record<string, unknown>> {
    return {
      target,
      analysisType,
      results: {
        complexity: analysisType === 'complexity' ? {
          cyclomaticComplexity: 15,
          cognitiveComplexity: 8,
          maintainabilityIndex: 72
        } : undefined,
        dependencies: analysisType === 'dependencies' ? {
          totalDependencies: 45,
          directDependencies: 12,
          transitiveDependencies: 33,
          outdatedDependencies: 3
        } : undefined,
        quality: analysisType === 'quality' ? {
          testCoverage: 85,
          codeDuplication: 5,
          technicalDebt: 'medium'
        } : undefined
      },
      recommendations: includeRecommendations ? [
        'Consider refactoring complex functions',
        'Update outdated dependencies',
        'Increase test coverage for critical paths'
      ] : undefined,
      depth,
      timestamp: new Date().toISOString()
    };
  }

  private async performValidation(target: string, principles: string[], strictMode: boolean, generateReport: boolean): Promise<Record<string, unknown>> {
    return {
      target,
      principles: principles.length > 0 ? principles : ['modularity', 'testability', 'maintainability'],
      validationResults: {
        modularity: { passed: true, score: 8.5, issues: [] },
        testability: { passed: true, score: 7.8, issues: ['Some functions lack unit tests'] },
        maintainability: { passed: strictMode ? false : true, score: 6.5, issues: ['High cyclomatic complexity in main function'] }
      },
      overallScore: 7.6,
      passed: strictMode ? false : true,
      report: generateReport ? {
        summary: 'Validation completed with some issues found',
        recommendations: ['Add more unit tests', 'Refactor complex functions'],
        nextSteps: ['Address high-priority issues', 'Schedule code review']
      } : undefined
    };
  }

  private createErrorResult(message: string, error: unknown): MCPToolResult {
    return {
      content: [{
        type: 'text',
        text: JSON.stringify({
          error: message,
          details: error instanceof Error ? error.message : 'Unknown error'
        }, null, 2)
      }],
      isError: true,
      error: error instanceof Error ? error.message : 'Unknown error'
    };
  }
}

/**
 * MCP Tools for Codebuff Integration
 * Provides AI models with access to Codebuff agent spawning and management
 * Following BMAD principles: Break, Map, Automate, Document
 */

import { CodebuffService } from '../../services/codebuff/codebuff-service';
import { Logger } from '../../core/logger';
import type { MCPTool } from '../types';
import type { 
  CodebuffRunOptions, 
  AgentSpawnRequest, 
  WorkflowDefinition,
  AgentStatus 
} from '../../services/codebuff/types';

export class CodebuffMCPTools {
  private _codebuffService: CodebuffService;
  private _logger: Logger;

  constructor(codebuffService: CodebuffService) {
    this._codebuffService = codebuffService;
    this._logger = new Logger('CodebuffMCPTools');
  }

  /**
   * Get all available Codebuff MCP tools
   */
  getTools(): MCPTool[] {
    return [
      {
        name: 'codebuff_run_task',
        description: 'Run a coding task using a Codebuff AI agent',
        inputSchema: {
          type: 'object',
          properties: {
            agent: { 
              type: 'string', 
              description: 'Agent ID to use for the task',
              enum: ['code-generator', 'bureaucracy-disruptor', 'thought-processor', 'collaboration-coordinator']
            },
            prompt: { 
              type: 'string', 
              description: 'The task prompt to execute' 
            },
            timeout: { 
              type: 'number', 
              description: 'Timeout in milliseconds (default: 300000)' 
            },
            retries: { 
              type: 'number', 
              description: 'Number of retry attempts (default: 3)' 
            }
          },
          required: ['agent', 'prompt']
        },
        handler: this.handleRunTask.bind(this)
      },
      {
        name: 'codebuff_spawn_agent',
        description: 'Spawn a new AI agent with specific capabilities',
        inputSchema: {
          type: 'object',
          properties: {
            agentType: { 
              type: 'string', 
              description: 'Type of agent to spawn',
              enum: ['code-generator', 'bureaucracy-disruptor', 'thought-processor', 'collaboration-coordinator']
            },
            capabilities: { 
              type: 'array', 
              items: { type: 'string' },
              description: 'List of capabilities for the agent' 
            },
            config: { 
              type: 'object', 
              description: 'Agent configuration options' 
            },
            priority: { 
              type: 'string', 
              enum: ['low', 'normal', 'high', 'urgent'],
              description: 'Priority level for the agent' 
            }
          },
          required: ['agentType', 'capabilities']
        },
        handler: this.handleSpawnAgent.bind(this)
      },
      {
        name: 'codebuff_get_agent_status',
        description: 'Get status of AI agents and their current tasks',
        inputSchema: {
          type: 'object',
          properties: {
            agentId: { 
              type: 'string', 
              description: 'Specific agent ID to check (optional)' 
            }
          }
        },
        handler: this.handleGetAgentStatus.bind(this)
      },
      {
        name: 'codebuff_create_workflow',
        description: 'Create a new workflow definition for automated agent coordination',
        inputSchema: {
          type: 'object',
          properties: {
            id: { type: 'string', description: 'Unique workflow ID' },
            name: { type: 'string', description: 'Workflow display name' },
            description: { type: 'string', description: 'Workflow description' },
            steps: {
              type: 'array',
              items: {
                type: 'object',
                properties: {
                  agent: { type: 'string', description: 'Agent to execute this step' },
                  prompt: { type: 'string', description: 'Prompt for this step' },
                  timeout: { type: 'number', description: 'Timeout for this step' },
                  retries: { type: 'number', description: 'Retry attempts for this step' }
                },
                required: ['agent', 'prompt']
              },
              description: 'List of workflow steps'
            }
          },
          required: ['id', 'name', 'description', 'steps']
        },
        handler: this.handleCreateWorkflow.bind(this)
      },
      {
        name: 'codebuff_execute_workflow',
        description: 'Execute a workflow with multiple agent steps',
        inputSchema: {
          type: 'object',
          properties: {
            workflowId: { 
              type: 'string', 
              description: 'ID of the workflow to execute' 
            }
          },
          required: ['workflowId']
        },
        handler: this.handleExecuteWorkflow.bind(this)
      },
      {
        name: 'codebuff_get_agent_definitions',
        description: 'Get all available agent definitions and their capabilities',
        inputSchema: {
          type: 'object',
          properties: {}
        },
        handler: this.handleGetAgentDefinitions.bind(this)
