        inputSchema: {
          type: 'object',
          properties: {
            agentId: { 
              type: 'string', 
              description: 'ID of the agent to terminate' 
            }
          },
          required: ['agentId']
        },
        handler: this.handleTerminateAgent.bind(this)
      }
    ];
  }

  // Tool Handlers

  private async handleRunTask(args: Record<string, unknown>): Promise<any> {
    try {
      const { agent, prompt, timeout, retries } = args;
      
      this._logger.info(`üöÄ Running Codebuff task with agent: ${agent}`);
      
      const options: CodebuffRunOptions = {
        agent: agent as string,
        prompt: prompt as string,
        timeout: timeout as number || 300000,
        retries: retries as number || 3,
        handleEvent: (event) => {
          this._logger.debug(`Codebuff event: ${event.type} - ${event.message}`);
        }
      };

      const result = await this._codebuffService.runTask(options);
      
      return {
        content: [{
          type: 'text',
          text: JSON.stringify({
            success: result.success,
            output: result.output,
            error: result.error,
            events: result.events,
            metadata: result.metadata
          }, null, 2)
        }]
      };

    } catch (error) {
      this._logger.error('‚ùå Error running Codebuff task', error);
      return {
        content: [{
          type: 'text',
          text: JSON.stringify({
            success: false,
            error: error instanceof Error ? error.message : 'Unknown error'
          }, null, 2)
        }]
      };
    }
  }

  private async handleSpawnAgent(args: Record<string, unknown>): Promise<any> {
    try {
      const { agentType, capabilities, config, priority } = args;
      
      this._logger.info(`ü§ñ Spawning agent of type: ${agentType}`);
      
      const request: AgentSpawnRequest = {
        agentType: agentType as string,
        capabilities: capabilities as string[],
        config: config as Record<string, unknown> || {},
        priority: priority as 'low' | 'normal' | 'high' | 'urgent' || 'normal'
      };

      const agent = await this._codebuffService.spawnAgent(request);
      
      return {
        content: [{
          type: 'text',
          text: JSON.stringify({
            success: true,
            agent
          }, null, 2)
        }]
      };

    } catch (error) {
      this._logger.error('‚ùå Error spawning agent', error);
      return {
        content: [{
          type: 'text',
          text: JSON.stringify({
            success: false,
            error: error instanceof Error ? error.message : 'Unknown error'
          }, null, 2)
        }]
      };
    }
  }

