/**
 * Error Handling Middleware
 * Centralized error handling with proper logging and responses
 */

import type { Request, Response, NextFunction } from 'express';
import { Logger } from '../core/logger';

const logger = new Logger('ErrorHandler');

export interface AppError extends Error {
  statusCode?: number;
  isOperational?: boolean;
}

export function errorHandler(
  error: AppError,
  req: Request,
  res: Response,
  _next: NextFunction
): void {
  // Log the error
  logger.error('Unhandled error', error, {
    url: req.url,
    method: req.method,
    ip: req.ip,
    userAgent: req.get('User-Agent')
  });

  // Determine status code
  const statusCode = error.statusCode || 500;
  const isOperational = error.isOperational || false;

  // Prepare error response
  const errorResponse: { error: any } = {
    error: {
      message: isOperational ? error.message : 'Internal Server Error',
      statusCode,
      timestamp: new Date().toISOString(),
      path: req.url,
      method: req.method
    }
  };

  // Add stack trace in development
  if (process.env['NODE_ENV'] === 'development') {
    (errorResponse.error as any).stack = error.stack || undefined;
    (errorResponse.error as any).details = error;
  }

  res.status(statusCode).json(errorResponse);
}

export function createError(message: string, statusCode: number = 500): AppError {
  const error = new Error(message) as AppError;
  error.statusCode = statusCode;
  error.isOperational = true;
  return error;
}

/**
 * Request Logging Middleware
 * Logs all incoming requests with timing and metadata
 */

import type { Request, Response, NextFunction } from 'express';
import { Logger } from '../core/logger';

const logger = new Logger('RequestLogger');

export function requestLogger(req: Request, res: Response, next: NextFunction): void {
  const startTime = Date.now();
  
  // Add request start time to headers for response time calculation
  req.headers['x-request-start'] = startTime.toString();
  
  // Log the incoming request
  logger.info('Incoming request', {
    method: req.method,
    url: req.url,
    ip: req.ip,
    userAgent: req.get('User-Agent'),
    timestamp: new Date().toISOString()
  });

  // Override res.end to log response
  const originalEnd = res.end;
  res.end = function(chunk?: any, encoding?: any, _cb?: any): any {
    const responseTime = Date.now() - startTime;
    
    logger.info('Response sent', {
      method: req.method,
      url: req.url,
      statusCode: res.statusCode,
      responseTime: `${responseTime}ms`,
      contentLength: res.get('Content-Length') || '0',
      timestamp: new Date().toISOString()
    });

    // Call original end method
    originalEnd.call(this, chunk, encoding);
  };

  next();
}

/**
 * Input Validation Middleware
 * Validates and sanitizes user input
 */

import type { Request, Response, NextFunction } from 'express';
import { z } from 'zod';
import { SecurityManager } from '../core/security';
import { Logger } from '../core/logger';

const logger = new Logger('ValidationMiddleware');

// Common validation schemas
export const commonSchemas = {
  email: z.string().email('Invalid email format'),
  password: z.string().min(8, 'Password must be at least 8 characters'),
  name: z.string().min(2, 'Name must be at least 2 characters').max(50, 'Name must be less than 50 characters'),
  uuid: z.string().uuid('Invalid UUID format'),
  positiveInt: z.number().int().positive('Must be a positive integer'),
  nonEmptyString: z.string().min(1, 'Field cannot be empty'),
  optionalString: z.string().optional(),
  optionalEmail: z.string().email('Invalid email format').optional().or(z.literal('')),
};

// Request validation schemas
export const requestSchemas = {
  register: z.object({
    email: commonSchemas.email,
    name: commonSchemas.name,
    password: commonSchemas.password,
  }),

  login: z.object({
    email: commonSchemas.email,
    password: commonSchemas.password,
  }),

  refreshToken: z.object({
    refreshToken: commonSchemas.nonEmptyString,
  }),

  updateProfile: z.object({
    name: commonSchemas.name.optional(),
    avatarUrl: commonSchemas.optionalString,
  }),

  changePassword: z.object({
    currentPassword: commonSchemas.password,
    newPassword: commonSchemas.password,
  }),

  createThought: z.object({
    content: z.string().min(1, 'Thought content cannot be empty').max(10000, 'Thought content too long'),
    type: z.enum(['text', 'voice', 'image']).optional(),
    metadata: z.record(z.string(), z.any()).optional(),
  }),

  createCollaborationSession: z.object({
    name: commonSchemas.nonEmptyString,
    description: commonSchemas.optionalString,
    settings: z.record(z.string(), z.any()).optional(),
  }),
};

export class ValidationMiddleware {
  private securityManager!: SecurityManager;

  constructor(securityManager: SecurityManager) {
    this.securityManager = securityManager;
  }

  /**
   * Generic validation middleware
   */
  validate = (schema: z.ZodSchema, target: 'body' | 'query' | 'params' = 'body') => {
    return (req: Request, res: Response, next: NextFunction): void => {
      try {
        const data = req[target];
        const validatedData = schema.parse(data);

        // Sanitize string inputs
        if (target === 'body') {
          this.sanitizeRequestBody(validatedData);
        }

        req[target] = validatedData;
        next();
      } catch (error) {
        if (error instanceof z.ZodError) {
          logger.warn(`Validation failed for ${req.method} ${req.path}:`, { issues: error.issues });
          res.status(400).json({
            success: false,
            error: 'Validation failed',
            details: error.issues.map((err: z.ZodIssue) => ({
              field: err.path.join('.'),
              message: err.message,
            })),
          });
          return;
        }

        logger.error('Validation error:', error);
        res.status(500).json({
          success: false,
          error: 'Internal validation error',
        });
      }
    };
  };

  /**
   * Sanitize request body
   */
  private sanitizeRequestBody(data: any): void {
    if (typeof data === 'object' && data !== null) {
      for (const key in data) {
        if (typeof data[key] === 'string') {
          data[key] = this.securityManager.sanitizeInput(data[key]);
        } else if (typeof data[key] === 'object') {
          this.sanitizeRequestBody(data[key]);
        }
      }
    }
  }

  /**
   * Validate email format
   */
  validateEmail = (req: Request, res: Response, next: NextFunction): void => {
    const { email } = req.body;

    if (email && !this.securityManager.validateEmail(email)) {
      res.status(400).json({
        success: false,
        error: 'Invalid email format',
      });
      return;
    }

    next();
  };

  /**
   * Validate password strength
   */
  validatePassword = (req: Request, res: Response, next: NextFunction): void => {
    const { password } = req.body;

    if (password) {
      const validation = this.securityManager.validatePassword(password);
      if (!validation.isValid) {
        res.status(400).json({
          success: false,
          error: 'Password validation failed',
          details: validation.errors,
        });
        return;
      }
    }

    next();
  };

  /**
   * Check for suspicious activity
   */
  checkSuspiciousActivity = (req: Request, res: Response, next: NextFunction): void => {
    if (this.securityManager.checkSuspiciousActivity(req)) {
      this.securityManager.logSecurityEvent('Suspicious activity detected', {
        ip: req.ip,
        userAgent: req.get('User-Agent'),
        path: req.path,
        method: req.method,
      });

      res.status(403).json({
        success: false,
        error: 'Access denied',
        message: 'Suspicious activity detected',
      });
      return;
    }

    next();
  };

  /**
   * Validate file upload
   */
  validateFileUpload = (maxSize: number = 10 * 1024 * 1024, allowedTypes: string[] = ['image/jpeg', 'image/png', 'image/gif']) => {
    return (req: Request, res: Response, next: NextFunction): void => {
      if (!(req as any).file) {
        next();
        return;
      }

      const { size, mimetype } = (req as any).file;

      if (size > maxSize) {
        res.status(400).json({
          success: false,
          error: 'File too large',
          message: `File size must be less than ${maxSize / (1024 * 1024)}MB`,
        });
        return;
      }

      if (!allowedTypes.includes(mimetype)) {
        res.status(400).json({
          success: false,
          error: 'Invalid file type',
          message: `Allowed types: ${allowedTypes.join(', ')}`,
        });
        return;
      }

      next();
    };
  };

  /**
   * Validate pagination parameters
   */
  validatePagination = (req: Request, res: Response, next: NextFunction): void => {
    const { page = '1', limit = '10' } = req.query;

    const pageNum = parseInt(page as string, 10);
    const limitNum = parseInt(limit as string, 10);

    if (isNaN(pageNum) || pageNum < 1) {
      res.status(400).json({
        success: false,
        error: 'Invalid page parameter',
        message: 'Page must be a positive integer',
      });
      return;
    }

    if (isNaN(limitNum) || limitNum < 1 || limitNum > 100) {
      res.status(400).json({
        success: false,
        error: 'Invalid limit parameter',
        message: 'Limit must be between 1 and 100',
      });
      return;
    }

    req.query['page'] = pageNum.toString();
    req.query['limit'] = limitNum.toString();

    next();
  };
}

/**
 * Create validation middleware instance
 */
export function createValidationMiddleware(securityManager: SecurityManager): ValidationMiddleware {
  return new ValidationMiddleware(securityManager);
}

/**
 * Oliver-OS Agent Routes
 * API endpoints for agent spawning and management
 */

import { Router } from 'express';
import type { Request, Response } from 'express';
import { ServiceManager } from '../services/service-manager';
import type { SpawnRequest } from '../services/agent-manager';

const router = Router();

export function createAgentRoutes(serviceManager: ServiceManager): Router {

  // Spawn a single agent
  router.post('/spawn', async (req: Request, res: Response): Promise<void> => {
    try {
      const spawnRequest: SpawnRequest = req.body;

      // Validate request
      if (!spawnRequest.agentType || !spawnRequest.prompt) {
        res.status(400).json({
          error: 'Invalid request',
          details: 'agentType and prompt are required'
        });
        return;
      }

      const spawnedAgent = await serviceManager.spawnAgent(spawnRequest);

      res.json({
        success: true,
        data: spawnedAgent
      });
    } catch (error) {
      console.error('Error spawning agent:', error);
      res.status(500).json({
        error: 'Failed to spawn agent',
        details: error instanceof Error ? error.message : 'Unknown error'
      });
    }
  });

  // Spawn multiple agents
  router.post('/spawn-multiple', async (req: Request, res: Response): Promise<void> => {
    try {
      const spawnRequests: SpawnRequest[] = req.body.requests || req.body;

      // Validate requests
      if (!Array.isArray(spawnRequests) || spawnRequests.length === 0) {
        res.status(400).json({
          error: 'Invalid request',
          details: 'requests array is required and cannot be empty'
        });
        return;
      }

      for (const request of spawnRequests) {
        if (!request.agentType || !request.prompt) {
          res.status(400).json({
            error: 'Invalid request',
            details: 'Each request must have agentType and prompt'
          });
          return;
        }
      }

      const spawnedAgents = await serviceManager.spawnMultipleAgents(spawnRequests);

      res.json({
        success: true,
        data: {
          spawned_agents: spawnedAgents,
          count: spawnedAgents.length
        }
      });
    } catch (error) {
      console.error('Error spawning multiple agents:', error);
      res.status(500).json({
        error: 'Failed to spawn multiple agents',
        details: error instanceof Error ? error.message : 'Unknown error'
      });
    }
  });

  // Get all available agent types
  router.get('/', async (_req: Request, res: Response): Promise<void> => {
    try {
      const agents = serviceManager.getAgents();

      res.json({
        success: true,
        data: {
          agents,
          count: agents.length
        }
      });
    } catch (error) {
      console.error('Error getting agents:', error);
      res.status(500).json({
        error: 'Failed to get agents',
        details: error instanceof Error ? error.message : 'Unknown error'
      });
    }
  });

  // Get all spawned agent instances
  router.get('/spawned', async (_req: Request, res: Response): Promise<void> => {
    try {
      const spawnedAgents = serviceManager.getSpawnedAgents();

      res.json({
        success: true,
        data: {
          spawned_agents: spawnedAgents,
          count: spawnedAgents.length
        }
      });
    } catch (error) {
      console.error('Error getting spawned agents:', error);
      res.status(500).json({
        error: 'Failed to get spawned agents',
        details: error instanceof Error ? error.message : 'Unknown error'
      });
    }
  });

  // Get specific agent type
  router.get('/:agentId', async (req: Request, res: Response): Promise<void> => {
    try {
      const { agentId } = req.params;
      if (!agentId) {
        res.status(400).json({
          error: 'Agent ID required'
        });
        return;
      }
      const agent = serviceManager.getAgent(agentId);

      if (!agent) {
        res.status(404).json({
          error: 'Agent not found',
          details: `No agent found with ID: ${agentId}`
        });
        return;
      }

      res.json({
        success: true,
        data: agent
      });
    } catch (error) {
      console.error('Error getting agent:', error);
      res.status(500).json({
        error: 'Failed to get agent',
        details: error instanceof Error ? error.message : 'Unknown error'
      });
    }
  });

  // Get specific spawned agent instance
  router.get('/spawned/:spawnedAgentId', async (req: Request, res: Response): Promise<void> => {
    try {
      const { spawnedAgentId } = req.params;
      if (!spawnedAgentId) {
        res.status(400).json({
          error: 'Spawned agent ID required'
        });
        return;
      }
      const spawnedAgent = serviceManager.getSpawnedAgent(spawnedAgentId);

      if (!spawnedAgent) {
        res.status(404).json({
          error: 'Spawned agent not found',
          details: `No spawned agent found with ID: ${spawnedAgentId}`
        });
        return;
      }

      res.json({
        success: true,
        data: spawnedAgent
      });
    } catch (error) {
      console.error('Error getting spawned agent:', error);
      res.status(500).json({
        error: 'Failed to get spawned agent',
        details: error instanceof Error ? error.message : 'Unknown error'
      });
    }
  });

  // Get agent status and health
  router.get('/health/status', async (_req: Request, res: Response): Promise<void> => {
    try {
      const agents = serviceManager.getAgents();
      const spawnedAgents = serviceManager.getSpawnedAgents();

      const status = {
        total_agent_types: agents.length,
        total_spawned_agents: spawnedAgents.length,
        running_agents: spawnedAgents.filter(agent => agent.status === 'running').length,
        completed_agents: spawnedAgents.filter(agent => agent.status === 'completed').length,
        failed_agents: spawnedAgents.filter(agent => agent.status === 'failed').length,
        agent_types: agents.map(agent => ({
          id: agent.id,
          displayName: agent.displayName,
          status: agent.status
        })),
        recent_activity: spawnedAgents
          .sort((a, b) => b.startTime.getTime() - a.startTime.getTime())
          .slice(0, 10)
          .map(agent => ({
            id: agent.id,
            agentType: agent.agentType,
            status: agent.status,
            startTime: agent.startTime
          }))
      };

      res.json({
        success: true,
        data: status
      });
    } catch (error) {
      console.error('Error getting agent health status:', error);
      res.status(500).json({
        error: 'Failed to get agent health status',
        details: error instanceof Error ? error.message : 'Unknown error'
      });
    }
  });

  return router;
}

/**
 * WebSocket Routes
 * API endpoints for WebSocket management and status
 */

import { Router, type Request, type Response } from 'express';
import { Logger } from '../core/logger';

const logger = new Logger('WebSocketRoutes');
const router: Router = Router();

// Store WebSocket manager reference (will be set by the server)
let wsManager: any = null;

export function setWebSocketManager(manager: any): void {
  wsManager = manager;
}

/**
 * Get WebSocket connection status
 */
router.get('/status', (_req: Request, res: Response) => {
  try {
    if (!wsManager) {
      return res.status(503).json({
        status: 'unavailable',
        message: 'WebSocket manager not initialized'
      });
    }

    const healthStatus = wsManager.getHealthStatus();
    return res.json(healthStatus);
  } catch (error) {
    logger.error('Error getting WebSocket status:', error);
    return res.status(500).json({
      status: 'error',
      message: 'Failed to get WebSocket status',
      error: error instanceof Error ? error.message : 'Unknown error'
    });
  }
});

/**
 * Get connected clients
 */
router.get('/clients', (_req: Request, res: Response) => {
  try {
    if (!wsManager) {
      return res.status(503).json({
        status: 'unavailable',
        message: 'WebSocket manager not initialized'
      });
    }

    const clients = wsManager.getConnectedClients();
    return res.json({
      clients: clients.map((client: any) => ({
        id: client.id,
        user_id: client.user_id,
        last_seen: client.last_seen,
        subscriptions: client.subscriptions
      })),
      count: clients.length
    });
  } catch (error) {
    logger.error('Error getting connected clients:', error);
    return res.status(500).json({
      status: 'error',
      message: 'Failed to get connected clients',
      error: error instanceof Error ? error.message : 'Unknown error'
    });
  }
});

/**
 * Get thought sessions
 */
router.get('/sessions', (_req: Request, res: Response) => {
  try {
    if (!wsManager) {
      return res.status(503).json({
