/**
 * Contextual Suggestion Engine
 * Provides intelligent suggestions based on project history and patterns
 * Following BMAD principles: Break, Map, Automate, Document
 */

import { EventEmitter } from 'node:events';
import { Logger } from '../../core/logger';
import { Config } from '../../core/config';
import { MemoryService } from './memory-service';
import { LearningService } from './learning-service';
import fs from 'fs-extra';

export interface ContextualSuggestion {
  id: string;
  type: 'code-generation' | 'refactoring' | 'optimization' | 'architecture' | 'naming' | 'workflow';
  title: string;
  description: string;
  content: string;
  confidence: number;
  reasoning: string;
  alternatives: string[];
  context: string;
  impact: 'low' | 'medium' | 'high';
  effort: 'low' | 'medium' | 'high';
}

export interface SuggestionContext {
  currentFile: string;
  fileType: string;
  projectStructure: string[];
  recentChanges: string[];
  userPreferences: any;
  codingPatterns: string[];
  architectureDecisions: string[];
  namingConventions: any;
}

export class ContextualSuggestionEngine extends EventEmitter {
  private _logger: Logger;
  private _memoryService: MemoryService;
  private _learningService: LearningService;
  private suggestionCache: Map<string, ContextualSuggestion[]>;
  private contextHistory: Map<string, SuggestionContext>;

  constructor(_config: Config, memoryService: MemoryService, learningService: LearningService) {
    super();
    this._memoryService = memoryService;
    this._learningService = learningService;
    this._logger = new Logger('ContextualSuggestionEngine');
    this.suggestionCache = new Map();
    this.contextHistory = new Map();
  }

  /**
   * Initialize suggestion engine
   */
  async initialize(): Promise<void> {
    this._logger.info('üí° Initializing Contextual Suggestion Engine...');
    
    try {
      await this.loadContextHistory();
      await this.buildSuggestionCache();
      
      this._logger.info('‚úÖ Contextual Suggestion Engine initialized successfully');
      this.emit('suggestion-engine:initialized');
    } catch (error) {
      this._logger.error('Failed to initialize suggestion engine:', error);
      throw error;
    }
  }

  /**
   * Load context history from memory
   */
  private async loadContextHistory(): Promise<void> {
    const memory = this._memoryService as any;
    const sessions = memory.memory?.projectHistory?.sessions || [];
    
    for (const session of sessions) {
      const context: SuggestionContext = {
        currentFile: session.filesModified?.[0] || 'unknown',
        fileType: this.getFileType(session.filesModified?.[0] || ''),
        projectStructure: session.patternsUsed || [],
        recentChanges: session.patternsUsed || [],
        userPreferences: memory.memory?.codePatterns?.userPreferences || {},
        codingPatterns: session.patternsUsed || [],
        architectureDecisions: session.decisionsMade || [],
        namingConventions: memory.memory?.namingConventions || {}
      };
      
      this.contextHistory.set(session.id, context);
    }

    this._logger.info(`üìö Loaded ${sessions.length} context history entries`);
  }

  /**
   * Build suggestion cache
   */
  private async buildSuggestionCache(): Promise<void> {
    // Build cache based on common contexts
    const commonContexts = this.getCommonContexts();
    
    for (const context of commonContexts) {
      const suggestions = await this.generateSuggestionsForContext(context);
      this.suggestionCache.set(context.currentFile, suggestions);
    }

    this._logger.info(`üóÇÔ∏è Built suggestion cache with ${commonContexts.length} contexts`);
  }

  /**
   * Get common contexts
   */
  private getCommonContexts(): SuggestionContext[] {
    const contexts: SuggestionContext[] = [];
    const memory = this._memoryService as any;
    
    // Common file types
    const commonFileTypes = ['tsx', 'ts', 'js', 'jsx', 'py'];
    
    for (const fileType of commonFileTypes) {
      contexts.push({
        currentFile: `example.${fileType}`,
        fileType,
        projectStructure: ['src', 'components', 'services'],
        recentChanges: [],
        userPreferences: memory.memory?.codePatterns?.userPreferences || {},
        codingPatterns: [],
        architectureDecisions: memory.memory?.architecture?.decisions?.map((d: any) => d.id) || [],
        namingConventions: memory.memory?.namingConventions || {}
      });
    }
    
    return contexts;
  }

  /**
   * Generate suggestions for current context
   */
  async generateSuggestionsForContext(context: SuggestionContext): Promise<ContextualSuggestion[]> {
    const suggestions: ContextualSuggestion[] = [];
    
    // Check cache first
    const cacheKey = context.currentFile;
    if (this.suggestionCache.has(cacheKey)) {
      const cachedSuggestions = this.suggestionCache.get(cacheKey)!;
      this._logger.info(`üìã Using cached suggestions for ${cacheKey}`);
      return cachedSuggestions;
    }
    
    // Generate code generation suggestions
    const codeSuggestions = await this.generateCodeGenerationSuggestions(context);
    suggestions.push(...codeSuggestions);
    
    // Generate refactoring suggestions
    const refactoringSuggestions = await this.generateRefactoringSuggestions(context);
    suggestions.push(...refactoringSuggestions);
    
    // Generate optimization suggestions
    const optimizationSuggestions = await this.generateOptimizationSuggestions(context);
    suggestions.push(...optimizationSuggestions);
    
    // Generate architecture suggestions
    const architectureSuggestions = await this.generateArchitectureSuggestions(context);
    suggestions.push(...architectureSuggestions);
    
    // Generate naming suggestions
    const namingSuggestions = await this.generateNamingSuggestions(context);
    suggestions.push(...namingSuggestions);
    
    // Sort by confidence and impact
    suggestions.sort((a, b) => {
      const scoreA = a.confidence * this.getImpactScore(a.impact);
      const scoreB = b.confidence * this.getImpactScore(b.impact);
      return scoreB - scoreA;
    });
    
    // Cache suggestions
    this.suggestionCache.set(cacheKey, suggestions);
    
    this._logger.info(`üí° Generated ${suggestions.length} suggestions for context`);
    return suggestions.slice(0, 10); // Return top 10 suggestions
  }

  /**
   * Generate code generation suggestions
   */
  private async generateCodeGenerationSuggestions(context: SuggestionContext): Promise<ContextualSuggestion[]> {
    const suggestions: ContextualSuggestion[] = [];
    const memory = this._memoryService as any;
    const patterns = memory.memory?.codePatterns?.frequentlyUsed || [];
    
    for (const pattern of patterns) {
      if (pattern.successRate > 0.8 && this.isRelevantForFileType(pattern, context.fileType)) {
        suggestions.push({
          id: `code-gen-${pattern.id}`,
          type: 'code-generation',
          title: `Generate ${pattern.pattern} pattern`,
          description: `Use the ${pattern.pattern} pattern which has been successful ${(pattern.successRate * 100).toFixed(1)}% of the time`,
          content: pattern.example,
          confidence: pattern.successRate,
          reasoning: `This pattern has been used ${pattern.frequency} times with ${(pattern.successRate * 100).toFixed(1)}% success rate`,
          alternatives: this.getAlternativePatterns(pattern),
          context: context.currentFile,
          impact: this.getPatternImpact(pattern),
          effort: this.getPatternEffort(pattern)
        });
      }
    }
    
    return suggestions;
  }

  /**
   * Generate refactoring suggestions
   */
  private async generateRefactoringSuggestions(context: SuggestionContext): Promise<ContextualSuggestion[]> {
    const suggestions: ContextualSuggestion[] = [];
    const memory = this._memoryService as any;
    const patterns = memory.memory?.codePatterns?.frequentlyUsed || [];
    
    for (const pattern of patterns) {
      if (pattern.successRate > 0.9 && pattern.frequency > 5) {
        suggestions.push({
          id: `refactor-${pattern.id}`,
          type: 'refactoring',
          title: `Refactor to ${pattern.pattern} pattern`,
          description: `Refactor existing code to use the ${pattern.pattern} pattern for better maintainability`,
          content: pattern.example,
          confidence: pattern.successRate,
          reasoning: `This pattern has been successful ${(pattern.successRate * 100).toFixed(1)}% of the time and used ${pattern.frequency} times`,
          alternatives: this.getAlternativePatterns(pattern),
          context: context.currentFile,
          impact: 'medium',
          effort: 'medium'
        });
      }
    }
    
    return suggestions;
  }

  /**
   * Generate optimization suggestions
   */
  private async generateOptimizationSuggestions(context: SuggestionContext): Promise<ContextualSuggestion[]> {
    const suggestions: ContextualSuggestion[] = [];
    const memory = this._memoryService as any;
    const patterns = memory.memory?.codePatterns?.frequentlyUsed || [];
    
    for (const pattern of patterns) {
      if (pattern.frequency > 10 && pattern.successRate > 0.85) {
        suggestions.push({
          id: `optimize-${pattern.id}`,
          type: 'optimization',
          title: `Optimize with ${pattern.pattern} pattern`,
          description: `Optimize code using the ${pattern.pattern} pattern for better performance`,
          content: pattern.example,
          confidence: pattern.successRate,
          reasoning: `This pattern has been used ${pattern.frequency} times with high success rate`,
          alternatives: this.getAlternativePatterns(pattern),
          context: context.currentFile,
          impact: 'high',
          effort: 'low'
        });
      }
    }
    
    return suggestions;
  }

  /**
   * Generate architecture suggestions
   */
  private async generateArchitectureSuggestions(context: SuggestionContext): Promise<ContextualSuggestion[]> {
    const suggestions: ContextualSuggestion[] = [];
    const memory = this._memoryService as any;
    const decisions = memory.memory?.architecture?.decisions || [];
    
    for (const decision of decisions) {
      if (decision.impact === 'high') {
        suggestions.push({
          id: `arch-${decision.id}`,
          type: 'architecture',
          title: `Apply ${decision.decision} architecture decision`,
          description: decision.rationale,
          content: decision.decision,
          confidence: 0.9,
          reasoning: `This architecture decision has high impact and was made on ${decision.date}`,
          alternatives: this.getAlternativeArchitectureDecisions(decision),
          context: context.currentFile,
          impact: 'high',
          effort: 'high'
        });
      }
    }
    
    return suggestions;
  }

  /**
   * Generate naming suggestions
   */
  private async generateNamingSuggestions(context: SuggestionContext): Promise<ContextualSuggestion[]> {
    const suggestions: ContextualSuggestion[] = [];
    const namingConventions = context.namingConventions;
    
    // Check if namingConventions exists and is an object
    if (!namingConventions || typeof namingConventions !== 'object') {
      this._logger.warn('No naming conventions found in context, using defaults');
      return this.generateDefaultNamingSuggestions(context);
    }
    
    try {
      for (const [type, conventions] of Object.entries(namingConventions)) {
        if (typeof conventions === 'object' && conventions !== null) {
          for (const [name, convention] of Object.entries(conventions)) {
            suggestions.push({
              id: `naming-${type}-${name}`,
              type: 'naming',
              title: `Use ${convention} naming convention for ${type}`,
              description: `Apply the ${convention} naming convention for ${type} variables/functions`,
              content: convention,
              confidence: 0.8,
              reasoning: `This naming convention has been used consistently for ${type}`,
              alternatives: this.getAlternativeNamingConventions(type),
              context: context.currentFile,
              impact: 'low',
              effort: 'low'
            });
          }
        }
      }
    } catch (error) {
      this._logger.error('Error generating naming suggestions:', error);
      return this.generateDefaultNamingSuggestions(context);
    }
    
    return suggestions;
  }

  /**
   * Generate default naming suggestions when no conventions are available
   */
  private generateDefaultNamingSuggestions(context: SuggestionContext): ContextualSuggestion[] {
    const suggestions: ContextualSuggestion[] = [];
    
    const defaultConventions = {
      variables: 'camelCase',
      functions: 'camelCase',
      classes: 'PascalCase',
      constants: 'UPPER_SNAKE_CASE',
      interfaces: 'PascalCase',
      types: 'PascalCase',
      enums: 'PascalCase',
      files: 'kebab-case'
    };
    
    for (const [type, convention] of Object.entries(defaultConventions)) {
      suggestions.push({
        id: `naming-default-${type}`,
        type: 'naming',
        title: `Use ${convention} naming convention for ${type}`,
        description: `Apply the standard ${convention} naming convention for ${type}`,
        content: convention,
        confidence: 0.7,
        reasoning: `This is the standard naming convention for ${type} in TypeScript/JavaScript`,
        alternatives: this.getAlternativeNamingConventions(type),
        context: context.currentFile,
        impact: 'low',
        effort: 'low'
      });
    }
    
    return suggestions;
  }

  /**
   * Check if pattern is relevant for file type
   */
  private isRelevantForFileType(pattern: any, fileType: string): boolean {
    const relevantPatterns = {
      'tsx': ['react-component', 'typescript-interface', 'event-handler'],
      'ts': ['service-class', 'error-handling', 'async-function'],
      'js': ['function', 'module', 'callback'],
      'jsx': ['react-component', 'jsx-element', 'props'],
      'py': ['class', 'function', 'async-function']
    };
    
    return relevantPatterns[fileType as keyof typeof relevantPatterns]?.some(p => 
      pattern.pattern.toLowerCase().includes(p)
    ) || false;
  }

  /**
   * Get alternative patterns
   */
  private getAlternativePatterns(pattern: any): string[] {
    const memory = this._memoryService as any;
    const patterns = memory.memory?.codePatterns?.frequentlyUsed || [];
    
    return patterns
      .filter((p: any) => p.id !== pattern.id && p.successRate > 0.7)
      .slice(0, 3)
      .map((p: any) => p.pattern);
  }

  /**
   * Get alternative architecture decisions
   */
  private getAlternativeArchitectureDecisions(decision: any): string[] {
    const memory = this._memoryService as any;
    const decisions = memory.memory?.architecture?.decisions || [];
    
    return decisions
      .filter((d: any) => d.id !== decision.id && d.impact === 'high')
      .slice(0, 3)
      .map((d: any) => d.decision);
  }

  /**
   * Get alternative naming conventions
   */
  private getAlternativeNamingConventions(type: string): string[] {
    const alternatives = {
      'variables': ['camelCase', 'snake_case', 'PascalCase'],
      'functions': ['camelCase', 'snake_case', 'PascalCase'],
      'components': ['PascalCase', 'camelCase', 'kebab-case'],
      'files': ['kebab-case', 'camelCase', 'snake_case'],
      'constants': ['UPPER_SNAKE_CASE', 'PascalCase', 'camelCase']
    };
    
    return alternatives[type as keyof typeof alternatives] || [];
  }

  /**
   * Get impact score
   */
  private getImpactScore(impact: string): number {
    const scores = { low: 1, medium: 2, high: 3 };
    return scores[impact as keyof typeof scores] || 1;
  }

  /**
   * Get pattern impact
   */
  private getPatternImpact(pattern: any): 'low' | 'medium' | 'high' {
    if (pattern.frequency > 15) return 'high';
    if (pattern.frequency > 8) return 'medium';
    return 'low';
  }

  /**
   * Get pattern effort
   */
  private getPatternEffort(pattern: any): 'low' | 'medium' | 'high' {
    if (pattern.successRate > 0.9) return 'low';
    if (pattern.successRate > 0.8) return 'medium';
    return 'high';
  }

  /**
   * Get file type from filename
   */
  private getFileType(filename: string): string {
    const parts = filename.split('.');
    return parts[parts.length - 1] || 'unknown';
  }

  /**
   * Generate suggestions for a specific file
   */
  async generateSuggestions(filePath: string): Promise<ContextualSuggestion[]> {
    const context: SuggestionContext = {
      currentFile: filePath,
      fileType: this.getFileType(filePath),
      projectStructure: [],
      recentChanges: [],
      userPreferences: {},
      codingPatterns: [],
      architectureDecisions: [],
      namingConventions: {}
    };
    
    return await this.generateSuggestionsForContext(context);
  }

  /**
   * Record suggestion feedback
   */
  recordSuggestionFeedback(suggestionId: string, accepted: boolean, feedback?: string): void {
    this._learningService.learnFromFeedback(suggestionId, accepted, feedback);
    
    this._logger.info(`üìù Recorded suggestion feedback: ${suggestionId} (accepted: ${accepted})`);
    this.emit('suggestion:feedback', { suggestionId, accepted, feedback });
  }

  /**
   * Get suggestion statistics
   */
  getSuggestionStats(): any {
    return {
      totalSuggestions: Array.from(this.suggestionCache.values()).reduce((sum, suggestions) => sum + suggestions.length, 0),
      cachedContexts: this.suggestionCache.size,
      contextHistorySize: this.contextHistory.size,
      learningStats: this._learningService.getLearningStats()
    };
  }

  /**
   * Analyze code quality and return detailed scores
   */
  analyzeCodeQuality(code: string): { overall: number; security: number; performance: number; maintainability: number; readability: number } {
    try {
      if (!code || typeof code !== 'string') {
        return { overall: 0, security: 0, performance: 0, maintainability: 0, readability: 0 };
      }

      let securityScore = 1.0;
      let performanceScore = 1.0;
      let maintainabilityScore = 1.0;
      let readabilityScore = 1.0;
      const issues: string[] = [];

      // Security analysis
      if (code && code.includes('eval(') || code.includes('innerHTML') || code.includes('document.write')) {
        securityScore -= 0.5;
        issues.push('Security vulnerability detected');
      }
      if (code && code.includes('password') && !code.includes('hash') && !code.includes('encrypt')) {
        securityScore -= 0.2;
        issues.push('Potential password handling issue');
      }

      // Performance analysis
      if (code && code.includes('for (let i = 0; i <') || 
          code.includes('while (true)') ||
          code.includes('setInterval') ||
          code.includes('setTimeout')) {
        performanceScore -= 0.3;
        issues.push('Potential performance issue');
      }
      // Detect nested loops (O(n¬≤) complexity)
      const forLoopCount = (code.match(/for\s*\(/g) || []).length;
      if (forLoopCount > 1) {
        performanceScore -= 0.4;
        issues.push('Nested loops detected (O(n¬≤) complexity)');
      }
      // Prefer async/await over .then() chains
      if (code && code.includes('.then(') && !code.includes('async') && !code.includes('await')) {
        performanceScore -= 0.2;
        issues.push('Using .then() chains instead of async/await');
      }
      // Prefer functional methods over imperative loops
      if (code && code.includes('for (') && !code.includes('.map(') && !code.includes('.filter(') && !code.includes('.reduce(')) {
        performanceScore -= 0.1;
        issues.push('Consider using functional methods instead of imperative loops');
      }

      // Maintainability analysis
      const lines = code.split('\n').length;
      if (lines > 100) {
        maintainabilityScore -= 0.2;
        issues.push('High complexity (long file)');
      }
      const hasTryCatch = code.includes('try {') && code.includes('} catch');
      const hasAsyncAwait = code.includes('async') && code.includes('await');
      if (hasAsyncAwait && !hasTryCatch) {
        maintainabilityScore -= 0.3;
        issues.push('Missing error handling for async operations');
      }
      // Check for missing type annotations in function parameters
      if (code && code.includes('function ') && !code.includes(': string') && !code.includes(': number') && !code.includes(': boolean')) {
        maintainabilityScore -= 0.2;
        issues.push('Missing type annotations');
      }
      // Check for proper error handling in async operations
      if (code && code.includes('fetch(') && !hasTryCatch) {
        maintainabilityScore -= 0.3;
        issues.push('Missing error handling for fetch operations');
      }

      // Readability analysis
      const hasComments = code.includes('//') || code.includes('/*') || code.includes('*');
      if (!hasComments && lines > 20) {
        readabilityScore -= 0.2;
        issues.push('Missing documentation');
      }
      const hasBadNaming = code.includes('var ') || code.includes('function ') || 
                          /[a-z][A-Z]/.test(code) || code.includes('_');
      if (hasBadNaming) {
        readabilityScore -= 0.1;
        issues.push('Naming convention issues');
      }
      if (code && code.includes('any') && !code.includes('// @ts-ignore')) {
        readabilityScore -= 0.1;
        issues.push('Type safety issue (use of any)');
      }

      // Ensure scores are between 0 and 1
      securityScore = Math.max(0, Math.min(1, securityScore));
      performanceScore = Math.max(0, Math.min(1, performanceScore));
      maintainabilityScore = Math.max(0, Math.min(1, maintainabilityScore));
      readabilityScore = Math.max(0, Math.min(1, readabilityScore));

      // Calculate overall score
      const overallScore = (securityScore + performanceScore + maintainabilityScore + readabilityScore) / 4;
      
      this._logger.info(`üîç Code quality analysis: overall=${overallScore.toFixed(3)}, security=${securityScore.toFixed(3)}, performance=${performanceScore.toFixed(3)} (${issues.length} issues)`);
      this.emit('code-quality:analyzed', { overall: overallScore, security: securityScore, performance: performanceScore, maintainability: maintainabilityScore, readability: readabilityScore, issues });
      
      return {
        overall: overallScore,
        security: securityScore,
        performance: performanceScore,
        maintainability: maintainabilityScore,
        readability: readabilityScore
      };
    } catch (error) {
      this._logger.error('Failed to analyze code quality:', error);
      return { overall: 0, security: 0, performance: 0, maintainability: 0, readability: 0 };
    }
  }

  /**
   * Clear suggestion cache
   */
  clearSuggestionCache(): void {
    this.suggestionCache.clear();
    this._logger.info('üóëÔ∏è Suggestion cache cleared');
    this.emit('suggestion-cache:cleared');
  }

  /**
   * Export suggestion data
   */
  async exportSuggestionData(exportPath: string): Promise<void> {
    try {
      const suggestionData = {
        cache: Array.from(this.suggestionCache.entries()),
        contextHistory: Array.from(this.contextHistory.entries()),
        stats: this.getSuggestionStats(),
        exportedAt: new Date().toISOString()
      };
      
      await fs.writeJson(exportPath, suggestionData, { spaces: 2 });
      this._logger.info(`üì§ Suggestion data exported to: ${exportPath}`);
      this.emit('suggestion-data:exported', { exportPath });
    } catch (error) {
      this._logger.error('Failed to export suggestion data:', error);
      throw error;
    }
  }

  /**
   * Get current context information
   */
  getContext(): any {
    return {
      files: Array.from(this.contextHistory.keys()),
      relationships: this.buildFileRelationships(),
      patterns: this.getActivePatterns(),
      preferences: this._memoryService.getMemory().codePatterns.userPreferences
    };
  }

  /**
   * Build file relationships
   */
  private buildFileRelationships(): any[] {
    const relationships = [];
    const files = Array.from(this.contextHistory.keys());
    
    for (let i = 0; i < files.length; i++) {
      for (let j = i + 1; j < files.length; j++) {
        relationships.push({
          source: files[i],
          target: files[j],
          type: 'dependency',
          strength: Math.random() * 0.8 + 0.2 // Simulate relationship strength
        });
      }
    }
    
    return relationships;
  }

  /**
   * Get active patterns
   */
  private getActivePatterns(): any[] {
    const memory = this._memoryService.getMemory();
    return memory.codePatterns.frequentlyUsed.slice(0, 5); // Return top 5 patterns
  }
}

/**
 * Cursor Learning Service
 * Advanced learning algorithms for pattern recognition and suggestion improvement
 * Following BMAD principles: Break, Map, Automate, Document
 */

import { EventEmitter } from 'node:events';
import { Logger } from '../../core/logger';
import { Config } from '../../core/config';
import { MemoryService } from './memory-service';

export interface LearningPattern {
  id: string;
  type: 'code' | 'architecture' | 'naming' | 'workflow';
  pattern: string;
  context: string;
  successRate: number;
  frequency: number;
  lastUsed: string;
  confidence: number;
}

export interface Suggestion {
  id: string;
  type: 'code-generation' | 'refactoring' | 'optimization' | 'architecture';
  content: string;
  confidence: number;
  reasoning: string;
  alternatives: string[];
  context: string;
}

export interface LearningContext {
  currentFile: string;
  projectStructure: string[];
  recentChanges: string[];
  userPreferences: any;
  codingPatterns: string[];
}

export class LearningService extends EventEmitter {
  private _logger: Logger;
  private memoryService!: MemoryService;
  private learningPatterns: Map<string, LearningPattern>;
  private suggestionHistory: Map<string, any[]>;

  constructor(_config: Config, memoryService: MemoryService) {
    super();
    this.memoryService = memoryService;
    this._logger = new Logger('LearningService');
    this.learningPatterns = new Map();
    this.suggestionHistory = new Map();
  }

  /**
   * Initialize learning service
   */
  async initialize(): Promise<void> {
    this._logger.info('üß† Initializing Learning Service...');
    
    try {
      await this.loadLearningPatterns();
      await this.analyzeProjectHistory();
      await this.buildPatternDatabase();
      
      this._logger.info('‚úÖ Learning Service initialized successfully');
      this.emit('learning:initialized');
    } catch (error) {
      this._logger.error('Failed to initialize learning service:', error);
      throw error;
    }
  }

  /**
   * Load learning patterns from memory
   */
  private async loadLearningPatterns(): Promise<void> {
    const memory = this.memoryService as any;
    const patterns = memory.memory?.codePatterns?.frequentlyUsed || [];
    
    for (const pattern of patterns) {
      this.learningPatterns.set(pattern.id, {
        id: pattern.id,
        type: 'code',
        pattern: pattern.pattern,
        context: pattern.example,
        successRate: pattern.successRate,
        frequency: pattern.frequency,
        lastUsed: pattern.lastUsed,
        confidence: pattern.successRate * 0.8 + (pattern.frequency / 100) * 0.2
      });
    }

    this._logger.info(`üìö Loaded ${patterns.length} learning patterns`);
  }

  /**
   * Analyze project history for patterns
   */
  private async analyzeProjectHistory(): Promise<void> {
    const memory = this.memoryService as any;
    const sessions = memory.memory?.projectHistory?.sessions || [];
    
    for (const session of sessions) {
      // Analyze patterns used in session
      for (const patternId of session.patternsUsed) {
        const pattern = this.learningPatterns.get(patternId);
        if (pattern) {
          pattern.frequency++;
          pattern.lastUsed = session.date;
        }
      }
    }

    this._logger.info(`üìä Analyzed ${sessions.length} project sessions`);
  }

  /**
   * Build pattern database
   */
  private async buildPatternDatabase(): Promise<void> {
    // Build pattern relationships
    const patterns = Array.from(this.learningPatterns.values());
    
    for (const pattern of patterns) {
      // Find similar patterns
      const similarPatterns = patterns.filter(p => 
        p.id !== pattern.id && 
        this.calculatePatternSimilarity(pattern, p) > 0.7
      );
      
      // Update confidence based on similar patterns
      if (similarPatterns.length > 0) {
        const avgSuccessRate = similarPatterns.reduce((sum, p) => sum + p.successRate, 0) / similarPatterns.length;
        pattern.confidence = (pattern.confidence + avgSuccessRate) / 2;
      }
    }

    this._logger.info(`üîó Built pattern database with ${patterns.length} patterns`);
  }

  /**
   * Calculate pattern similarity
   */
  private calculatePatternSimilarity(pattern1: LearningPattern, pattern2: LearningPattern): number {
    // Simple similarity calculation based on pattern content and context
    const contentSimilarity = this.calculateStringSimilarity(pattern1.pattern, pattern2.pattern);
    const contextSimilarity = this.calculateStringSimilarity(pattern1.context, pattern2.context);
    
    return (contentSimilarity + contextSimilarity) / 2;
  }

  /**
   * Calculate string similarity using Levenshtein distance
   */
  private calculateStringSimilarity(str1: string, str2: string): number {
    const maxLength = Math.max(str1.length, str2.length);
    if (maxLength === 0) return 1;
    
    const distance = this.levenshteinDistance(str1, str2);
    return 1 - (distance / maxLength);
  }

  /**
   * Calculate Levenshtein distance
   */
  private levenshteinDistance(str1: string, str2: string): number {
    const matrix: number[][] = [];
    
    for (let i = 0; i <= str2.length; i++) {
      matrix[i] = [i];
    }
    
    for (let j = 0; j <= str1.length; j++) {
      matrix[0]![j] = j;
    }
    
    for (let i = 1; i <= str2.length; i++) {
      for (let j = 1; j <= str1.length; j++) {
        if (str2.charAt(i - 1) === str1.charAt(j - 1)) {
          matrix[i]![j] = matrix[i - 1]![j - 1]!;
        } else {
          matrix[i]![j] = Math.min(
            matrix[i - 1]![j - 1]! + 1,
            matrix[i]![j - 1]! + 1,
            matrix[i - 1]![j]! + 1
          );
        }
      }
    }
    
    return matrix[str2.length]![str1.length]!;
  }

  /**
   * Generate contextual suggestions
   */
  generateContextualSuggestions(context: LearningContext): Suggestion[] {
    const suggestions: Suggestion[] = [];
    
    // Generate code generation suggestions
    const codeSuggestions = this.generateCodeSuggestions(context);
    suggestions.push(...codeSuggestions);
    
    // Generate refactoring suggestions
    const refactoringSuggestions = this.generateRefactoringSuggestions(context);
    suggestions.push(...refactoringSuggestions);
    
    // Generate optimization suggestions
    const optimizationSuggestions = this.generateOptimizationSuggestions(context);
    suggestions.push(...optimizationSuggestions);
    
    // Sort by confidence
    suggestions.sort((a, b) => b.confidence - a.confidence);
    
    this._logger.info(`üí° Generated ${suggestions.length} contextual suggestions`);
    return suggestions.slice(0, 5); // Return top 5 suggestions
  }

  /**
   * Generate code generation suggestions
   */
  private generateCodeSuggestions(context: LearningContext): Suggestion[] {
    const suggestions: Suggestion[] = [];
    const relevantPatterns = this.getRelevantPatterns(context, 'code');
    
    for (const pattern of relevantPatterns) {
      if (pattern.confidence > 0.8) {
        suggestions.push({
          id: `code-${pattern.id}`,
          type: 'code-generation',
          content: pattern.pattern,
          confidence: pattern.confidence,
          reasoning: `Based on successful pattern usage (${pattern.frequency} times, ${(pattern.successRate * 100).toFixed(1)}% success rate)`,
          alternatives: this.getAlternativePatterns(pattern),
          context: context.currentFile
        });
      }
    }
    
    return suggestions;
  }

  /**
   * Generate refactoring suggestions
   */
  private generateRefactoringSuggestions(context: LearningContext): Suggestion[] {
    const suggestions: Suggestion[] = [];
    
    // Analyze current code for refactoring opportunities
    const refactoringPatterns = this.getRelevantPatterns(context, 'code').filter(p => p.type === 'code');
    
    for (const pattern of refactoringPatterns) {
      if (pattern.successRate > 0.9) {
        suggestions.push({
          id: `refactor-${pattern.id}`,
          type: 'refactoring',
          content: `Refactor to use ${pattern.pattern} pattern`,
          confidence: pattern.successRate,
          reasoning: `This pattern has been successful ${(pattern.successRate * 100).toFixed(1)}% of the time`,
          alternatives: this.getAlternativePatterns(pattern),
          context: context.currentFile
        });
      }
    }
    
    return suggestions;
  }

  /**
   * Generate optimization suggestions
   */
  private generateOptimizationSuggestions(context: LearningContext): Suggestion[] {
    const suggestions: Suggestion[] = [];
    
    // Analyze for optimization opportunities
    const optimizationPatterns = this.getRelevantPatterns(context, 'code').filter(p => p.frequency > 10);
    
    for (const pattern of optimizationPatterns) {
      if (pattern.confidence > 0.85) {
        suggestions.push({
          id: `optimize-${pattern.id}`,
          type: 'optimization',
          content: `Optimize using ${pattern.pattern} pattern`,
          confidence: pattern.confidence,
          reasoning: `This pattern has been used ${pattern.frequency} times with high success rate`,
          alternatives: this.getAlternativePatterns(pattern),
          context: context.currentFile
        });
      }
    }
    
    return suggestions;
  }

  /**
   * Get relevant patterns for context
   */
  private getRelevantPatterns(_context: LearningContext, type: string): LearningPattern[] {
    const patterns = Array.from(this.learningPatterns.values())
      .filter(p => p.type === type)
      .sort((a, b) => b.confidence - a.confidence);
    
    return patterns.slice(0, 10); // Return top 10 relevant patterns
  }

  /**
   * Get alternative patterns
   */
  private getAlternativePatterns(pattern: LearningPattern): string[] {
    const alternatives = Array.from(this.learningPatterns.values())
      .filter(p => p.id !== pattern.id && p.type === pattern.type)
      .sort((a, b) => b.confidence - a.confidence)
      .slice(0, 3)
      .map(p => p.pattern);
    
    return alternatives;
  }

  /**
   * Record a new learning pattern
   */
  recordPattern(pattern: LearningPattern): void {
    this.learningPatterns.set(pattern.id, pattern);
    this._logger.info(`üìö Recorded pattern: ${pattern.id}`);
  }

  /**
   * Learn from user feedback
   */
  learnFromFeedback(suggestionId: string, accepted: boolean, feedback?: string): void {
    const patternId = suggestionId.replace(/^(code-|refactor-|optimize-)/, '');
    const pattern = this.learningPatterns.get(patternId);
    
    if (pattern) {
      if (accepted) {
        pattern.successRate = Math.min(1, pattern.successRate + 0.1);
        pattern.confidence = Math.min(1, pattern.confidence + 0.05);
      } else {
        pattern.successRate = Math.max(0, pattern.successRate - 0.1);
        pattern.confidence = Math.max(0, pattern.confidence - 0.05);
      }
      
      pattern.lastUsed = new Date().toISOString();
    }
    
    // Record in suggestion history
    if (!this.suggestionHistory.has(suggestionId)) {
      this.suggestionHistory.set(suggestionId, []);
    }
    
    this.suggestionHistory.get(suggestionId)!.push({
      accepted,
      feedback,
      timestamp: new Date().toISOString()
    });
    
    this._logger.info(`üß† Learned from feedback: ${suggestionId} (accepted: ${accepted})`);
    this.emit('learning:feedback', { suggestionId, accepted, feedback });
  }

  /**
   * Get learning statistics
   */
  getLearningStats(): any {
    const patterns = Array.from(this.learningPatterns.values());
    
    return {
      totalPatterns: patterns.length,
      averageConfidence: patterns.reduce((sum, p) => sum + p.confidence, 0) / patterns.length,
      averageSuccessRate: patterns.reduce((sum, p) => sum + p.successRate, 0) / patterns.length,
      totalSuggestions: Array.from(this.suggestionHistory.values()).reduce((sum, history) => sum + history.length, 0),
      learningProgress: this.calculateLearningProgress()
    };
  }

  /**
   * Calculate learning progress
   */
  private calculateLearningProgress(): number {
    const patterns = Array.from(this.learningPatterns.values());
    if (patterns.length === 0) return 0;
    
    const avgConfidence = patterns.reduce((sum, p) => sum + p.confidence, 0) / patterns.length;
    const avgSuccessRate = patterns.reduce((sum, p) => sum + p.successRate, 0) / patterns.length;
    
    return (avgConfidence + avgSuccessRate) / 2;
  }

  /**
   * Update pattern success rate based on usage feedback
   */
  updatePatternSuccess(pattern: LearningPattern, success: boolean): void {
    try {
      const currentPattern = this.learningPatterns.get(pattern.id);
      if (!currentPattern) {
        this._logger.warn(`Pattern not found: ${pattern.id}`);
        return;
      }

      // Update success rate using exponential moving average
      const alpha = 0.1; // Learning rate
      const newSuccessRate = success 
        ? currentPattern.successRate + alpha * (1 - currentPattern.successRate)
        : currentPattern.successRate + alpha * (0 - currentPattern.successRate);

      currentPattern.successRate = Math.max(0, Math.min(1, newSuccessRate));
      currentPattern.lastUsed = new Date().toISOString();

      this.learningPatterns.set(pattern.id, currentPattern);
      this._logger.info(`üìà Updated pattern success rate: ${pattern.id} -> ${currentPattern.successRate.toFixed(3)}`);
      this.emit('learning:patternUpdated', { patternId: pattern.id, successRate: currentPattern.successRate });
    } catch (error) {
      this._logger.error('Failed to update pattern success:', error);
      throw error;
    }
  }

  /**
   * Update pattern frequency when used
   */
  updatePatternFrequency(pattern: LearningPattern): void {
    try {
      // If pattern is not in our map, add it first
      if (!this.learningPatterns.has(pattern.id)) {
        this.learningPatterns.set(pattern.id, { ...pattern });
      }

      const currentPattern = this.learningPatterns.get(pattern.id)!;
      currentPattern.frequency += 1;
      currentPattern.lastUsed = new Date().toISOString();

      // Update confidence based on frequency and success rate
      const frequencyWeight = Math.min(currentPattern.frequency / 10, 1); // Cap at 10 uses
      currentPattern.confidence = (currentPattern.successRate * 0.7) + (frequencyWeight * 0.3);

      this.learningPatterns.set(pattern.id, currentPattern);
      
      // Update the original pattern object passed to the method
      pattern.frequency = currentPattern.frequency;
      pattern.confidence = currentPattern.confidence;
      pattern.lastUsed = currentPattern.lastUsed;
      
      this._logger.info(`üìä Updated pattern frequency: ${pattern.id} -> ${currentPattern.frequency}`);
      this.emit('learning:patternUsed', { patternId: pattern.id, frequency: currentPattern.frequency });
    } catch (error) {
      this._logger.error('Failed to update pattern frequency:', error);
      throw error;
    }
  }


  /**
   * Export learning data
   */
  async exportLearningData(exportPath: string): Promise<void> {
    try {
      const learningData = {
        patterns: Array.from(this.learningPatterns.entries()),
        suggestionHistory: Array.from(this.suggestionHistory.entries()),
        stats: this.getLearningStats(),
        exportedAt: new Date().toISOString()
      };
      
      await fs.writeJson(exportPath, learningData, { spaces: 2 });
      this._logger.info(`üì§ Learning data exported to: ${exportPath}`);
      this.emit('learning:exported', { exportPath });
    } catch (error) {
      this._logger.error('Failed to export learning data:', error);
      throw error;
    }
  }
}

import fs from 'fs-extra';

/**
 * Cursor Memory Service
 * Manages memory of previous sessions, code patterns, and learning
 * Following BMAD principles: Break, Map, Automate, Document
 */

import { EventEmitter } from 'node:events';
import { Logger } from '../../core/logger';
import { Config } from '../../core/config';
import fs from 'fs-extra';
import path from 'path';

export interface CodePattern {
  id: string;
  pattern: string;
  frequency: number;
  successRate: number;
  lastUsed: string;
  example: string;
}

export interface ArchitectureDecision {
  id: string;
  decision: string;
  rationale: string;
  date: string;
  impact: 'low' | 'medium' | 'high';
}

export interface NamingConvention {
  [key: string]: string;
}

export interface ProjectSession {
  id: string;
  date: string;
  duration: number;
  filesModified: number;
  patternsUsed: string[];
  decisionsMade: string[];
  successRate: number;
}

export interface LearningFeedback {
  suggestionId: string;
  type: string;
  accepted: boolean;
  userFeedback: 'positive' | 'negative' | 'neutral';
  reason?: string;
  date: string;
}

export interface CursorMemory {
  version: string;
  lastUpdated: string;
  codePatterns: {
    frequentlyUsed: CodePattern[];
    successfulPatterns: CodePattern[];
    userPreferences: {
      preferredImports: string[];
      preferredNaming: NamingConvention;
      preferredPatterns: string[];
    };
  };
  architecture: {
    decisions: ArchitectureDecision[];
    patterns: any[];
    preferences: any;
  };
  namingConventions: {
    variables: NamingConvention;
    functions: NamingConvention;
    components: NamingConvention;
    files: NamingConvention;
    constants: NamingConvention;
    [key: string]: NamingConvention; // Add index signature for dynamic access
  };
  projectHistory: {
    sessions: ProjectSession[];
    decisions: any[];
    evolution: any[];
  };
  learning: {
    successfulSuggestions: LearningFeedback[];
    rejectedSuggestions: LearningFeedback[];
    userFeedback: any;
  };
  feedback?: any[]; // Add missing feedback property
  preferences?: any; // Add missing preferences property
}

export class MemoryService extends EventEmitter {
  private _logger: Logger;
  private memory!: CursorMemory;
  private memoryFilePath: string;

  constructor(_config: Config) {
    super();
    this._logger = new Logger('MemoryService');
    this.memoryFilePath = path.join(process.cwd(), 'cursor-memory.json');
    this.initializeMemory();
  }

  /**
   * Initialize memory system
   */
  private async initializeMemory(): Promise<void> {
    try {
      await this.loadMemory();
      this._logger.info('üß† Memory system initialized');
      this.emit('memory:initialized', { memorySize: this.getMemorySize() });
    } catch (error) {
      this._logger.error('Failed to initialize memory system:', error);
      this.createDefaultMemory();
    }
  }


  /**
   * Load memory from file
   */
  private async loadMemory(): Promise<void> {
    try {
      if (await fs.pathExists(this.memoryFilePath)) {
        this.memory = await fs.readJson(this.memoryFilePath);
        this._logger.info('üìñ Memory loaded from file');
      } else {
        this.createDefaultMemory();
      }
    } catch (error) {
      this._logger.error('Failed to load memory:', error);
      this.createDefaultMemory();
    }
  }

  /**
   * Create default memory structure
   */
  private createDefaultMemory(): void {
    this.memory = {
      version: '1.0.0',
      lastUpdated: new Date().toISOString(),
      codePatterns: {
        frequentlyUsed: [],
        successfulPatterns: [],
        userPreferences: {
          preferredImports: [],
          preferredNaming: {},
          preferredPatterns: []
        }
      },
      architecture: {
        decisions: [],
        patterns: [],
        preferences: {}
      },
      namingConventions: {
        variables: {},
        functions: {},
        components: {},
        files: {},
        constants: {}
      },
      projectHistory: {
        sessions: [],
        decisions: [],
        evolution: []
      },
      learning: {
        successfulSuggestions: [],
        rejectedSuggestions: [],
        userFeedback: {}
      }
    };
  }

  /**
   * Save memory to file
   */
  async saveMemory(): Promise<void> {
    try {
      this.memory.lastUpdated = new Date().toISOString();
      await fs.writeJson(this.memoryFilePath, this.memory, { spaces: 2 });
      this._logger.info('üíæ Memory saved to file');
      this.emit('memory:saved', { memorySize: this.getMemorySize() });
    } catch (error) {
      this._logger.error('Failed to save memory:', error);
      throw error;
    }
  }

  /**
   * Record code pattern usage
   */
  recordCodePattern(pattern: CodePattern): void {
    this.ensureMemoryInitialized();
    const existingPattern = this.memory.codePatterns.frequentlyUsed.find(p => p.id === pattern.id);
    
    if (existingPattern) {
      existingPattern.frequency++;
      existingPattern.lastUsed = new Date().toISOString();
      existingPattern.successRate = (existingPattern.successRate + pattern.successRate) / 2;
    } else {
      this.memory.codePatterns.frequentlyUsed.push(pattern);
    }

    this._logger.info(`üìù Recorded code pattern: ${pattern.id}`);
    this.emit('pattern:recorded', { patternId: pattern.id, frequency: pattern.frequency });
  }

  /**
   * Record architecture decision
   */
  recordArchitectureDecision(decision: ArchitectureDecision): void {
    this.memory.architecture.decisions.push(decision);
    this._logger.info(`üèóÔ∏è Recorded architecture decision: ${decision.id}`);
    this.emit('decision:recorded', { decisionId: decision.id, impact: decision.impact });
  }

  /**
   * Record naming convention
   */
  recordNamingConvention(type: string, name: string, convention: string): void {
    if (!this.memory.namingConventions[type]) {
      this.memory.namingConventions[type] = {};
    }
    
    this.memory.namingConventions[type][name] = convention;
    this._logger.info(`üìù Recorded naming convention: ${type}.${name} = ${convention}`);
    this.emit('convention:recorded', { type, name, convention });
  }

  /**
   * Record project session
   */
  recordProjectSession(session: ProjectSession): void {
    this.memory.projectHistory.sessions.push(session);
    this._logger.info(`üìä Recorded project session: ${session.id}`);
    this.emit('session:recorded', { sessionId: session.id, duration: session.duration });
  }

  /**
   * Record learning feedback
   */
  recordLearningFeedback(feedback: LearningFeedback): void {
    if (feedback.accepted) {
      this.memory.learning.successfulSuggestions.push(feedback);
    } else {
      this.memory.learning.rejectedSuggestions.push(feedback);
    }

    this._logger.info(`üìö Recorded learning feedback: ${feedback.suggestionId}`);
    this.emit('feedback:recorded', { suggestionId: feedback.suggestionId, accepted: feedback.accepted });
  }

  /**
   * Get contextual suggestions based on history
   */
  getContextualSuggestions(_context: string): any[] {
    const suggestions = [];
    
    // Get relevant patterns
    const relevantPatterns = this.memory.codePatterns.frequentlyUsed
      .filter(pattern => pattern.successRate > 0.8)
      .sort((a, b) => b.frequency - a.frequency)
      .slice(0, 5);

    // Get relevant decisions
    const relevantDecisions = this.memory.architecture.decisions
      .filter(decision => decision.impact === 'high')
      .slice(0, 3);

    // Get relevant naming conventions
    const relevantConventions = this.memory.namingConventions;

    suggestions.push({
      type: 'patterns',
      data: relevantPatterns,
      confidence: 0.9
    });

    suggestions.push({
      type: 'decisions',
      data: relevantDecisions,
      confidence: 0.85
    });

    suggestions.push({
      type: 'conventions',
      data: relevantConventions,
      confidence: 0.8
    });

    this._logger.info(`üí° Generated ${suggestions.length} contextual suggestions`);
    return suggestions;
  }

  /**
   * Learn from user feedback
   */
  learnFromFeedback(feedback: LearningFeedback): void {
    this.recordLearningFeedback(feedback);
    
    // Update pattern success rates
    if (feedback.accepted) {
      this.updatePatternSuccessRate(feedback.suggestionId, 0.1);
    } else {
      this.updatePatternSuccessRate(feedback.suggestionId, -0.1);
    }

    this._logger.info(`üß† Learned from feedback: ${feedback.suggestionId}`);
    this.emit('learning:updated', { suggestionId: feedback.suggestionId, accepted: feedback.accepted });
  }

  /**
   * Update pattern success rate
   */
  private updatePatternSuccessRate(patternId: string, adjustment: number): void {
    const pattern = this.memory.codePatterns.frequentlyUsed.find(p => p.id === patternId);
    if (pattern) {
      pattern.successRate = Math.max(0, Math.min(1, pattern.successRate + adjustment));
    }
  }

  /**
   * Get memory statistics
   */
  getMemoryStats(): any {
    return {
      totalPatterns: this.memory.codePatterns.frequentlyUsed.length,
      totalDecisions: this.memory.architecture.decisions.length,
      totalSessions: this.memory.projectHistory.sessions.length,
      totalSuggestions: this.memory.learning.successfulSuggestions.length + this.memory.learning.rejectedSuggestions.length,
      successRate: this.calculateOverallSuccessRate(),
      lastUpdated: this.memory.lastUpdated
    };
  }

  /**
   * Calculate overall success rate
   */
  private calculateOverallSuccessRate(): number {
    const totalSuggestions = this.memory.learning.successfulSuggestions.length + this.memory.learning.rejectedSuggestions.length;
    if (totalSuggestions === 0) return 0;
    return this.memory.learning.successfulSuggestions.length / totalSuggestions;
  }

  /**
   * Get memory size
   */
  private getMemorySize(): number {
    return JSON.stringify(this.memory).length;
  }

  /**
   * Clear memory
   */
  async clearMemory(): Promise<void> {
    this.createDefaultMemory();
    await this.saveMemory();
    this._logger.info('üóëÔ∏è Memory cleared');
    this.emit('memory:cleared');
  }

  /**
   * Export memory
   */
  async exportMemory(exportPath: string): Promise<void> {
    try {
      await fs.writeJson(exportPath, this.memory, { spaces: 2 });
      this._logger.info(`üì§ Memory exported to: ${exportPath}`);
      this.emit('memory:exported', { exportPath });
    } catch (error) {
      this._logger.error('Failed to export memory:', error);
      throw error;
    }
  }

  /**
   * Import memory
   */
  async importMemory(importPath: string): Promise<void> {
    try {
      const importedMemory = await fs.readJson(importPath);
      this.memory = importedMemory;
      await this.saveMemory();
      this._logger.info(`üì• Memory imported from: ${importPath}`);
      this.emit('memory:imported', { importPath });
    } catch (error) {
      this._logger.error('Failed to import memory:', error);
      throw error;
    }
  }

  /**
   * Store user feedback for learning
   */
  async storeFeedback(feedback: any): Promise<void> {
    try {
      if (!this.memory.feedback) {
        this.memory.feedback = [];
      }
      
      const feedbackEntry = {
        id: `feedback_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`,
        ...feedback,
        timestamp: new Date().toISOString()
      };
      
      this.memory.feedback.push(feedbackEntry);
      await this.saveMemory();
      
      this._logger.info('üìù Feedback stored successfully');
      this.emit('feedback:stored', feedbackEntry);
    } catch (error) {
      this._logger.error('Failed to store feedback:', error);
      throw error;
    }
  }

  /**
   * Store user preferences
   */
  async storePreferences(preferences: any): Promise<void> {
    try {
      this.memory.preferences = {
        ...this.memory.preferences,
        ...preferences,
        lastUpdated: new Date().toISOString()
      };
      
      await this.saveMemory();
      
      this._logger.info('‚öôÔ∏è Preferences stored successfully');
      this.emit('preferences:stored', preferences);
    } catch (error) {
      this._logger.error('Failed to store preferences:', error);
      throw error;
    }
  }

  /**
   * Get memory object
   */
  getMemory(): CursorMemory {
    return this.memory;
  }

  /**
   * Initialize memory if not already done
   */
  private ensureMemoryInitialized(): void {
    if (!this.memory) {
      this.createDefaultMemory();
    }
  }
}

