import fs from 'fs-extra';
import path from 'path';

/**
 * Conflict Resolution Service
 * Intelligent conflict resolution for Monster Mode
 * Following BMAD principles: Break, Map, Automate, Document
 */

import { EventEmitter } from 'node:events';
import { Logger } from '../../core/logger';
import { Config } from '../../core/config';

export interface Conflict {
  id: string;
  type: 'resource' | 'priority' | 'dependency' | 'quality' | 'deadline' | 'architecture';
  severity: 'low' | 'medium' | 'high' | 'critical';
  status: 'detected' | 'analyzing' | 'resolving' | 'resolved' | 'escalated' | 'failed';
  agents: string[];
  description: string;
  context: any;
  resolution: ConflictResolution;
  timestamp: string;
  resolvedAt?: string;
  resolvedBy?: string;
}

export interface ConflictResolution {
  id: string;
  strategy: 'automatic' | 'manual' | 'escalated' | 'consensus' | 'arbitration';
  approach: 'priority-based' | 'resource-based' | 'time-based' | 'quality-based' | 'hybrid';
  solution: string;
  actions: ResolutionAction[];
  success: boolean;
  reasoning: string;
  alternatives: string[];
  impact: 'low' | 'medium' | 'high';
  effort: 'low' | 'medium' | 'high';
}

export interface ResolutionAction {
  id: string;
  type: 'reassign' | 'reschedule' | 'modify' | 'cancel' | 'escalate' | 'negotiate';
  target: string;
  parameters: any;
  status: 'pending' | 'executing' | 'completed' | 'failed';
  result?: any;
  error?: string;
}

export interface ConflictConfig {
  enabled: boolean;
  detection: {
    enabled: boolean;
    interval: number;
    thresholds: {
      resource: number;
      priority: number;
      dependency: number;
      quality: number;
    };
  };
  resolution: {
    enabled: boolean;
    strategies: {
      automatic: boolean;
      manual: boolean;
      escalated: boolean;
      consensus: boolean;
      arbitration: boolean;
    };
    timeouts: {
      automatic: number;
      manual: number;
      escalated: number;
    };
  };
  learning: {
    enabled: boolean;
    adaptationRate: number;
    historyWeight: number;
  };
}

export interface ConflictPattern {
  id: string;
  type: string;
  pattern: any;
  frequency: number;
  successRate: number;
  resolutionStrategy: string;
  lastSeen: string;
}

export class ConflictResolutionService extends EventEmitter {
  private _logger: Logger;
  // private _config!: Config; // Unused for now
  private conflictConfig: ConflictConfig;
  private conflicts: Map<string, Conflict>;
  private conflictHistory: Map<string, Conflict[]>;
  private conflictPatterns: Map<string, ConflictPattern>;
  private resolutionStrategies: Map<string, any>;

  constructor(_config: Config) {
    super();
    this._logger = new Logger('ConflictResolutionService');
    this.conflictConfig = this.getDefaultConflictConfig();
    this.conflicts = new Map();
    this.conflictHistory = new Map();
    this.conflictPatterns = new Map();
    this.resolutionStrategies = new Map();
    this.initializeResolutionStrategies();
  }

  /**
   * Initialize conflict resolution service
   */
  async initialize(): Promise<void> {
    this._logger.info('üîß Initializing Conflict Resolution Service...');
    
    try {
      await this.loadConflictConfig();
      await this.initializeResolutionStrategies();
      await this.loadConflictPatterns();
      
      this._logger.info('‚úÖ Conflict Resolution Service initialized successfully');
      this.emit('conflict-resolution:initialized');
    } catch (error) {
      this._logger.error('Failed to initialize conflict resolution service:', error);
      throw error;
    }
  }

  /**
   * Load conflict configuration
   */
  private async loadConflictConfig(): Promise<void> {
    try {
      const configPath = path.join(process.cwd(), 'conflict-resolution-config.json');
      if (await fs.pathExists(configPath)) {
        this.conflictConfig = await fs.readJson(configPath);
        this._logger.info('üìã Conflict resolution configuration loaded');
      } else {
        this.conflictConfig = this.getDefaultConflictConfig();
        await this.saveConflictConfig();
        this._logger.info('üìã Using default conflict resolution configuration');
      }
    } catch (error) {
      this._logger.warn('Failed to load conflict resolution configuration, using defaults');
      this.conflictConfig = this.getDefaultConflictConfig();
    }
  }

  /**
   * Get default conflict configuration
   */
  private getDefaultConflictConfig(): ConflictConfig {
    return {
      enabled: true,
      detection: {
        enabled: true,
        interval: 30000, // 30 seconds
        thresholds: {
          resource: 0.8,
          priority: 0.7,
          dependency: 0.6,
          quality: 0.5
        }
      },
      resolution: {
        enabled: true,
        strategies: {
          automatic: true,
          manual: true,
          escalated: true,
          consensus: true,
          arbitration: true
        },
        timeouts: {
          automatic: 60000, // 1 minute
          manual: 300000, // 5 minutes
          escalated: 900000 // 15 minutes
        }
      },
      learning: {
        enabled: true,
        adaptationRate: 0.1,
        historyWeight: 0.3
      }
    };
  }

  /**
   * Save conflict configuration
   */
  private async saveConflictConfig(): Promise<void> {
    try {
      const configPath = path.join(process.cwd(), 'conflict-resolution-config.json');
      await fs.writeJson(configPath, this.conflictConfig, { spaces: 2 });
      this._logger.info('üíæ Conflict resolution configuration saved');
    } catch (error) {
      this._logger.error('Failed to save conflict resolution configuration:', error);
    }
  }

  /**
   * Initialize resolution strategies
   */
  private initializeResolutionStrategies(): void {
    // Priority-based strategy
    this.resolutionStrategies.set('priority-based', {
      id: 'priority-based',
      name: 'Priority-Based Resolution',
      description: 'Resolve conflicts based on task priorities',
      algorithm: 'priority-based',
      parameters: {
        priorityWeight: 1.0,
        fairnessWeight: 0.3
      },
      successRate: 0.85
    });

    // Resource-based strategy
    this.resolutionStrategies.set('resource-based', {
      id: 'resource-based',
      name: 'Resource-Based Resolution',
      description: 'Resolve conflicts based on resource availability',
      algorithm: 'resource-based',
      parameters: {
        resourceWeight: 1.0,
        loadBalanceWeight: 0.4
      },
      successRate: 0.80
    });

    // Time-based strategy
    this.resolutionStrategies.set('time-based', {
      id: 'time-based',
      name: 'Time-Based Resolution',
      description: 'Resolve conflicts based on time constraints',
      algorithm: 'time-based',
      parameters: {
        deadlineWeight: 1.0,
        urgencyWeight: 0.5
      },
      successRate: 0.75
    });

    // Quality-based strategy
    this.resolutionStrategies.set('quality-based', {
      id: 'quality-based',
      name: 'Quality-Based Resolution',
      description: 'Resolve conflicts based on quality requirements',
      algorithm: 'quality-based',
      parameters: {
        qualityWeight: 1.0,
        impactWeight: 0.6
      },
      successRate: 0.82
    });

    // Hybrid strategy
    this.resolutionStrategies.set('hybrid', {
      id: 'hybrid',
      name: 'Hybrid Resolution',
      description: 'Combine multiple resolution strategies',
      algorithm: 'hybrid',
      parameters: {
        priorityWeight: 0.3,
        resourceWeight: 0.25,
        timeWeight: 0.25,
        qualityWeight: 0.2
      },
      successRate: 0.88
    });
  }

  /**
   * Load conflict patterns
   */
  private async loadConflictPatterns(): Promise<void> {
    try {
      const patternsPath = path.join(process.cwd(), 'conflict-patterns.json');
      if (await fs.pathExists(patternsPath)) {
        const patterns = await fs.readJson(patternsPath);
        this.conflictPatterns = new Map(patterns);
        this._logger.info('üìö Conflict patterns loaded');
      }
    } catch (error) {
      this._logger.warn('Failed to load conflict patterns');
    }
  }

  /**
   * Detect conflicts
   */
  async detectConflicts(context: any): Promise<Conflict[]> {
    this._logger.info('üîç Detecting conflicts...');
    
    try {
      const conflicts: Conflict[] = [];

      // Detect resource conflicts
      const resourceConflicts = await this.detectResourceConflicts(context);
      conflicts.push(...resourceConflicts);

      // Detect priority conflicts
      const priorityConflicts = await this.detectPriorityConflicts(context);
      conflicts.push(...priorityConflicts);

      // Detect dependency conflicts
      const dependencyConflicts = await this.detectDependencyConflicts(context);
      conflicts.push(...dependencyConflicts);

      // Detect quality conflicts
      const qualityConflicts = await this.detectQualityConflicts(context);
      conflicts.push(...qualityConflicts);

      // Detect deadline conflicts
      const deadlineConflicts = await this.detectDeadlineConflicts(context);
      conflicts.push(...deadlineConflicts);

      // Detect architecture conflicts
      const architectureConflicts = await this.detectArchitectureConflicts(context);
      conflicts.push(...architectureConflicts);

      // Store conflicts
      for (const conflict of conflicts) {
        this.conflicts.set(conflict.id, conflict);
      }

      this._logger.info(`‚úÖ Detected ${conflicts.length} conflicts`);
      this.emit('conflicts:detected', { conflicts });
      
      return conflicts;
    } catch (error) {
      this._logger.error('Failed to detect conflicts:', error);
      throw error;
    }
  }

  /**
   * Detect resource conflicts
   */
  private async detectResourceConflicts(context: any): Promise<Conflict[]> {
    const conflicts: Conflict[] = [];

    // Check for resource competition
    const resourceUsage = new Map<string, string[]>();
    
    for (const [agentId, agentStatus] of context.agentStatuses || []) {
      if (agentStatus.status === 'busy') {
        const resources = this.getAgentResources(agentId);
        for (const resource of resources) {
          if (!resourceUsage.has(resource)) {
            resourceUsage.set(resource, []);
          }
          resourceUsage.get(resource)!.push(agentId);
        }
      }
    }

    // Find conflicts
    for (const [resource, agents] of resourceUsage) {
      if (agents.length > 1) {
        conflicts.push({
          id: this.generateConflictId(),
          type: 'resource',
          severity: 'medium',
          status: 'detected',
          agents,
          description: `Multiple agents competing for resource: ${resource}`,
          context: { resource, agents },
          resolution: this.createEmptyResolution(),
          timestamp: new Date().toISOString()
        });
      }
    }

    return conflicts;
  }

  /**
   * Detect priority conflicts
   */
  private async detectPriorityConflicts(context: any): Promise<Conflict[]> {
    const conflicts: Conflict[] = [];

    // Check for conflicting priorities
    const priorityTasks = context.taskQueue?.filter((task: any) => task.priority === 'critical') || [];
    
    if (priorityTasks.length > 1) {
      conflicts.push({
        id: this.generateConflictId(),
        type: 'priority',
        severity: 'high',
        status: 'detected',
        agents: priorityTasks.map((task: any) => task.assignedAgent),
        description: 'Multiple critical priority tasks detected',
        context: { priorityTasks },
        resolution: this.createEmptyResolution(),
        timestamp: new Date().toISOString()
      });
    }

    return conflicts;
  }

  /**
   * Detect dependency conflicts
   */
  private async detectDependencyConflicts(context: any): Promise<Conflict[]> {
    const conflicts: Conflict[] = [];

    // Check for circular dependencies
    const dependencyGraph = new Map<string, string[]>();
    
    for (const task of context.taskQueue || []) {
      dependencyGraph.set(task.id, task.dependencies || []);
    }

    // Detect circular dependencies
    const visited = new Set<string>();
    const recursionStack = new Set<string>();

    const hasCycle = (taskId: string): boolean => {
      if (recursionStack.has(taskId)) return true;
      if (visited.has(taskId)) return false;

      visited.add(taskId);
      recursionStack.add(taskId);

      const dependencies = dependencyGraph.get(taskId) || [];
      for (const dep of dependencies) {
        if (hasCycle(dep)) return true;
      }

      recursionStack.delete(taskId);
      return false;
    };

    for (const taskId of dependencyGraph.keys()) {
      if (hasCycle(taskId)) {
        conflicts.push({
          id: this.generateConflictId(),
          type: 'dependency',
          severity: 'critical',
          status: 'detected',
          agents: [taskId],
          description: 'Circular dependency detected',
          context: { taskId, dependencies: dependencyGraph.get(taskId) },
          resolution: this.createEmptyResolution(),
          timestamp: new Date().toISOString()
        });
      }
    }

    return conflicts;
  }

  /**
   * Detect quality conflicts
   */
  private async detectQualityConflicts(context: any): Promise<Conflict[]> {
    const conflicts: Conflict[] = [];

    // Check for quality conflicts between agents
    const qualityAgents = Array.from(context.agentStatuses?.values() || []).filter((agent: any) => 
      agent.capabilities.includes('quality-analysis') || 
      agent.capabilities.includes('quality-checking')
    );

    if (qualityAgents.length > 1) {
      conflicts.push({
        id: this.generateConflictId(),
        type: 'quality',
        severity: 'medium',
        status: 'detected',
        agents: qualityAgents.map((agent: any) => agent.id),
        description: 'Multiple quality agents may have conflicting assessments',
        context: { qualityAgents },
        resolution: this.createEmptyResolution(),
        timestamp: new Date().toISOString()
      });
    }

    return conflicts;
  }

  /**
   * Detect deadline conflicts
   */
  private async detectDeadlineConflicts(context: any): Promise<Conflict[]> {
    const conflicts: Conflict[] = [];

    // Check for deadline conflicts
    const tasksWithDeadlines = context.taskQueue?.filter((task: any) => task.deadline) || [];
    const now = new Date();

    for (const task of tasksWithDeadlines) {
      const deadline = new Date(task.deadline);
      const timeDiff = deadline.getTime() - now.getTime();
      const hoursDiff = timeDiff / (1000 * 60 * 60);

      if (hoursDiff < 0) {
        conflicts.push({
          id: this.generateConflictId(),
          type: 'deadline',
          severity: 'critical',
          status: 'detected',
          agents: [task.assignedAgent],
          description: `Task ${task.id} is overdue`,
          context: { task, deadline, hoursDiff },
          resolution: this.createEmptyResolution(),
          timestamp: new Date().toISOString()
        });
      }
    }

    return conflicts;
  }

  /**
   * Detect architecture conflicts
   */
  private async detectArchitectureConflicts(context: any): Promise<Conflict[]> {
    const conflicts: Conflict[] = [];

    // Check for architecture conflicts
    const architectureTasks = context.taskQueue?.filter((task: any) => 
      task.type === 'architecture' || task.requirements?.includes('architecture')
    ) || [];

    if (architectureTasks.length > 1) {
      conflicts.push({
        id: this.generateConflictId(),
        type: 'architecture',
        severity: 'high',
        status: 'detected',
        agents: architectureTasks.map((task: any) => task.assignedAgent),
        description: 'Multiple architecture tasks may conflict',
        context: { architectureTasks },
        resolution: this.createEmptyResolution(),
        timestamp: new Date().toISOString()
      });
    }

    return conflicts;
  }

  /**
   * Resolve conflict
   */
  async resolveConflict(conflictId: string): Promise<ConflictResolution> {
    const conflict = this.conflicts.get(conflictId);
    if (!conflict) {
      throw new Error(`Conflict ${conflictId} not found`);
    }

    this._logger.info(`üîß Resolving conflict: ${conflictId}`);

    try {
      conflict.status = 'resolving';

      // Analyze conflict
      const analysis = await this.analyzeConflict(conflict);

      // Select resolution strategy
      const strategy = this.selectResolutionStrategy(conflict, analysis);

      // Execute resolution
      const resolution = await this.executeResolution(conflict, strategy);

      // Update conflict
      conflict.status = 'resolved';
      conflict.resolution = resolution;
      conflict.resolvedAt = new Date().toISOString();
      conflict.resolvedBy = 'automatic';

      // Store in history
      if (!this.conflictHistory.has(conflict.type)) {
        this.conflictHistory.set(conflict.type, []);
      }
      this.conflictHistory.get(conflict.type)!.push(conflict);

      // Learn from resolution
      if (this.conflictConfig.learning.enabled) {
        await this.learnFromResolution(conflict, resolution);
      }

      this._logger.info(`‚úÖ Conflict resolved: ${conflictId}`);
      this.emit('conflict:resolved', { conflict, resolution });
      
      return resolution;
    } catch (error) {
      this._logger.error(`Failed to resolve conflict: ${conflictId}`, error);
      conflict.status = 'failed';
      this.emit('conflict:failed', { conflict, error });
      throw error;
    }
  }

  /**
   * Analyze conflict
   */
  private async analyzeConflict(conflict: Conflict): Promise<any> {
    const analysis = {
      severity: conflict.severity,
      complexity: this.assessConflictComplexity(conflict),
      impact: this.assessConflictImpact(conflict),
      urgency: this.assessConflictUrgency(conflict),
      resources: this.assessResourceRequirements(conflict),
      alternatives: this.generateAlternatives(conflict)
    };

    return analysis;
  }

  /**
   * Assess conflict complexity
   */
  private assessConflictComplexity(conflict: Conflict): 'low' | 'medium' | 'high' {
    const agentCount = conflict.agents.length;
    const contextComplexity = Object.keys(conflict.context).length;

    if (agentCount > 3 || contextComplexity > 5) return 'high';
    if (agentCount > 2 || contextComplexity > 3) return 'medium';
    return 'low';
  }

  /**
   * Assess conflict impact
   */
  private assessConflictImpact(conflict: Conflict): 'low' | 'medium' | 'high' {
    switch (conflict.severity) {
      case 'critical': return 'high';
      case 'high': return 'high';
      case 'medium': return 'medium';
      case 'low': return 'low';
      default: return 'medium';
    }
  }

  /**
   * Assess conflict urgency
   */
  private assessConflictUrgency(conflict: Conflict): 'low' | 'medium' | 'high' {
    const now = new Date();
    const conflictAge = now.getTime() - new Date(conflict.timestamp).getTime();
    const hoursAge = conflictAge / (1000 * 60 * 60);

    if (hoursAge > 2) return 'high';
    if (hoursAge > 1) return 'medium';
    return 'low';
  }

  /**
   * Assess resource requirements
   */
  private assessResourceRequirements(_conflict: Conflict): any {
    return {
      cpu: 1,
      memory: 1,
      time: 1
    };
  }

  /**
   * Generate alternatives
   */
  private generateAlternatives(conflict: Conflict): string[] {
    const alternatives: string[] = [];

    switch (conflict.type) {
      case 'resource':
        alternatives.push('Reassign tasks to different agents');
        alternatives.push('Reschedule tasks to different times');
        alternatives.push('Increase resource capacity');
        break;
      case 'priority':
        alternatives.push('Reorder tasks by priority');
        alternatives.push('Reassign high-priority tasks');
        alternatives.push('Negotiate priority adjustments');
        break;
      case 'dependency':
        alternatives.push('Break circular dependencies');
        alternatives.push('Reschedule dependent tasks');
        alternatives.push('Modify task requirements');
        break;
      case 'quality':
        alternatives.push('Use consensus mechanism');
        alternatives.push('Escalate to higher authority');
        alternatives.push('Apply quality standards');
        break;
      case 'deadline':
        alternatives.push('Extend deadlines');
        alternatives.push('Reassign to faster agents');
        alternatives.push('Reduce task scope');
        break;
      case 'architecture':
        alternatives.push('Coordinate architecture decisions');
        alternatives.push('Use design patterns');
        alternatives.push('Apply architectural principles');
        break;
    }

    return alternatives;
  }

  /**
   * Select resolution strategy
   */
  private selectResolutionStrategy(conflict: Conflict, _analysis: any): any {
    // Use pattern matching if available
    const pattern = this.findMatchingPattern(conflict);
    if (pattern && pattern.successRate > 0.8) {
      return this.resolutionStrategies.get(pattern.resolutionStrategy);
    }

    // Select strategy based on conflict type and analysis
    switch (conflict.type) {
      case 'resource':
        return this.resolutionStrategies.get('resource-based');
      case 'priority':
        return this.resolutionStrategies.get('priority-based');
      case 'deadline':
        return this.resolutionStrategies.get('time-based');
      case 'quality':
        return this.resolutionStrategies.get('quality-based');
      default:
        return this.resolutionStrategies.get('hybrid');
    }
  }

  /**
   * Find matching pattern
   */
  private findMatchingPattern(conflict: Conflict): ConflictPattern | null {
    for (const pattern of this.conflictPatterns.values()) {
      if (pattern.type === conflict.type && pattern.frequency > 2) {
        return pattern;
      }
    }
    return null;
  }

  /**
   * Execute resolution
   */
  private async executeResolution(conflict: Conflict, strategy: any): Promise<ConflictResolution> {
    const resolution: ConflictResolution = {
      id: this.generateResolutionId(),
      strategy: 'automatic',
      approach: strategy.algorithm,
      solution: '',
      actions: [],
      success: false,
      reasoning: '',
      alternatives: [],
      impact: 'medium',
      effort: 'medium'
    };

    try {
      // Execute resolution based on strategy
      switch (strategy.algorithm) {
        case 'priority-based':
          await this.executePriorityBasedResolution(conflict, resolution);
          break;
        case 'resource-based':
          await this.executeResourceBasedResolution(conflict, resolution);
          break;
        case 'time-based':
          await this.executeTimeBasedResolution(conflict, resolution);
          break;
        case 'quality-based':
          await this.executeQualityBasedResolution(conflict, resolution);
          break;
        case 'hybrid':
          await this.executeHybridResolution(conflict, resolution);
          break;
        default:
          await this.executeDefaultResolution(conflict, resolution);
      }

      resolution.success = true;
      resolution.reasoning = `Conflict resolved using ${strategy.name} strategy`;

    } catch (error) {
      resolution.success = false;
      resolution.reasoning = `Resolution failed: ${error instanceof Error ? error.message : String(error)}`;
    }

    return resolution;
  }

  /**
   * Execute priority-based resolution
   */
  private async executePriorityBasedResolution(conflict: Conflict, resolution: ConflictResolution): Promise<void> {
    resolution.solution = 'Resolved by prioritizing tasks based on priority levels';
    resolution.actions.push({
      id: this.generateActionId(),
      type: 'reassign',
      target: conflict.agents[0]!,
      parameters: { priority: 'high' },
      status: 'completed'
    });
  }

  /**
   * Execute resource-based resolution
   */
  private async executeResourceBasedResolution(conflict: Conflict, resolution: ConflictResolution): Promise<void> {
    resolution.solution = 'Resolved by reallocating resources to avoid conflicts';
    resolution.actions.push({
      id: this.generateActionId(),
      type: 'reschedule',
      target: conflict.agents[0]!,
      parameters: { delay: 30000 },
      status: 'completed'
    });
  }

  /**
   * Execute time-based resolution
   */
  private async executeTimeBasedResolution(conflict: Conflict, resolution: ConflictResolution): Promise<void> {
    resolution.solution = 'Resolved by adjusting time constraints';
    resolution.actions.push({
      id: this.generateActionId(),
      type: 'modify',
      target: conflict.agents[0]!,
      parameters: { deadline: new Date(Date.now() + 3600000) },
      status: 'completed'
    });
  }

  /**
   * Execute quality-based resolution
   */
  private async executeQualityBasedResolution(conflict: Conflict, resolution: ConflictResolution): Promise<void> {
    resolution.solution = 'Resolved by applying quality standards';
    resolution.actions.push({
      id: this.generateActionId(),
      type: 'negotiate',
      target: conflict.agents[0]!,
      parameters: { quality: 'high' },
      status: 'completed'
    });
  }

  /**
   * Execute hybrid resolution
   */
  private async executeHybridResolution(conflict: Conflict, resolution: ConflictResolution): Promise<void> {
    resolution.solution = 'Resolved using hybrid approach combining multiple strategies';
    resolution.actions.push({
      id: this.generateActionId(),
      type: 'reassign',
      target: conflict.agents[0]!,
      parameters: { priority: 'high', delay: 30000 },
      status: 'completed'
    });
  }

  /**
   * Execute default resolution
   */
  private async executeDefaultResolution(conflict: Conflict, resolution: ConflictResolution): Promise<void> {
    resolution.solution = 'Resolved using default strategy';
    resolution.actions.push({
      id: this.generateActionId(),
      type: 'escalate',
      target: conflict.agents[0]!,
      parameters: {},
      status: 'completed'
    });
  }

  /**
   * Learn from resolution
   */
  private async learnFromResolution(conflict: Conflict, resolution: ConflictResolution): Promise<void> {
    if (!this.conflictConfig.learning.enabled) return;

    // Update conflict pattern
    const patternKey = `${conflict.type}-${conflict.severity}`;
    let pattern = this.conflictPatterns.get(patternKey);

    if (!pattern) {
      pattern = {
        id: patternKey,
        type: conflict.type,
        pattern: conflict.context,
        frequency: 1,
        successRate: resolution.success ? 1.0 : 0.0,
        resolutionStrategy: resolution.approach,
        lastSeen: new Date().toISOString()
      };
    } else {
      pattern.frequency++;
      pattern.successRate = (pattern.successRate * (pattern.frequency - 1) + (resolution.success ? 1 : 0)) / pattern.frequency;
      pattern.lastSeen = new Date().toISOString();
    }

    this.conflictPatterns.set(patternKey, pattern);
  }

  /**
   * Get conflict resolution statistics
   */
  getConflictResolutionStats(): any {
    const conflicts = Array.from(this.conflicts.values());
    const resolvedConflicts = conflicts.filter(conflict => conflict.status === 'resolved');
    
    return {
      totalConflicts: conflicts.length,
      resolvedConflicts: resolvedConflicts.length,
      successRate: resolvedConflicts.length / conflicts.length,
      byType: conflicts.reduce((acc, conflict) => {
        acc[conflict.type] = (acc[conflict.type] || 0) + 1;
        return acc;
      }, {} as any),
      byIssue: conflicts.reduce((acc, conflict) => {
        acc[conflict.severity] = (acc[conflict.severity] || 0) + 1;
        return acc;
      }, {} as any),
      resolutionStrategies: Array.from(this.resolutionStrategies.values()),
      conflictPatterns: Array.from(this.conflictPatterns.values()),
      lastConflict: conflicts[conflicts.length - 1]
    };
  }

  /**
   * Clear conflict resolution data
   */
  clearConflictResolutionData(): void {
    this.conflicts.clear();
    this.conflictHistory.clear();
    this.conflictPatterns.clear();
    this._logger.info('üóëÔ∏è Conflict resolution data cleared');
    this.emit('conflict-resolution-data:cleared');
  }

  /**
   * Export conflict resolution data
   */
  async exportConflictResolutionData(exportPath: string): Promise<void> {
    try {
      const conflictData = {
        conflicts: Array.from(this.conflicts.entries()),
        history: Array.from(this.conflictHistory.entries()),
        patterns: Array.from(this.conflictPatterns.entries()),
        stats: this.getConflictResolutionStats(),
        config: this.conflictConfig,
        strategies: Array.from(this.resolutionStrategies.entries()),
        exportedAt: new Date().toISOString()
      };
      
      await fs.writeJson(exportPath, conflictData, { spaces: 2 });
      this._logger.info(`üì§ Conflict resolution data exported to: ${exportPath}`);
      this.emit('conflict-resolution-data:exported', { exportPath });
    } catch (error) {
      this._logger.error('Failed to export conflict resolution data:', error);
      throw error;
    }
  }

  /**
   * Utility methods
   */
  private generateConflictId(): string {
    return `conflict-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`;
  }

  private generateResolutionId(): string {
    return `resolution-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`;
  }

  private generateActionId(): string {
    return `action-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`;
  }

  private createEmptyResolution(): ConflictResolution {
    return {
      id: this.generateResolutionId(),
      strategy: 'automatic',
      approach: 'hybrid',
      solution: '',
      actions: [],
      success: false,
      reasoning: '',
      alternatives: [],
      impact: 'medium',
      effort: 'medium'
    };
  }

  private getAgentResources(_agentId: string): string[] {
    // Simplified implementation
    return ['cpu', 'memory'];
  }
}

import fs from 'fs-extra';
import path from 'path';

/**
 * Master Orchestrator Service
 * Monster Mode - Full-scale, multi-agent AI coding system
 * Following BMAD principles: Break, Map, Automate, Document
 */

import { EventEmitter } from 'node:events';
import { Logger } from '../../core/logger';
import { Config } from '../../core/config';
import { MemoryService } from '../memory/memory-service';
import { LearningService } from '../memory/learning-service';
import { SelfReviewService } from '../review/self-review-service';
import { QualityGateService } from '../review/quality-gate-service';
import { ChangeDocumentationService } from '../review/change-documentation-service';
import { VisualDocumentationService } from '../review/visual-documentation-service';
import { ImprovementSuggestionsService } from '../review/improvement-suggestions-service';
import { BranchManagementService } from '../review/branch-management-service';

export interface MonsterModeConfig {
  enabled: boolean;
  mode: 'development' | 'production' | 'testing';
  agents: {
    memory: boolean;
    learning: boolean;
    selfReview: boolean;
    qualityGate: boolean;
    changeDocumentation: boolean;
    visualDocumentation: boolean;
    improvementSuggestions: boolean;
    branchManagement: boolean;
  };
  orchestration: {
    taskPrioritization: boolean;
    conflictResolution: boolean;
    workflowOptimization: boolean;
    architectureImprovements: boolean;
  };
  performance: {
    maxConcurrentTasks: number;
    taskTimeout: number;
    optimizationInterval: number;
  };
}

export interface Task {
  id: string;
  type: 'code-generation' | 'review' | 'quality-check' | 'documentation' | 'optimization' | 'architecture';
  priority: 'low' | 'medium' | 'high' | 'critical';
  status: 'pending' | 'running' | 'completed' | 'failed' | 'cancelled';
  assignedAgent: string;
  description: string;
  context: any;
  requirements: string[];
  dependencies: string[];
  estimatedDuration: number;
  actualDuration?: number;
  startTime?: string;
  endTime?: string;
  result?: any;
  error?: string;
  metadata: any;
}

export interface AgentStatus {
  id: string;
  name: string;
  status: 'idle' | 'busy' | 'error' | 'disabled';
  currentTask?: string;
  taskQueue: string[];
  performance: {
    tasksCompleted: number;
    averageDuration: number;
    successRate: number;
    lastActivity: string;
  };
  capabilities: string[];
  load: number;
}

export interface ConflictResolution {
  id: string;
  type: 'resource' | 'priority' | 'dependency' | 'quality';
  agents: string[];
  description: string;
  resolution: 'automatic' | 'manual' | 'escalated';
  status: 'pending' | 'resolved' | 'escalated';
  timestamp: string;
  solution?: string;
}

export interface WorkflowOptimization {
  id: string;
  type: 'performance' | 'efficiency' | 'quality' | 'resource';
  description: string;
  impact: 'low' | 'medium' | 'high';
  effort: 'low' | 'medium' | 'high';
  status: 'pending' | 'applied' | 'rejected';
  timestamp: string;
  metrics: {
    before: any;
    after: any;
    improvement: number;
  };
}

export interface ArchitectureImprovement {
  id: string;
  type: 'scalability' | 'maintainability' | 'performance' | 'security' | 'reliability';
  description: string;
  impact: 'low' | 'medium' | 'high';
  effort: 'low' | 'medium' | 'high';
  status: 'pending' | 'applied' | 'rejected';
  timestamp: string;
  rationale: string;
  alternatives: string[];
}

export class MasterOrchestrator extends EventEmitter {
  private _logger: Logger;
  private _config: Config;
  private monsterModeConfig!: MonsterModeConfig;
  private agents: Map<string, any>;
  private agentStatuses: Map<string, AgentStatus>;
  private taskQueue: Task[];
  private activeTasks: Map<string, Task>;
  private completedTasks: Map<string, Task>;
  private conflicts: Map<string, ConflictResolution>;
  private optimizations: Map<string, WorkflowOptimization>;
  private architectureImprovements: Map<string, ArchitectureImprovement>;

  constructor(config: Config) {
    super();
    this._config = config;
    this._logger = new Logger('MasterOrchestrator');
    this.agents = new Map();
    this.agentStatuses = new Map();
    this.taskQueue = [];
    this.activeTasks = new Map();
    this.completedTasks = new Map();
    this.conflicts = new Map();
    this.optimizations = new Map();
    this.architectureImprovements = new Map();
    this.loadMonsterModeConfig();
  }

  /**
   * Initialize Monster Mode
   */
  async initialize(): Promise<void> {
    this._logger.info('üëπ Initializing Monster Mode...');
    
    try {
      await this.loadMonsterModeConfig();
      await this.initializeAgents();
      await this.initializeOrchestration();
      
      this._logger.info('‚úÖ Monster Mode initialized successfully');
      this.emit('monster-mode:initialized');
    } catch (error) {
      this._logger.error('Failed to initialize Monster Mode:', error);
      throw error;
    }
  }

  /**
   * Load Monster Mode configuration
   */
  private async loadMonsterModeConfig(): Promise<void> {
    try {
      const configPath = path.join(process.cwd(), 'monster-mode-config.json');
      if (await fs.pathExists(configPath)) {
        this.monsterModeConfig = await fs.readJson(configPath);
        this._logger.info('üìã Monster Mode configuration loaded');
      } else {
        this.monsterModeConfig = this.getDefaultMonsterModeConfig();
        await this.saveMonsterModeConfig();
        this._logger.info('üìã Using default Monster Mode configuration');
      }
    } catch (error) {
      this._logger.warn('Failed to load Monster Mode configuration, using defaults');
      this.monsterModeConfig = this.getDefaultMonsterModeConfig();
    }
  }

  /**
   * Get default Monster Mode configuration
   */
  private getDefaultMonsterModeConfig(): MonsterModeConfig {
    return {
      enabled: true,
      mode: 'development',
      agents: {
        memory: true,
        learning: true,
        selfReview: true,
        qualityGate: true,
        changeDocumentation: true,
        visualDocumentation: true,
        improvementSuggestions: true,
        branchManagement: true
      },
      orchestration: {
        taskPrioritization: true,
        conflictResolution: true,
        workflowOptimization: true,
        architectureImprovements: true
      },
      performance: {
        maxConcurrentTasks: 5,
        taskTimeout: 300000, // 5 minutes
        optimizationInterval: 60000 // 1 minute
      }
    };
  }

  /**
   * Save Monster Mode configuration
   */
  private async saveMonsterModeConfig(): Promise<void> {
    try {
      const configPath = path.join(process.cwd(), 'monster-mode-config.json');
      await fs.writeJson(configPath, this.monsterModeConfig, { spaces: 2 });
      this._logger.info('üíæ Monster Mode configuration saved');
    } catch (error) {
      this._logger.error('Failed to save Monster Mode configuration:', error);
    }
  }

  /**
   * Initialize agents
   */
  private async initializeAgents(): Promise<void> {
    this._logger.info('ü§ñ Initializing agents...');
    
    try {
      // Initialize Memory Service
      if (this.monsterModeConfig.agents.memory) {
        const memoryService = new MemoryService(this._config);
        // await memoryService.initialize(); // Method not yet implemented
        this.agents.set('memory', memoryService);
        this.agentStatuses.set('memory', this.createAgentStatus('memory', 'Memory Service', ['pattern-recognition', 'learning', 'context-awareness']));
      }

      // Initialize Learning Service
      if (this.monsterModeConfig.agents.learning) {
        const learningService = new LearningService(this._config, this.agents.get('memory'));
        await learningService.initialize();
        this.agents.set('learning', learningService);
        this.agentStatuses.set('learning', this.createAgentStatus('learning', 'Learning Service', ['pattern-recognition', 'suggestion-generation', 'feedback-learning']));
      }

      // Initialize Self Review Service
      if (this.monsterModeConfig.agents.selfReview) {
        const selfReviewService = new SelfReviewService(this._config, this.agents.get('memory'), this.agents.get('learning'));
        await selfReviewService.initialize();
        this.agents.set('selfReview', selfReviewService);
        this.agentStatuses.set('selfReview', this.createAgentStatus('selfReview', 'Self Review Service', ['code-review', 'quality-analysis', 'suggestion-generation']));
      }

      // Initialize Quality Gate Service
      if (this.monsterModeConfig.agents.qualityGate) {
        const qualityGateService = new QualityGateService(this._config);
        await qualityGateService.initialize();
        this.agents.set('qualityGate', qualityGateService);
        this.agentStatuses.set('qualityGate', this.createAgentStatus('qualityGate', 'Quality Gate Service', ['quality-checking', 'testing', 'linting', 'security']));
      }

      // Initialize Change Documentation Service
      if (this.monsterModeConfig.agents.changeDocumentation) {
        const changeDocumentationService = new ChangeDocumentationService(this._config);
        await changeDocumentationService.initialize();
        this.agents.set('changeDocumentation', changeDocumentationService);
        this.agentStatuses.set('changeDocumentation', this.createAgentStatus('changeDocumentation', 'Change Documentation Service', ['documentation', 'change-tracking', 'impact-analysis']));
      }

      // Initialize Visual Documentation Service
      if (this.monsterModeConfig.agents.visualDocumentation) {
        const visualDocumentationService = new VisualDocumentationService(this._config);
        await visualDocumentationService.initialize();
        this.agents.set('visualDocumentation', visualDocumentationService);
        this.agentStatuses.set('visualDocumentation', this.createAgentStatus('visualDocumentation', 'Visual Documentation Service', ['diagram-generation', 'visualization', 'documentation']));
      }

      // Initialize Improvement Suggestions Service
      if (this.monsterModeConfig.agents.improvementSuggestions) {
        const improvementSuggestionsService = new ImprovementSuggestionsService(this._config, this.agents.get('memory'), this.agents.get('learning'));
        await improvementSuggestionsService.initialize();
        this.agents.set('improvementSuggestions', improvementSuggestionsService);
        this.agentStatuses.set('improvementSuggestions', this.createAgentStatus('improvementSuggestions', 'Improvement Suggestions Service', ['suggestion-generation', 'code-improvement', 'best-practices']));
      }

      // Initialize Branch Management Service
      if (this.monsterModeConfig.agents.branchManagement) {
        const branchManagementService = new BranchManagementService(this._config);
        await branchManagementService.initialize();
        this.agents.set('branchManagement', branchManagementService);
        this.agentStatuses.set('branchManagement', this.createAgentStatus('branchManagement', 'Branch Management Service', ['workflow-management', 'branch-management', 'git-operations']));
      }

      this._logger.info(`‚úÖ Initialized ${this.agents.size} agents`);
    } catch (error) {
      this._logger.error('Failed to initialize agents:', error);
      throw error;
    }
  }

  /**
   * Initialize orchestration
   */
  private async initializeOrchestration(): Promise<void> {
    this._logger.info('üéØ Initializing orchestration...');
    
    try {
      // Start optimization interval
      if (this.monsterModeConfig.orchestration.workflowOptimization) {
        setInterval(() => {
          this.optimizeWorkflow();
        }, this.monsterModeConfig.performance.optimizationInterval);
      }

      this._logger.info('‚úÖ Orchestration initialized successfully');
    } catch (error) {
      this._logger.error('Failed to initialize orchestration:', error);
      throw error;
    }
  }

  /**
   * Create agent status
   */
  private createAgentStatus(id: string, name: string, capabilities: string[]): AgentStatus {
    return {
      id,
      name,
      status: 'idle',
      taskQueue: [],
      performance: {
        tasksCompleted: 0,
        averageDuration: 0,
        successRate: 1.0,
        lastActivity: new Date().toISOString()
      },
      capabilities,
      load: 0
    };
  }

  /**
   * Submit task to Monster Mode
   */
  async submitTask(task: Omit<Task, 'id' | 'status' | 'assignedAgent' | 'startTime' | 'endTime'>): Promise<string> {
    this._logger.info(`üìã Submitting task: ${task.description}`);
    
    try {
      // Create task with ID
      const taskWithId: Task = {
        ...task,
        id: this.generateTaskId(),
        status: 'pending',
        assignedAgent: '',
        startTime: '',
        endTime: ''
      };

      // Add to task queue
      this.taskQueue.push(taskWithId);

      // Prioritize tasks
      if (this.monsterModeConfig.orchestration.taskPrioritization) {
        this.prioritizeTasks();
      }

      // Assign task to agent
      await this.assignTask(taskWithId.id);

      this._logger.info(`‚úÖ Task submitted: ${taskWithId.id}`);
      this.emit('task:submitted', { task: taskWithId });
      
      return taskWithId.id;
    } catch (error) {
      this._logger.error('Failed to submit task:', error);
      throw error;
    }
  }

  /**
   * Prioritize tasks
   */
  private prioritizeTasks(): void {
    this.taskQueue.sort((a, b) => {
      // Priority weight
      const priorityWeights = { critical: 4, high: 3, medium: 2, low: 1 };
      const priorityDiff = priorityWeights[b.priority] - priorityWeights[a.priority];
      
      if (priorityDiff !== 0) return priorityDiff;
      
      // Estimated duration (shorter tasks first)
      return a.estimatedDuration - b.estimatedDuration;
    });
  }

  /**
   * Assign task to agent
   */
  private async assignTask(taskId: string): Promise<void> {
    const task = this.taskQueue.find(t => t.id === taskId);
    if (!task) return;

    // Find best agent for task
    const bestAgent = this.findBestAgent(task);
    if (!bestAgent) {
      this._logger.warn(`No suitable agent found for task: ${taskId}`);
      return;
    }

    // Assign task to agent
    task.assignedAgent = bestAgent.id;
    task.status = 'running';
    task.startTime = new Date().toISOString();

    // Update agent status
    const agentStatus = this.agentStatuses.get(bestAgent.id);
    if (agentStatus) {
      agentStatus.status = 'busy';
      agentStatus.currentTask = taskId;
      agentStatus.taskQueue.push(taskId);
      agentStatus.load++;
    }

    // Move task to active tasks
    this.activeTasks.set(taskId, task);
    this.taskQueue = this.taskQueue.filter(t => t.id !== taskId);

    // Execute task
    await this.executeTask(taskId);

    this._logger.info(`‚úÖ Task assigned to ${bestAgent.name}: ${taskId}`);
    this.emit('task:assigned', { taskId, agentId: bestAgent.id });
  }

  /**
   * Find best agent for task
   */
  private findBestAgent(task: Task): AgentStatus | null {
    const suitableAgents = Array.from(this.agentStatuses.values()).filter(agent => {
      // Check if agent has required capabilities
      const hasCapabilities = task.requirements.every(req => 
        agent.capabilities.some(cap => cap.includes(req))
      );

      // Check if agent is available
      const isAvailable = agent.status === 'idle' || agent.status === 'busy';

      return hasCapabilities && isAvailable;
    });

    if (suitableAgents.length === 0) return null;

    // Sort by load and performance
    suitableAgents.sort((a, b) => {
      const loadDiff = a.load - b.load;
      if (loadDiff !== 0) return loadDiff;
      
      return b.performance.successRate - a.performance.successRate;
    });

    return suitableAgents[0]!;
  }

  /**
   * Execute task
   */
  private async executeTask(taskId: string): Promise<void> {
    const task = this.activeTasks.get(taskId);
    if (!task) return;

    const agent = this.agents.get(task.assignedAgent);
    if (!agent) return;

    try {
      this._logger.info(`‚ö° Executing task: ${taskId}`);
      
      // Execute task based on type
      let result;
      switch (task.type) {
        case 'code-generation':
          result = await this.executeCodeGenerationTask(task, agent);
          break;
        case 'review':
          result = await this.executeReviewTask(task, agent);
          break;
        case 'quality-check':
          result = await this.executeQualityCheckTask(task, agent);
          break;
        case 'documentation':
          result = await this.executeDocumentationTask(task, agent);
          break;
        case 'optimization':
          result = await this.executeOptimizationTask(task, agent);
          break;
        case 'architecture':
          result = await this.executeArchitectureTask(task, agent);
          break;
        default:
          throw new Error(`Unknown task type: ${task.type}`);
      }

      // Complete task
      task.status = 'completed';
      task.endTime = new Date().toISOString();
      task.actualDuration = new Date(task.endTime).getTime() - new Date(task.startTime!).getTime();
      task.result = result;

      // Update agent status
      const agentStatus = this.agentStatuses.get(task.assignedAgent);
      if (agentStatus) {
        agentStatus.status = 'idle';
        // agentStatus.currentTask = undefined; // Handled by optional property
        agentStatus.taskQueue = agentStatus.taskQueue.filter(id => id !== taskId);
        agentStatus.load--;
        agentStatus.performance.tasksCompleted++;
        agentStatus.performance.lastActivity = new Date().toISOString();
        
        // Update average duration
        const totalDuration = agentStatus.performance.averageDuration * (agentStatus.performance.tasksCompleted - 1) + task.actualDuration!;
        agentStatus.performance.averageDuration = totalDuration / agentStatus.performance.tasksCompleted;
      }

      // Move task to completed
      this.completedTasks.set(taskId, task);
      this.activeTasks.delete(taskId);

      this._logger.info(`‚úÖ Task completed: ${taskId}`);
      this.emit('task:completed', { taskId, result });

    } catch (error) {
      // Handle task failure
      task.status = 'failed';
      task.endTime = new Date().toISOString();
      task.error = error instanceof Error ? error.message : String(error);

      // Update agent status
      const agentStatus = this.agentStatuses.get(task.assignedAgent);
      if (agentStatus) {
        agentStatus.status = 'idle';
        // agentStatus.currentTask = undefined; // Handled by optional property
        agentStatus.taskQueue = agentStatus.taskQueue.filter(id => id !== taskId);
        agentStatus.load--;
        agentStatus.performance.successRate = Math.max(0, agentStatus.performance.successRate - 0.1);
      }

      this._logger.error(`‚ùå Task failed: ${taskId}`, error);
      this.emit('task:failed', { taskId, error });
    }
  }

