  name: string;
  description: string;
  algorithm: 'priority-based' | 'deadline-based' | 'resource-based' | 'hybrid';
  parameters: any;
  performance: {
    efficiency: number;
    fairness: number;
    predictability: number;
  };
}

export class TaskPrioritizationService extends EventEmitter {
  private _logger: Logger;
