/**
 * Multi-Agent Service for Oliver-OS
 * Coordinates all agents and manages task distribution
 * DEV MODE implementation with mock agents
 * Following BMAD principles: Break, Map, Automate, Document
 */

import { EventEmitter } from 'node:events';
import { Logger } from '../../core/logger';
import { Config } from '../../core/config';
import { CentralOrchestrator } from './orchestrator';
import { FrontendAgent } from './agents/frontend-agent';
import { BackendAgent } from './agents/backend-agent';
import { AIServicesAgent } from './agents/ai-services-agent';
import { DatabaseAgent } from './agents/database-agent';
import { IntegrationAgent } from './agents/integration-agent';
import type {
  AgentType,
  TaskDefinition,
  TaskProgress,
  AgentStatus,
  SystemMetrics,
  MultiAgentConfig
} from './types';

export class MultiAgentService extends EventEmitter {
  private _logger: Logger;
  private _config: Config;
  private orchestrator!: CentralOrchestrator;
  private agents: Map<AgentType, any> = new Map();
  private isDevMode: boolean = true;
  private serviceConfig: MultiAgentConfig;

  constructor(config: Config) {
    super();
    this._config = config;
    this._logger = new Logger('MultiAgentService');
    this.isDevMode = !process.env['CODEBUFF_API_KEY'] || process.env['NODE_ENV'] === 'development';
    
    this.serviceConfig = {
      devMode: this.isDevMode,
      maxConcurrentTasks: 10,
      agentTimeout: 60000,
      heartbeatInterval: 30000,
      retryAttempts: 3,
      enableMetrics: true,
      enablePersistence: true
    };

    this._logger.info(`üöÄ Multi-Agent Service initialized in ${this.isDevMode ? 'DEV MODE' : 'RUN MODE'}`);
    
    this.initializeOrchestrator();
    this.initializeAgents();
    this.setupEventHandlers();
  }

  /**
   * Initialize the central orchestrator
   */
  private initializeOrchestrator(): void {
    this.orchestrator = new CentralOrchestrator(this._config);
    
    // Listen to orchestrator events
    this.orchestrator.on('task:distributed', (data: any) => {
      this.emit('task:distributed', data);
    });

    this.orchestrator.on('task:progress', (data: any) => {
      this.emit('task:progress', data);
    });

    this.orchestrator.on('task:completed', (data: any) => {
      this.emit('task:completed', data);
    });

    this.orchestrator.on('orchestrator:initialized', (data: any) => {
      this._logger.info(`‚úÖ Orchestrator initialized with ${data.agentCount} agents`);
      this.emit('service:ready', data);
    });

    this._logger.info('üß† Central Orchestrator initialized');
  }

  /**
   * Initialize all agent instances
   */
  private initializeAgents(): void {
    const agentTypes: AgentType[] = ['frontend', 'backend', 'ai-services', 'database', 'integration'];
    
    agentTypes.forEach(agentType => {
      const agent = this.createAgent(agentType);
      this.agents.set(agentType, agent);
      
      // Listen to agent events
      agent.on('task:completed', (data: any) => {
        this.emit('agent:task-completed', { agentType, ...data });
      });

      agent.on('task:failed', (data: any) => {
        this.emit('agent:task-failed', { agentType, ...data });
      });

      agent.on('health:check', (data: any) => {
        this.emit('agent:health-check', { agentType, ...data });
      });

      this._logger.info(`ü§ñ ${agentType} agent initialized`);
    });

    this._logger.info(`‚úÖ All ${agentTypes.length} agents initialized`);
  }

  /**
   * Create agent instance based on type
   */
  private createAgent(agentType: AgentType): any {
    switch (agentType) {
      case 'frontend':
        return new FrontendAgent(this.isDevMode);
      case 'backend':
        return new BackendAgent(this.isDevMode);
      case 'ai-services':
        return new AIServicesAgent(this.isDevMode);
      case 'database':
        return new DatabaseAgent(this.isDevMode);
      case 'integration':
        return new IntegrationAgent(this.isDevMode);
      default:
        throw new Error(`Unknown agent type: ${agentType}`);
    }
  }

  /**
   * Setup event handlers
   */
  private setupEventHandlers(): void {
    // Handle service-level events
    this.on('service:ready', (_data: any) => {
      this._logger.info('üéâ Multi-Agent Service ready for task processing');
    });

    this.on('task:distributed', (data) => {
      this._logger.info(`üìã Task distributed: ${data.task.name}`);
    });

    this.on('task:progress', (data) => {
      this._logger.info(`üìä Task progress: ${data.taskId} - ${data.overallProgress}%`);
    });

    this.on('task:completed', (data) => {
      this._logger.info(`‚úÖ Task completed: ${data.taskId}`);
    });
  }

  /**
   * Distribute task to appropriate agents
   */
  async distributeTask(task: TaskDefinition): Promise<string> {
    this._logger.info(`üìã Distributing task: ${task.name}`);
    
    try {
      const taskId = await this.orchestrator.distributeTask(task);
      this.emit('task:distributed', { taskId, task });
      return taskId;
    } catch (error) {
      this._logger.error(`Failed to distribute task: ${error instanceof Error ? error.message : String(error)}`);
      throw error;
    }
  }

  /**
   * Get task progress
   */
  getTaskProgress(taskId: string): TaskProgress | undefined {
    return this.orchestrator.getTaskProgress(taskId);
  }

  /**
   * Get agent status
   */
  getAgentStatus(agentType: AgentType): AgentStatus | undefined {
    return this.orchestrator.getAgentStatus(agentType);
  }

  /**
   * Get all agent statuses
   */
  getAllAgentStatuses(): Map<AgentType, AgentStatus> {
    return this.orchestrator.getAllAgentStatuses();
  }

  /**
   * Get system metrics
   */
  getSystemMetrics(): SystemMetrics {
    const health = this.orchestrator.getSystemHealth();
    const activeTasks = this.orchestrator.getActiveTasks();
    
    return {
      totalAgents: this.agents.size,
      activeAgents: Array.from(this.agents.values()).filter(agent => 
        agent.getAgentStatus().status !== 'offline'
      ).length,
      totalTasks: activeTasks.size,
      activeTasks: activeTasks.size,
      completedTasks: health.completedTasks,
      failedTasks: 0, // Would be tracked in real implementation
      averageTaskDuration: 0, // Would be calculated in real implementation
      systemUptime: Date.now(), // Would be tracked in real implementation
      devMode: this.isDevMode
    };
  }

  /**
   * Send message to specific agent
   */
  async sendMessageToAgent(agentType: AgentType, message: any): Promise<void> {
    const agent = this.agents.get(agentType);
    if (!agent) {
      throw new Error(`Agent ${agentType} not found`);
    }

    await agent.handleMessage(message);
  }

  /**
   * Broadcast message to all agents
   */
  async broadcastMessage(message: any): Promise<void> {
    this._logger.info(`üì¢ Broadcasting message to all agents: ${message.type}`);
    
    const agentTypes: AgentType[] = ['frontend', 'backend', 'ai-services', 'database', 'integration'];
    
    for (const agentType of agentTypes) {
      await this.sendMessageToAgent(agentType, message);
    }
  }

  /**
   * Get system health
   */
  getSystemHealth(): any {
    return this.orchestrator.getSystemHealth();
  }

  /**
   * Create a sample task for testing
   */
  createSampleTask(taskName: string, assignedAgents: AgentType[]): TaskDefinition {
    return {
      id: `sample-task-${Date.now()}`,
      name: taskName,
      description: `Sample task for testing multi-agent system: ${taskName}`,
      assignedAgents,
      complexity: 'medium',
      estimatedDuration: 30000, // 30 seconds
      subtasks: [
        `Setup ${assignedAgents[0]!} implementation`,
        `Test ${assignedAgents[0]!} functionality`,
        `Integrate ${assignedAgents[0]!} with other agents`
      ],
      metadata: {
        createdBy: 'MultiAgentService',
        devMode: this.isDevMode,
        timestamp: new Date().toISOString()
      }
    };
  }

  /**
   * Run a sample workflow
   */
  async runSampleWorkflow(): Promise<void> {
    this._logger.info('üé¨ Running sample multi-agent workflow');

    try {
      // Create sample tasks
      const frontendTask = this.createSampleTask('Create React Component', ['frontend']);
      const backendTask = this.createSampleTask('Create API Endpoint', ['backend']);
      const aiTask = this.createSampleTask('Process Thought', ['ai-services']);
      const dbTask = this.createSampleTask('Design Database Schema', ['database']);
      const integrationTask = this.createSampleTask('Setup API Integration', ['integration']);

      // Distribute tasks
      const frontendTaskId = await this.distributeTask(frontendTask);
      const backendTaskId = await this.distributeTask(backendTask);
      const aiTaskId = await this.distributeTask(aiTask);
      const dbTaskId = await this.distributeTask(dbTask);
      const integrationTaskId = await this.distributeTask(integrationTask);

      this._logger.info(`‚úÖ Sample workflow started with 5 tasks:
        - Frontend: ${frontendTaskId}
        - Backend: ${backendTaskId}
        - AI Services: ${aiTaskId}
        - Database: ${dbTaskId}
        - Integration: ${integrationTaskId}`);

      // Monitor progress
      const monitorProgress = setInterval(() => {
        const frontendProgress = this.getTaskProgress(frontendTaskId);
        const backendProgress = this.getTaskProgress(backendTaskId);
        const aiProgress = this.getTaskProgress(aiTaskId);
        const dbProgress = this.getTaskProgress(dbTaskId);
        const integrationProgress = this.getTaskProgress(integrationTaskId);

        const allCompleted = [frontendProgress, backendProgress, aiProgress, dbProgress, integrationProgress]
          .every(progress => progress?.status === 'completed');

        if (allCompleted) {
          clearInterval(monitorProgress);
          this._logger.info('üéâ Sample workflow completed successfully!');
          this.emit('workflow:completed', {
            frontendTaskId,
            backendTaskId,
            aiTaskId,
            dbTaskId,
            integrationTaskId
          });
        }
      }, 1000);

    } catch (error) {
      this._logger.error(`Sample workflow failed: ${error instanceof Error ? error.message : String(error)}`);
      throw error;
    }
  }

  /**
   * Get service configuration
   */
  getServiceConfig(): MultiAgentConfig {
    return this.serviceConfig;
  }

  /**
   * Update service configuration
   */
  updateServiceConfig(newConfig: Partial<MultiAgentConfig>): void {
    this.serviceConfig = { ...this.serviceConfig, ...newConfig };
    this._logger.info('üîß Service configuration updated');
  }

  /**
   * Shutdown the multi-agent service
   */
  async shutdown(): Promise<void> {
    this._logger.info('üõë Shutting down Multi-Agent Service');
    
    // Shutdown orchestrator
    await this.orchestrator.shutdown();
    
    // Clear agents
    this.agents.clear();
    
    this.emit('service:shutdown', { timestamp: new Date().toISOString() });
    this._logger.info('‚úÖ Multi-Agent Service shutdown complete');
  }
}

/**
 * Central Orchestrator Agent for Oliver-OS Multi-Agent System
 * Coordinates all agents and tracks progress in DEV MODE
 * Following BMAD principles: Break, Map, Automate, Document
 */

import { EventEmitter } from 'node:events';
import { Logger } from '../../core/logger';
import { Config } from '../../core/config';
import type {
  AgentType,
  AgentStatus,
  TaskDefinition,
  TaskProgress,
  AgentMessage,
  AgentCapabilities,
  TaskResult,
  AgentHealth
} from './types';

export class CentralOrchestrator extends EventEmitter {
  private _logger: Logger;
  // private _config!: Config; // Unused for now
  private agents: Map<AgentType, AgentStatus> = new Map();
  private activeTasks: Map<string, TaskDefinition> = new Map();
  private taskProgress: Map<string, TaskProgress> = new Map();
  private agentHealth: Map<AgentType, AgentHealth> = new Map();
  private isDevMode: boolean = true;

  constructor(_config: Config) {
    super();
    this._logger = new Logger('CentralOrchestrator');
    this.isDevMode = !process.env['CODEBUFF_API_KEY'] || process.env['NODE_ENV'] === 'development';
    
    this._logger.info(`üß† Central Orchestrator initialized in ${this.isDevMode ? 'DEV MODE' : 'RUN MODE'}`);
    this.initializeAgents();
  }

  /**
   * Initialize all agent types with their capabilities
   */
  private initializeAgents(): void {
    const agentTypes: AgentType[] = ['frontend', 'backend', 'ai-services', 'database', 'integration'];
    
    agentTypes.forEach(agentType => {
      const capabilities = this.getAgentCapabilities(agentType);
      const status: AgentStatus = {
        id: `${agentType}-agent`,
        type: agentType,
        status: 'idle',
        capabilities,
        lastActivity: new Date().toISOString(),
        metadata: {
          devMode: this.isDevMode,
          mockMode: this.isDevMode
        }
      };

      this.agents.set(agentType, status);
      this.agentHealth.set(agentType, {
        status: 'healthy',
        lastHeartbeat: new Date().toISOString(),
        uptime: 0,
        tasksCompleted: 0,
        tasksFailed: 0
      });

      this._logger.info(`ü§ñ Initialized ${agentType} agent with capabilities: ${capabilities.join(', ')}`);
    });

    this.emit('orchestrator:initialized', { agentCount: agentTypes.length, devMode: this.isDevMode });
  }

  /**
   * Get capabilities for each agent type
   */
  private getAgentCapabilities(agentType: AgentType): AgentCapabilities {
    const capabilities: Record<AgentType, AgentCapabilities> = {
      'frontend': ['react-components', 'typescript', 'tailwind', 'state-management', 'ui-ux'],
      'backend': ['express-apis', 'middleware', 'authentication', 'websocket', 'microservices'],
      'ai-services': ['thought-processing', 'pattern-recognition', 'ai-integration', 'nlp', 'ml-models'],
      'database': ['schema-design', 'migrations', 'queries', 'optimization', 'multi-db'],
      'integration': ['api-integration', 'webhook-handling', 'data-sync', 'error-handling', 'monitoring']
    };

    return capabilities[agentType];
  }

  /**
   * Distribute task to appropriate agents
   */
  async distributeTask(task: TaskDefinition): Promise<string> {
    const taskId = `task-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`;
    
    this._logger.info(`üìã Distributing task: ${task.name} to ${task.assignedAgents.join(', ')}`);
    
    // Initialize task progress tracking
    const progress: TaskProgress = {
      taskId,
      taskName: task.name,
      status: 'in-progress',
      assignedAgents: task.assignedAgents,
      agentProgress: new Map(),
      overallProgress: 0,
      startTime: new Date().toISOString(),
      estimatedCompletion: new Date(Date.now() + task.estimatedDuration).toISOString(),
      subtasks: task.subtasks || [],
      metadata: task.metadata || {}
    };

    this.activeTasks.set(taskId, task);
    this.taskProgress.set(taskId, progress);

    // Assign subtasks to agents
    for (const agentType of task.assignedAgents) {
      const agentStatus = this.agents.get(agentType);
      if (agentStatus) {
        agentStatus.status = 'busy';
        agentStatus.currentTask = taskId;
        agentStatus.lastActivity = new Date().toISOString();

        // Simulate agent task assignment
        await this.assignTaskToAgent(agentType, taskId, task);
      }
    }

    this.emit('task:distributed', { taskId, task, progress });
    return taskId;
  }

  /**
   * Assign task to specific agent (simulated in dev mode)
   */
  private async assignTaskToAgent(agentType: AgentType, taskId: string, task: TaskDefinition): Promise<void> {
    const agentStatus = this.agents.get(agentType);
    if (!agentStatus) return;

    this._logger.info(`üéØ Assigning task ${taskId} to ${agentType} agent`);

    // Simulate agent processing in dev mode
    if (this.isDevMode) {
      await this.simulateAgentProcessing(agentType, taskId, task);
    } else {
      // In run mode, this would communicate with real agents
      await this.sendRealMessageToAgent(agentType, { 
        id: `msg-${Date.now()}`,
        type: 'task-assignment', 
        sender: 'orchestrator',
        recipient: agentType,
        content: { task }, 
        timestamp: new Date().toISOString(),
        priority: 'normal'
      });
    }
  }

  /**
   * Simulate agent processing in dev mode
   */
  private async simulateAgentProcessing(agentType: AgentType, taskId: string, task: TaskDefinition): Promise<void> {
    const progress = this.taskProgress.get(taskId);
    if (!progress) return;

    // Simulate processing time based on task complexity
    const processingTime = this.calculateProcessingTime(task, agentType);
    
    this._logger.info(`üîÑ Simulating ${agentType} agent processing for ${processingTime}ms`);

    // Simulate progress updates
    const progressSteps = [25, 50, 75, 100];
    for (const step of progressSteps) {
      await new Promise(resolve => setTimeout(resolve, processingTime / progressSteps.length));
      
      // Update agent progress
      progress.agentProgress.set(agentType, {
        agentType,
        progress: step,
        status: step === 100 ? 'completed' : 'in-progress',
        lastUpdate: new Date().toISOString(),
        subtasks: this.generateMockSubtasks(agentType, task)
      });

      // Update overall progress
      progress.overallProgress = this.calculateOverallProgress(progress);
      
      // Emit progress update
      this.emit('task:progress', { taskId, agentType, progress: step, overallProgress: progress.overallProgress });
      
      this._logger.info(`üìä Task ${taskId} progress: ${agentType} ${step}%, Overall ${progress.overallProgress}%`);
    }

    // Mark agent as completed
    const agentStatus = this.agents.get(agentType);
    if (agentStatus) {
      agentStatus.status = 'idle';
      agentStatus.currentTask = '';
      agentStatus.lastActivity = new Date().toISOString();
    }

    // Update health metrics
    const health = this.agentHealth.get(agentType);
    if (health) {
      health.tasksCompleted++;
      health.lastHeartbeat = new Date().toISOString();
    }

    // Check if all agents are done
    const allAgentsDone = task.assignedAgents.every(agentType => {
      const agentProgress = progress.agentProgress.get(agentType);
      return agentProgress?.status === 'completed';
    });

    if (allAgentsDone) {
      progress.status = 'completed';
      progress.endTime = new Date().toISOString();
      progress.duration = new Date(progress.endTime).getTime() - new Date(progress.startTime).getTime();
      
      this.emit('task:completed', { taskId, progress, result: this.generateMockTaskResult(task) });
      this._logger.info(`‚úÖ Task ${taskId} completed successfully`);
    }
  }

  /**
   * Calculate processing time based on task complexity and agent type
   */
  private calculateProcessingTime(task: TaskDefinition, agentType: AgentType): number {
    const baseTime = 1000; // 1 second base
    const complexityMultiplier = task.complexity || 'medium';
    const agentMultiplier = this.getAgentProcessingMultiplier(agentType);
    
    const complexityMultipliers = {
      'low': 1,
      'medium': 2,
      'high': 4,
      'critical': 8
    };

    return baseTime * complexityMultipliers[complexityMultiplier] * agentMultiplier;
  }

  /**
   * Get processing multiplier for different agent types
   */
  private getAgentProcessingMultiplier(agentType: AgentType): number {
    const multipliers = {
      'frontend': 1.2,
      'backend': 1.5,
      'ai-services': 2.0,
      'database': 1.0,
      'integration': 1.8
    };

    return multipliers[agentType];
  }

  /**
   * Generate mock subtasks for agent
   */
  private generateMockSubtasks(agentType: AgentType, _task: TaskDefinition): string[] {
    const subtaskTemplates: Record<AgentType, string[]> = {
      'frontend': [
        'Creating React components',
        'Implementing TypeScript interfaces',
        'Setting up state management',
        'Applying Tailwind styling',
        'Testing component functionality'
      ],
      'backend': [
        'Setting up Express routes',
        'Implementing middleware',
        'Adding authentication logic',
        'Configuring WebSocket handlers',
        'Testing API endpoints'
      ],
      'ai-services': [
        'Processing thought patterns',
        'Running AI model inference',
        'Analyzing data patterns',
        'Generating insights',
        'Updating knowledge graph'
      ],
      'database': [
        'Designing database schema',
        'Creating migrations',
        'Optimizing queries',
        'Setting up indexes',
        'Testing data integrity'
      ],
      'integration': [
        'Configuring API connections',
        'Setting up webhook handlers',
        'Implementing data synchronization',
        'Adding error handling',
        'Monitoring system health'
      ]
    };

    return subtaskTemplates[agentType].slice(0, Math.floor(Math.random() * 3) + 2);
  }

  /**
   * Calculate overall task progress
   */
  private calculateOverallProgress(progress: TaskProgress): number {
    if (progress.agentProgress.size === 0) return 0;
    
    const totalProgress = Array.from(progress.agentProgress.values())
      .reduce((sum, agentProgress) => sum + agentProgress.progress, 0);
    
    return Math.round(totalProgress / progress.agentProgress.size);
  }

  /**
   * Generate mock task result
   */
  private generateMockTaskResult(task: TaskDefinition): TaskResult {
    return {
      success: true,
      taskId: task.id || 'unknown',
      duration: Date.now() - Date.now(), // Will be calculated properly
      artifacts: this.generateMockArtifacts(task),
      metrics: {
        agentsUsed: task.assignedAgents.length,
        processingTime: this.calculateProcessingTime(task, task.assignedAgents[0]!),
        successRate: 100,
        errorCount: 0
      },
      metadata: {
        devMode: this.isDevMode,
        mockResult: true
      }
    };
  }

  /**
   * Generate mock artifacts
   */
  private generateMockArtifacts(task: TaskDefinition): any[] {
    return task.assignedAgents.map(agentType => ({
      id: `artifact-${agentType}-${Date.now()}`,
      type: 'code',
      name: `${agentType}-implementation`,
      content: `Mock implementation for ${agentType} agent`,
      agentType,
      createdAt: new Date().toISOString()
    }));
  }

  /**
   * Get agent status
   */
  getAgentStatus(agentType: AgentType): AgentStatus | undefined {
    return this.agents.get(agentType);
  }

  /**
   * Get all agent statuses
   */
  getAllAgentStatuses(): Map<AgentType, AgentStatus> {
    return new Map(this.agents);
  }

  /**
   * Get task progress
   */
  getTaskProgress(taskId: string): TaskProgress | undefined {
    return this.taskProgress.get(taskId);
  }

  /**
   * Get all active tasks
   */
  getActiveTasks(): Map<string, TaskDefinition> {
    return new Map(this.activeTasks);
  }

  /**
   * Get system health
   */
  getSystemHealth(): {
    orchestrator: 'healthy' | 'degraded' | 'unhealthy';
    agents: Map<AgentType, AgentHealth>;
    activeTasks: number;
    completedTasks: number;
    devMode: boolean;
  } {
    const healthyAgents = Array.from(this.agentHealth.values())
      .filter(health => health.status === 'healthy').length;
    
    const totalAgents = this.agentHealth.size;
    const healthRatio = healthyAgents / totalAgents;

    let orchestratorHealth: 'healthy' | 'degraded' | 'unhealthy' = 'healthy';
    if (healthRatio < 0.5) {
      orchestratorHealth = 'unhealthy';
    } else if (healthRatio < 0.8) {
      orchestratorHealth = 'degraded';
    }

    return {
      orchestrator: orchestratorHealth,
      agents: new Map(this.agentHealth),
      activeTasks: this.activeTasks.size,
      completedTasks: Array.from(this.agentHealth.values())
        .reduce((sum, health) => sum + health.tasksCompleted, 0),
      devMode: this.isDevMode
    };
  }

  /**
   * Send message to specific agent
   */
  async sendMessageToAgent(agentType: AgentType, message: AgentMessage): Promise<void> {
    const agentStatus = this.agents.get(agentType);
    if (!agentStatus) {
      throw new Error(`Agent ${agentType} not found`);
    }

    this._logger.info(`üì® Sending message to ${agentType} agent: ${message.type}`);

    if (this.isDevMode) {
      // Simulate message processing
      await this.simulateMessageProcessing(agentType, message);
    } else {
      // In run mode, this would send real messages to agents
      await this.sendRealMessageToAgent(agentType, message);
    }
  }

  /**
   * Simulate message processing in dev mode
   */
  private async simulateMessageProcessing(agentType: AgentType, message: AgentMessage): Promise<void> {
    // Simulate processing delay
    await new Promise(resolve => setTimeout(resolve, 100));
    
    // Emit message processed event
    this.emit('message:processed', { agentType, message, timestamp: new Date().toISOString() });
    
    this._logger.info(`‚úÖ Message processed by ${agentType} agent`);
  }

  /**
   * Send real message to agent (run mode)
   */
  private async sendRealMessageToAgent(agentType: AgentType, _message: AgentMessage): Promise<void> {
    // This would be implemented for run mode with real agent communication
    this._logger.info(`üöÄ Sending real message to ${agentType} agent (run mode)`);
  }

  /**
   * Broadcast message to all agents
   */
  async broadcastMessage(message: AgentMessage): Promise<void> {
    this._logger.info(`üì¢ Broadcasting message to all agents: ${message.type}`);

    const agentTypes: AgentType[] = ['frontend', 'backend', 'ai-services', 'database', 'integration'];
    
    for (const agentType of agentTypes) {
      await this.sendMessageToAgent(agentType, message);
    }
  }

  /**
   * Shutdown orchestrator
   */
  async shutdown(): Promise<void> {
    this._logger.info('üõë Shutting down Central Orchestrator');
    
    // Mark all agents as offline
    for (const [, status] of this.agents) {
      status.status = 'offline';
      status.lastActivity = new Date().toISOString();
    }

    this.emit('orchestrator:shutdown', { timestamp: new Date().toISOString() });
    this._logger.info('‚úÖ Central Orchestrator shutdown complete');
  }
}

/**
 * Multi-Agent System Types for Oliver-OS
 * DEV MODE implementation with mock agents
 * Following BMAD principles: Break, Map, Automate, Document
 */

export type AgentType = 'frontend' | 'backend' | 'ai-services' | 'database' | 'integration';

export type AgentCapabilities = string[];

export type TaskStatus = 'pending' | 'in-progress' | 'completed' | 'failed' | 'cancelled';

export type AgentStatusType = 'idle' | 'busy' | 'error' | 'offline';

export interface AgentStatus {
  id: string;
  type: AgentType;
  status: AgentStatusType;
  capabilities: AgentCapabilities;
  currentTask?: string;
  lastActivity: string;
  metadata: Record<string, unknown>;
}

export interface AgentHealth {
  status: 'healthy' | 'degraded' | 'unhealthy';
  lastHeartbeat: string;
  uptime: number;
  tasksCompleted: number;
  tasksFailed: number;
}

export interface TaskDefinition {
  id?: string;
  name: string;
  description: string;
  assignedAgents: AgentType[];
  complexity: 'low' | 'medium' | 'high' | 'critical';
  estimatedDuration: number; // milliseconds
  subtasks?: string[];
  dependencies?: string[];
  metadata?: Record<string, unknown>;
}

export interface TaskProgress {
  taskId: string;
  taskName: string;
  status: TaskStatus;
  assignedAgents: AgentType[];
  agentProgress: Map<AgentType, AgentTaskProgress>;
  overallProgress: number;
  startTime: string;
  endTime?: string;
  duration?: number;
  estimatedCompletion: string;
  subtasks: string[];
  metadata?: Record<string, unknown>;
}

export interface AgentTaskProgress {
  agentType: AgentType;
  progress: number; // 0-100
  status: TaskStatus;
  lastUpdate: string;
  subtasks: string[];
}

export interface TaskResult {
  success: boolean;
  taskId: string;
  duration: number;
  artifacts: any[];
  metrics: {
    agentsUsed: number;
    processingTime: number;
    successRate: number;
    errorCount: number;
  };
  metadata?: Record<string, unknown>;
}

export interface AgentMessage {
  id: string;
  type: 'task-assignment' | 'progress-update' | 'status-change' | 'health-check' | 'broadcast';
  sender: AgentType | 'orchestrator';
  recipient?: AgentType | 'all';
  content: Record<string, unknown>;
  timestamp: string;
  priority: 'low' | 'normal' | 'high' | 'urgent';
}

export interface OrchestrationEvent {
  type: 'task:distributed' | 'task:progress' | 'task:completed' | 'agent:status-change' | 'system:health-check';
  data: Record<string, unknown>;
  timestamp: string;
}

export interface MultiAgentConfig {
  devMode: boolean;
  maxConcurrentTasks: number;
  agentTimeout: number;
  heartbeatInterval: number;
  retryAttempts: number;
  enableMetrics: boolean;
  enablePersistence: boolean;
}

export interface AgentCommunicationProtocol {
  sendMessage(message: AgentMessage): Promise<void>;
  receiveMessage(handler: (message: AgentMessage) => void): void;
  broadcastMessage(message: AgentMessage): Promise<void>;
  getAgentStatus(agentType: AgentType): Promise<AgentStatus>;
}

export interface ProgressReporting {
  reportProgress(taskId: string, agentType: AgentType, progress: number): void;
  getTaskProgress(taskId: string): TaskProgress | undefined;
  getAllActiveTasks(): TaskProgress[];
  getSystemMetrics(): SystemMetrics;
}

export interface SystemMetrics {
  totalAgents: number;
  activeAgents: number;
  totalTasks: number;
  activeTasks: number;
  completedTasks: number;
  failedTasks: number;
  averageTaskDuration: number;
  systemUptime: number;
  devMode: boolean;
}

export interface AgentTaskAssignment {
  taskId: string;
  agentType: AgentType;
  subtasks: string[];
  priority: 'low' | 'normal' | 'high' | 'urgent';
  deadline?: string;
  metadata?: Record<string, unknown>;
}

export interface AgentResponse {
  taskId: string;
  agentType: AgentType;
  status: 'accepted' | 'rejected' | 'in-progress' | 'completed' | 'failed';
  progress: number;
  result?: any;
  error?: string;
  timestamp: string;
}

export interface WorkflowDefinition {
  id: string;
  name: string;
  description: string;
